import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import getData, { postData } from '../../api/endpoint';
import { FiFileText, FiUser, FiAlertCircle, FiHeart } from 'react-icons/fi';

const CreerDossier = () => {
  const navigate = useNavigate();
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [formData, setFormData] = useState({
    patientId: '',
    resumeMedical: '',
    groupeSanguin: '',
    antecedents: [],
    allergies: [],
    contactUrgence: {
      nom: '',
      telephone: '',
      lien: ''
    },
    statut: 'ACTIF'
  });
  const [newAntecedent, setNewAntecedent] = useState('');
  const [newAllergie, setNewAllergie] = useState('');

  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    try {
      const users = await getData('/users');
      const patientsList = users.filter(user => user.role === 'Patient');
      setPatients(patientsList);
    } catch (err) {
      console.error('Erreur lors du chargement des patients:', err);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith('contactUrgence.')) {
      const field = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        contactUrgence: {
          ...prev.contactUrgence,
          [field]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
    setError(null);
  };

  const addAntecedent = () => {
    if (newAntecedent.trim()) {
      setFormData(prev => ({
        ...prev,
        antecedents: [...prev.antecedents, { antecedent: newAntecedent.trim() }]
      }));
      setNewAntecedent('');
    }
  };

  const removeAntecedent = (index) => {
    setFormData(prev => ({
      ...prev,
      antecedents: prev.antecedents.filter((_, i) => i !== index)
    }));
  };

  const addAllergie = () => {
    if (newAllergie.trim()) {
      setFormData(prev => ({
        ...prev,
        allergies: [...prev.allergies, { allergie: newAllergie.trim() }]
      }));
      setNewAllergie('');
    }
  };

  const removeAllergie = (index) => {
    setFormData(prev => ({
      ...prev,
      allergies: prev.allergies.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(false);

    if (!formData.patientId) {
      setError('Veuillez sélectionner un patient.');
      return;
    }

    try {
      setLoading(true);
      await postData('/new-dossier', formData);
      setSuccess(true);
      setTimeout(() => {
        navigate('/medecin/dossiers');
      }, 2000);
    } catch (err) {
      console.error('Erreur lors de la création du dossier:', err);
      setError(err.message || 'Erreur lors de la création du dossier.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900">Créer un Dossier Médical</h2>
        <p className="text-gray-600 mt-2">Créez un nouveau dossier médical pour un patient</p>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded mb-6">
          {error}
        </div>
      )}

      {success && (
        <div className="bg-green-50 border border-green-200 text-green-700 px-4 py-3 rounded mb-6">
          Dossier créé avec succès ! Redirection en cours...
        </div>
      )}

      <form onSubmit={handleSubmit} className="bg-white rounded-2xl shadow-lg p-8 border border-gray-100">
        {/* Sélection du patient */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            <FiUser className="inline mr-2" size={16} />
            Patient *
          </label>
          <select
            name="patientId"
            value={formData.patientId}
            onChange={handleChange}
            required
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          >
            <option value="">Sélectionner un patient</option>
            {patients.map(patient => (
              <option key={patient._id} value={patient._id}>
                {patient.fullName} - {patient.email}
              </option>
            ))}
          </select>
        </div>

        {/* Groupe sanguin */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">Groupe Sanguin</label>
          <select
            name="groupeSanguin"
            value={formData.groupeSanguin}
            onChange={handleChange}
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          >
            <option value="">Sélectionner un groupe sanguin</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
          </select>
        </div>

        {/* Résumé médical */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            <FiFileText className="inline mr-2" size={16} />
            Résumé Médical
          </label>
          <textarea
            name="resumeMedical"
            value={formData.resumeMedical}
            onChange={handleChange}
            rows={4}
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Résumé médical du patient..."
          />
        </div>

        {/* Antécédents */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            <FiAlertCircle className="inline mr-2" size={16} />
            Antécédents Médicaux
          </label>
          <div className="flex gap-2 mb-2">
            <input
              type="text"
              value={newAntecedent}
              onChange={(e) => setNewAntecedent(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addAntecedent())}
              placeholder="Ajouter un antécédent"
              className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
            <button
              type="button"
              onClick={addAntecedent}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-all"
            >
              Ajouter
            </button>
          </div>
          <div className="space-y-2">
            {formData.antecedents.map((antecedent, index) => (
              <div key={index} className="flex items-center justify-between bg-gray-50 px-4 py-2 rounded-lg">
                <span>{antecedent.antecedent}</span>
                <button
                  type="button"
                  onClick={() => removeAntecedent(index)}
                  className="text-red-600 hover:text-red-800"
                >
                  Supprimer
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Allergies */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            <FiHeart className="inline mr-2" size={16} />
            Allergies
          </label>
          <div className="flex gap-2 mb-2">
            <input
              type="text"
              value={newAllergie}
              onChange={(e) => setNewAllergie(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addAllergie())}
              placeholder="Ajouter une allergie"
              className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
            <button
              type="button"
              onClick={addAllergie}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-all"
            >
              Ajouter
            </button>
          </div>
          <div className="space-y-2">
            {formData.allergies.map((allergie, index) => (
              <div key={index} className="flex items-center justify-between bg-gray-50 px-4 py-2 rounded-lg">
                <span>{allergie.allergie}</span>
                <button
                  type="button"
                  onClick={() => removeAllergie(index)}
                  className="text-red-600 hover:text-red-800"
                >
                  Supprimer
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Contact d'urgence */}
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Contact d'Urgence</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Nom</label>
              <input
                type="text"
                name="contactUrgence.nom"
                value={formData.contactUrgence.nom}
                onChange={handleChange}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Téléphone</label>
              <input
                type="tel"
                name="contactUrgence.telephone"
                value={formData.contactUrgence.telephone}
                onChange={handleChange}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Lien</label>
              <input
                type="text"
                name="contactUrgence.lien"
                value={formData.contactUrgence.lien}
                onChange={handleChange}
                placeholder="ex: Époux, Parent, etc."
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
          </div>
        </div>

        {/* Boutons */}
        <div className="flex gap-4">
          <button
            type="submit"
            disabled={loading}
            className="flex-1 bg-blue-500 text-white py-3 rounded-lg hover:bg-blue-600 transition-all font-medium disabled:opacity-50"
          >
            {loading ? 'Création...' : 'Créer le dossier'}
          </button>
          <button
            type="button"
            onClick={() => navigate('/medecin/dossiers')}
            className="px-6 py-3 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-all"
          >
            Annuler
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreerDossier;
