import React, { useEffect, useState } from "react";
import { axiosInstance } from "../api/endpoint";

const MesDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchDossiers = async () => {
    try {
      // Pour médecin connecté
      const res = await axiosInstance.get("/medecin/dossiers");
      setDossiers(res.data);
      setLoading(false);
    } catch (err) {
      console.error("Erreur récupération dossiers:", err);
      alert(err.response?.data?.message || "Erreur serveur");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDossiers();
  }, []);

  if (loading) return <p>Chargement des dossiers...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Mes Dossiers Patients</h2>
      {dossiers.length === 0 && <p>Aucun dossier trouvé.</p>}
      {dossiers.map((dossier) => (
        <div key={dossier._id} style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "10px" }}>
          <p><strong>Patient:</strong> {dossier.patient.fullName} ({dossier.patient.email})</p>
          <p><strong>Médecin:</strong> {dossier.medecin.fullName} ({dossier.medecin.email})</p>
          <p><strong>Résumé Médical:</strong> {dossier.resumeMedical}</p>
          <p><strong>Groupe Sanguin:</strong> {dossier.groupeSanguin}</p>
          <p><strong>Antécédents:</strong> {dossier.antecedents.map(a => a.libelle).join(", ")}</p>
          <p><strong>Allergies:</strong> {dossier.allergies.map(a => a.libelle).join(", ")}</p>
          <p><strong>Contact urgence:</strong> {dossier.contactUrgence.nom} ({dossier.contactUrgence.telephone}) – {dossier.contactUrgence.lien}</p>
        </div>
      ))}
    </div>
  );
};

export default MesDossiers;
