import React, { useState, useEffect } from 'react';
import { Link, useNavigate, Routes, Route, useLocation } from 'react-router-dom';
import { 
  getAuthToken, 
  decodeToken, 
  removeAuthToken 
} from '../api/endpoint';
import {
  FiUsers, 
  FiActivity, 
  FiCalendar, 
  FiDollarSign, 
  FiBarChart, 
  FiBell, 
  FiSettings, 
  FiLogOut, 
  FiMenu, 
  FiFileText,
  FiUser
} from 'react-icons/fi';

// Import des pages admin
import Patients from './admin/Patients';
import Medecins from './admin/Medecins';
import Services from './admin/Services';
import Rdv from './admin/Rdv';
import Finances from './admin/Finances';
import Moderateurs from './admin/Moderateurs';

// Import de recharts pour les graphiques
import {
  LineChart, Line, AreaChart, Area, XAxis, YAxis, 
  CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

/* ---------------- DASHBOARD HOME ---------------- */
const DashboardHome = ({ stats, recentActivity }) => {
  const tendanceData = [
    { mois: 'Août', patients: 1100, medecins: 85, rdv: 420 },
    { mois: 'Sept', patients: 1150, medecins: 86, rdv: 430 },
    { mois: 'Oct', patients: 1180, medecins: 87, rdv: 440 },
    { mois: 'Nov', patients: 1200, medecins: 88, rdv: 450 },
    { mois: 'Déc', patients: 1220, medecins: 88, rdv: 455 },
    { mois: 'Jan', patients: 1247, medecins: 89, rdv: 456 },
  ];

  const revenusData = [
    { mois: 'Août', revenus: 2100000 },
    { mois: 'Sept', revenus: 2200000 },
    { mois: 'Oct', revenus: 2300000 },
    { mois: 'Nov', revenus: 2350000 },
    { mois: 'Déc', revenus: 2400000 },
    { mois: 'Jan', revenus: 2450000 },
  ];

  const statsConfig = [
    { label: 'Patients', value: stats.totalPatients, icon: FiUsers, color: 'blue' },
    { label: 'Médecins', value: stats.totalMedecins, icon: FiUser, color: 'emerald' },
    { label: 'Rendez-vous', value: stats.totalRdv, icon: FiCalendar, color: 'violet' },
    { label: 'Revenus', value: `${stats.revenue.toLocaleString()} CFA`, icon: FiDollarSign, color: 'amber' },
  ];

  return (
    <>
      {/* Header */}
      <div className="mb-10">
        <h2 className="text-4xl font-bold tracking-tight text-gray-900">
          Dashboard Administrateur
        </h2>
        <p className="text-gray-500 mt-3 text-lg">
          Supervision complète de la plateforme my virtuActive
        </p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-8 mb-12">
        {statsConfig.map((item, index) => (
          <div
            key={index}
            className="group bg-white/80 backdrop-blur p-8 rounded-2xl shadow-lg hover:shadow-2xl 
                     border border-gray-100 transition-all duration-300 hover:-translate-y-1"
          >
            <div className="flex justify-between items-center">
              <div>
                <p className="text-sm font-medium text-gray-500 uppercase tracking-wide">
                  {item.label}
                </p>
                <p className="text-3xl font-bold text-gray-900 mt-2">
                  {item.value}
                </p>
              </div>
              <item.icon
                size={34}
                className={`p-3 rounded-xl bg-${item.color}-50 text-${item.color}-600 
                           group-hover:scale-110 transition-transform duration-200`}
              />
            </div>
          </div>
        ))}
      </div>

      {/* Graphiques */}
      <div className="grid grid-cols-1 xl:grid-cols-2 gap-10 mb-12">
        <div className="bg-white rounded-2xl p-8 shadow-xl border border-gray-100">
          <h3 className="text-2xl font-semibold text-gray-900 mb-6">
            Activité générale (6 mois)
          </h3>
          <ResponsiveContainer width="100%" height={320}>
            <AreaChart data={tendanceData}>
              <defs>
                <linearGradient id="patientsGradient" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="0%" stopColor="#3b82f6" stopOpacity={0.4}/>
                  <stop offset="100%" stopColor="#dbeafe" stopOpacity={0.1}/>
                </linearGradient>
                <linearGradient id="medecinsGradient" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="0%" stopColor="#10b981" stopOpacity={0.4}/>
                  <stop offset="100%" stopColor="#d1fae5" stopOpacity={0.1}/>
                </linearGradient>
                <linearGradient id="rdvGradient" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="0%" stopColor="#8b5cf6" stopOpacity={0.4}/>
                  <stop offset="100%" stopColor="#ede9fe" stopOpacity={0.1}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
              <XAxis dataKey="mois" stroke="#6b7280" />
              <YAxis stroke="#6b7280" />
              <Tooltip contentStyle={{ borderRadius: '12px', border: '1px solid #e5e7eb' }} />
              <Legend />
              <Area 
                type="monotone" 
                dataKey="patients" 
                stroke="#3b82f6" 
                fill="url(#patientsGradient)" 
              />
              <Area 
                type="monotone" 
                dataKey="medecins" 
                stroke="#10b981" 
                fill="url(#medecinsGradient)" 
              />
              <Area 
                type="monotone" 
                dataKey="rdv" 
                stroke="#8b5cf6" 
                fill="url(#rdvGradient)" 
              />
            </AreaChart>
          </ResponsiveContainer>
        </div>

        <div className="bg-white rounded-2xl p-8 shadow-xl border border-gray-100">
          <h3 className="text-2xl font-semibold text-gray-900 mb-6">Revenus générés</h3>
          <ResponsiveContainer width="100%" height={320}>
            <LineChart data={revenusData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
              <XAxis dataKey="mois" stroke="#6b7280" />
              <YAxis 
                stroke="#6b7280" 
                tickFormatter={(value) => `${(value / 1000000).toFixed(1)}M`}
              />
              <Tooltip 
                formatter={(value) => [`${value.toLocaleString()} CFA`, 'Revenus']}
                contentStyle={{ borderRadius: '12px', border: '1px solid #e5e7eb' }}
              />
              <Legend />
              <Line
                type="monotone"
                dataKey="revenus"
                stroke="#f59e0b"
                strokeWidth={4}
                dot={{ fill: '#f59e0b', strokeWidth: 2, r: 6 }}
                activeDot={{ r: 8, strokeWidth: 2 }}
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Activité récente + Actions rapides */}
      <div className="grid grid-cols-1 xl:grid-cols-3 gap-10">
        <div className="xl:col-span-2 bg-white rounded-2xl p-8 shadow-xl border border-gray-100">
          <h3 className="text-2xl font-semibold text-gray-900 mb-6 flex items-center gap-2">
            Activité récente
            <span className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full font-medium">
              Live
            </span>
          </h3>
          <div className="space-y-4">
            {recentActivity.map((activity) => (
              <div
                key={activity.id}
                className="flex items-start p-5 bg-gradient-to-r from-slate-50 to-blue-50 
                          rounded-xl hover:shadow-md transition-all duration-200 border border-slate-100"
              >
                <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-indigo-600 
                               rounded-xl flex items-center justify-center text-white font-bold flex-shrink-0 shadow-md">
                  ✓
                </div>
                <div className="ml-4 flex-1 min-w-0">
                  <p className="font-medium text-gray-900 leading-tight">{activity.action}</p>
                  <p className="text-sm text-gray-500 mt-1 truncate">{activity.user}</p>
                </div>
                <span className="text-sm font-medium text-blue-600 ml-4 whitespace-nowrap flex-shrink-0">
                  {activity.time}
                </span>
              </div>
            ))}
          </div>
        </div>

        <div className="bg-white rounded-2xl p-8 shadow-xl border border-gray-100">
          <h3 className="text-2xl font-semibold text-gray-900 mb-8">Actions rapides</h3>
          <div className="space-y-4">
            <Link 
              to="/admin/utilisateurs"
              className="block w-full bg-gradient-to-r from-blue-600 to-indigo-600 text-white 
                        p-6 rounded-xl text-center font-semibold shadow-lg hover:shadow-xl 
                        hover:-translate-y-1 hover:scale-[1.02] transition-all duration-200 flex items-center justify-center gap-3"
            >
              <FiPlus size={20} />
              Nouvel utilisateur
            </Link>
            <Link 
              to="/admin/services"
              className="block w-full bg-gradient-to-r from-emerald-600 to-teal-600 text-white 
                        p-6 rounded-xl text-center font-semibold shadow-lg hover:shadow-xl 
                        hover:-translate-y-1 hover:scale-[1.02] transition-all duration-200 flex items-center justify-center gap-3"
            >
              <FiBarChart size={20} />
              Gérer services
            </Link>
          </div>
        </div>
      </div>
    </>
  );
};

/* ---------------- ADMIN DASHBOARD LAYOUT ---------------- */
const AdminDashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState(null);

  const [stats] = useState({
    totalPatients: 1247,
    totalMedecins: 89,
    totalRdv: 456,
    revenue: 2450000
  });

  const [recentActivity, setRecentActivity] = useState([]);

  // Navigation items
  const navigation = [
    { name: 'Dashboard', href: '/admin', icon: FiBarChart, exact: true },
    { name: 'Patients', href: '/admin/patients', icon: FiUsers },
    { name: 'Médecins', href: '/admin/medecins', icon: FiUser },
    { name: 'Rendez-vous', href: '/admin/rdv', icon: FiCalendar },
    { name: 'Services', href: '/admin/services', icon: FiActivity },
    { name: 'Finances', href: '/admin/finances', icon: FiDollarSign },
    { name: 'Modérateurs', href: '/admin/moderateurs', icon: FiUsers },
    { name: 'Paramètres', href: '/admin/settings', icon: FiSettings },
  ];

  useEffect(() => {
    const token = getAuthToken();
    if (!token) {
      navigate('/login');
      return;
    }

    const decoded = decodeToken(token);
    if (decoded?.role !== 'Admin') {
      navigate('/dashboard');
      return;
    }

    setUser(decoded);
    setRecentActivity([
      { id: 1, action: 'Nouveau patient inscrit', user: 'Mamadou Diop', time: '2 min' },
      { id: 2, action: 'Médecin validé', user: 'Dr Awa Ndiaye', time: '15 min' },
      { id: 3, action: 'RDV confirmé', user: 'Service Pédiatrie', time: '1h' }
    ]);
  }, [navigate]);

  const handleLogout = () => {
    removeAuthToken();
    navigate('/login');
  };

  const isActive = (href, exact = false) => {
    return exact 
      ? location.pathname === href 
      : location.pathname.startsWith(href);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-slate-100">
      {/* Mobile Sidebar Overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 z-40 bg-black/50 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Sidebar */}
      <aside className={`fixed inset-y-0 left-0 z-50 w-64 bg-white shadow-xl border-r border-gray-200 
                       transform lg:translate-x-0 transition-transform duration-300 ease-in-out
                       ${sidebarOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'}`}>
        <div className="flex flex-col h-full">
          {/* Sidebar Header */}
          <div className="p-6 border-b border-gray-100">
            <h2 className="text-xl font-bold text-gray-900">my virtuActive</h2>
            <p className="text-sm text-gray-500 mt-1">Administration</p>
          </div>

          {/* Navigation */}
          <nav className="flex-1 p-4 space-y-2 overflow-y-auto">
            {navigation.map((item) => (
              <Link
                key={item.name}
                to={item.href}
                className={`flex items-center gap-3 p-4 rounded-xl transition-all duration-200 group 
                          ${isActive(item.href, item.exact)
                            ? 'bg-blue-600 text-white shadow-lg shadow-blue-200'
                            : 'text-gray-700 hover:bg-blue-50 hover:shadow-md hover:text-blue-900'
                          }`}
                onClick={() => setSidebarOpen(false)}
              >
                <item.icon 
                  size={22}
                  className={`transition-transform group-hover:scale-110 flex-shrink-0
                            ${isActive(item.href, item.exact) ? 'text-white' : ''}`}
                />
                <span className="font-medium">{item.name}</span>
              </Link>
            ))}
          </nav>

          {/* Logout */}
          <div className="p-4 border-t border-gray-100 mt-auto">
            <button
              onClick={handleLogout}
              className="w-full flex items-center gap-3 p-4 text-left text-gray-700 hover:bg-red-50 
                        hover:text-red-600 rounded-xl transition-all duration-200 group"
            >
              <FiLogOut size={20} className="group-hover:scale-110 transition-transform" />
              <span className="font-medium">Déconnexion</span>
            </button>
          </div>
        </div>
      </aside>

      <div className="lg:ml-64">
        {/* Header */}
        <header className="sticky top-0 z-40 bg-white/90 backdrop-blur-md border-b border-gray-100 shadow-sm">
          <div className="h-16 px-6 flex items-center justify-between">
            {/* Mobile menu button */}
            <button
              className="lg:hidden p-2 rounded-lg hover:bg-gray-100"
              onClick={() => setSidebarOpen(true)}
            >
              <FiMenu size={22} className="text-gray-700" />
            </button>

            {/* Header actions */}
            <div className="flex items-center gap-4">
              <button className="p-2 text-gray-500 hover:text-gray-900 hover:bg-gray-100 rounded-lg transition-colors">
                <FiBell size={20} />
              </button>
              
              <div className="flex items-center gap-3 p-2 hover:bg-gray-100 rounded-xl cursor-pointer group">
                <div className="w-9 h-9 rounded-full bg-gradient-to-r from-blue-600 to-indigo-600 
                              text-white flex items-center justify-center font-bold text-sm shadow-md
                              group-hover:scale-105 transition-transform">
                  {user?.email?.[0]?.toUpperCase() || 'A'}
                </div>
                <span className="hidden md:block text-sm font-medium text-gray-900">
                  {user?.email || 'Administrateur'}
                </span>
              </div>
            </div>
          </div>
        </header>

        {/* Main Content */}
        <main className="p-8">
          <Routes>
            <Route 
              path="/" 
              element={<DashboardHome stats={stats} recentActivity={recentActivity} />} 
            />
            <Route path="/patients" element={<Patients />} />
            <Route path="/medecins" element={<Medecins />} />
            <Route path="/rdv" element={<Rdv />} />
            <Route path="/services" element={<Services />} />
            <Route path="/finances" element={<Finances />} />
            <Route path="/moderateurs" element={<Moderateurs />} />
            <Route path="*" element={<DashboardHome stats={stats} recentActivity={recentActivity} />} />
          </Routes>
        </main>
      </div>
    </div>
  );
};

// Exports
export const AdminLayout = AdminDashboard;
export const AdminLayoutWrapper = AdminDashboard;
export { DashboardHome };
export default AdminDashboard;
