import React, { useState, useEffect, useCallback } from 'react';
import { postData, getData } from '../api/endpoint';

const CreateDossierModal = ({ onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    patientId: '',
    patientFullName: '',
    patientEmail: '',
    sexe: '',
    groupeSanguin: '',
    resumeMedical: '',
    allergies: ''
  });
  
  // ‚úÖ VOTRE ID M√âDECIN ICI üëá
  const MEDECIN_ID = "66f8d4e5f123456789abcdef0"; // ‚Üê REMPLACEZ !
  
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingPatients, setLoadingPatients] = useState(true);
  const [showPatientsList, setShowPatientsList] = useState(false);

  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    try {
      setLoadingPatients(true);
      const data = await getData('/patients');
      setPatients(Array.isArray(data) ? data : []);
    } catch (err) {
      setPatients([]);
    } finally {
      setLoadingPatients(false);
    }
  };

  const selectPatient = (patient) => {
    setFormData({
      patientId: patient._id,
      patientFullName: patient.fullName || patient.nom || '',
      patientEmail: patient.email || '',
      sexe: patient.sexe || '',
      groupeSanguin: '',
      resumeMedical: '',
      allergies: ''
    });
    setShowPatientsList(false);
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.patientId || !formData.resumeMedical) {
      alert('‚ùå Patient + R√©sum√© obligatoires');
      return;
    }

    setLoading(true);

    try {
      const payload = {
        patientId: formData.patientId,
        medecinId: MEDECIN_ID,
        resumeMedical: formData.resumeMedical,
        groupeSanguin: formData.groupeSanguin,
        sexe: formData.sexe,
        allergies: formData.allergies ? [{ allergie: formData.allergies }] : [],
        antecedents: []
      };

      console.log('üì§ Envoi:', payload);
      await postData('/dossier', payload);
      
      alert('‚úÖ Dossier cr√©√© !');
      onSuccess();
      
    } catch (err) {
      // Fallback local
      const dossierLocal = { _id: `local_${Date.now()}`, ...formData, medecinId: MEDECIN_ID };
      localStorage.setItem('dossiersMedecin', JSON.stringify([dossierLocal]));
      alert('‚úÖ Sauvegard√© localement');
      onSuccess();
    } finally {
      setLoading(false);
    }
  };

  const inputStyle = {
    padding: '1rem 1.2rem', border: '2px solid #e0e0e0', 
    borderRadius: '8px', fontSize: '1rem', width: '100%'
  };

  return (
    <div style={{ position: 'fixed', zIndex: 10000, inset: 0, background: 'rgba(0,0,0,0.6)', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <div style={{ background: 'white', padding: '2.5rem', borderRadius: '20px', maxWidth: '700px', width: '90%' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '2rem' }}>
          <h2 style={{ color: '#1976d2', fontSize: '1.8rem' }}>‚ûï Nouveau Dossier</h2>
          <button onClick={onClose} style={{ fontSize: '2rem', cursor: 'pointer' }}>√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          {/* PATIENT */}
          <button 
            type="button" onClick={() => setShowPatientsList(!showPatientsList)}
            style={{ width: '100%', padding: '1.2rem', background: formData.patientId ? '#4caf50' : '#2196f3', color: 'white', border: 'none', borderRadius: '12px' }}
          >
            {formData.patientId ? `‚úÖ ${formData.patientFullName}` : 'üë§ S√©lectionner patient'}
          </button>

          {showPatientsList && patients.map(patient => (
            <div key={patient._id} onClick={() => selectPatient(patient)} style={{ padding: '1rem', cursor: 'pointer', borderBottom: '1px solid #eee' }}>
              {patient.fullName} - {patient.email}
            </div>
          ))}

          {formData.patientId && (
            <div style={{ padding: '1rem', background: '#e8f5e8', borderRadius: '8px', margin: '1rem 0' }}>
              ‚úÖ {formData.patientFullName}
            </div>
          )}

          {/* CHAMPS */}
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', marginBottom: '1rem' }}>
            <div>
              <label>ü©∏ Groupe sanguin</label>
              <select name="groupeSanguin" value={formData.groupeSanguin} onChange={handleInputChange} style={inputStyle}>
                <option value="">Choisir...</option>
                <option value="A+">A+</option><option value="A-">A-</option>
                <option value="B+">B+</option><option value="B-">B-</option>
                <option value="AB+">AB+</option><option value="AB-">AB-</option>
                <option value="O+">O+</option><option value="O-">O-</option>
              </select>
            </div>
            <div>
              <label>‚öïÔ∏è Sexe</label>
              <select name="sexe" value={formData.sexe} onChange={handleInputChange} style={inputStyle} required>
                <option value="">Choisir...</option>
                <option value="M">Masculin üë®</option>
                <option value="F">F√©minin üë©</option>
              </select>
            </div>
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label>üìù R√©sum√© m√©dical *</label>
            <textarea name="resumeMedical" value={formData.resumeMedical} onChange={handleInputChange} 
              rows={3} style={{...inputStyle, minHeight: '100px'}} required />
          </div>

          <div style={{ display: 'flex', gap: '1rem' }}>
            <button type="submit" disabled={loading} style={{ flex: 1, padding: '1rem', background: '#4caf50', color: 'white', border: 'none', borderRadius: '8px' }}>
              {loading ? '‚è≥ Cr√©ation...' : 'üíæ Cr√©er Dossier'}
            </button>
            <button type="button" onClick={onClose} style={{ flex: 1, padding: '1rem', background: '#f44336', color: 'white', border: 'none', borderRadius: '8px' }}>
              ‚ùå Annuler
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateDossierModal;
