const BASE_URL = process.env.REACT_APP_API_BASE_URL || "http://localhost:5000/api";

// =================== TOKEN MANAGEMENT ===================
export const TOKEN_KEY = "token";  // ← CHANGÉ "authToken" → "token"

export const getAuthToken = () => localStorage.getItem(TOKEN_KEY);
export const setAuthToken = (token) => localStorage.setItem(TOKEN_KEY, token);
export const removeAuthToken = () => localStorage.removeItem(TOKEN_KEY);

export const decodeToken = (token) => {
  if (!token) return null;
  try {
    return JSON.parse(atob(token.split('.')[1]));
  } catch {
    return null;
  }
};

// Fonction pour vérifier si un token JWT est valide (non expiré)
export const isTokenValid = (token) => {
  if (!token) return false;
  try {
    const decoded = decodeToken(token);
    if (!decoded || !decoded.exp) return false;
    // Vérifier si le token n'est pas expiré (exp est en secondes, Date.now() en millisecondes)
    return decoded.exp * 1000 > Date.now();
  } catch {
    return false;
  }
};

// =================== API FUNCTIONS ===================
const getData = async (endpoint) => {
  try {
    const token = getAuthToken();
    const headers = { 'Content-Type': 'application/json' };
    if (token) headers.Authorization = `Bearer ${token}`;

    const url = endpoint.startsWith('/') ? `${BASE_URL}${endpoint}` : `${BASE_URL}/${endpoint}`;
    const response = await fetch(url, { headers });
    
    if (!response.ok) throw new Error(`Erreur ${response.status}`);
    return await response.json();
  } catch (error) {
    console.error("Error fetching data:", error);
    throw error;
  }
};

const postData = async (endpoint, payload) => {
  try {
    const token = getAuthToken();
    const headers = { 'Content-Type': 'application/json' };
    if (token) headers.Authorization = `Bearer ${token}`;

    const url = endpoint.startsWith('/') ? `${BASE_URL}${endpoint}` : `${BASE_URL}/${endpoint}`;
    const response = await fetch(url, {
      method: "POST",
      headers,
      body: JSON.stringify(payload),
    });
    
    if (!response.ok) {
      const errorData = await response.json().catch(() => ({ message: `Erreur ${response.status}` }));
      throw new Error(errorData.message || `Erreur ${response.status}`);
    }
    return await response.json();
  } catch (error) {
    console.error("Error posting data:", error);
    throw error;
  }
};

// Fonction PUT pour mettre à jour des données
const putData = async (endpoint, payload) => {
  try {
    const token = getAuthToken();
    const headers = { 'Content-Type': 'application/json' };
    if (token) headers.Authorization = `Bearer ${token}`;

    const url = endpoint.startsWith('/') ? `${BASE_URL}${endpoint}` : `${BASE_URL}/${endpoint}`;
    const response = await fetch(url, {
      method: "PUT",
      headers,
      body: JSON.stringify(payload),
    });
    
    if (!response.ok) {
      const errorData = await response.json().catch(() => ({ message: `Erreur ${response.status}` }));
      throw new Error(errorData.message || `Erreur ${response.status}`);
    }
    return await response.json();
  } catch (error) {
    console.error("Error updating data:", error);
    throw error;
  }
};

// Fonction DELETE pour supprimer des données
const deleteData = async (endpoint) => {
  try {
    const token = getAuthToken();
    const headers = { 'Content-Type': 'application/json' };
    if (token) headers.Authorization = `Bearer ${token}`;

    const url = endpoint.startsWith('/') ? `${BASE_URL}${endpoint}` : `${BASE_URL}/${endpoint}`;
    const response = await fetch(url, {
      method: "DELETE",
      headers,
    });
    
    if (!response.ok) {
      const errorData = await response.json().catch(() => ({ message: `Erreur ${response.status}` }));
      throw new Error(errorData.message || `Erreur ${response.status}`);
    }
    return await response.json();
  } catch (error) {
    console.error("Error deleting data:", error);
    throw error;
  }
};

// =================== EXPORTS ===================
export { postData, putData, deleteData };
export default getData;
