import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { 
  getAuthToken, 
  decodeToken, 
  removeAuthToken,
  getData
} from '../api/endpoint';
import {
  FiUsers, 
  FiFileText, 
  FiBarChart, 
  FiBell, 
  FiSettings, 
  FiLogOut, 
  FiMenu, 
  FiX,
  FiShield,
  FiAlertCircle,
  FiCheckCircle
} from 'react-icons/fi';

// Composant Dashboard principal (page d'accueil moderateur)
const DashboardHome = ({ user }) => {
  const [stats, setStats] = useState({
    utilisateurs: 0,
    contenus: 0,
    signalements: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadStats();
  }, []);

  const loadStats = async () => {
    try {
      setLoading(true);
      const usersData = await getData('/users').catch(() => []);
      
      setStats({
        utilisateurs: usersData.length,
        contenus: 0, // √Ä impl√©menter selon votre mod√®le
        signalements: 0 // √Ä impl√©menter selon votre mod√®le
      });
    } catch (err) {
      console.error('Erreur lors du chargement des statistiques:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900">Tableau de Bord Mod√©rateur</h2>
        <p className="text-gray-600 mt-2">Bienvenue, {user?.fullName || user?.email || 'Mod√©rateur'}</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
        <div className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Utilisateurs</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.utilisateurs}
              </p>
              <p className="text-sm text-blue-600 mt-1 font-medium">Total</p>
            </div>
            <FiUsers size={32} className="text-blue-500 group-hover:scale-110 transition-transform" />
          </div>
        </div>

        <div className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Contenus</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.contenus}
              </p>
