import React, { useState, useEffect } from 'react';
import { postData, getData } from '../api/endpoint';

const CreateDossier = () => {
  const [formData, setFormData] = useState({
    patientId: '',
    motif: '',
    description: ''
  });
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingPatients, setLoadingPatients] = useState(true);
  const [error, setError] = useState('');

  // ‚úÖ CHARGEMENT AUTO DES PATIENTS
  useEffect(() => {
    const fetchPatients = async () => {
      try {
        setLoadingPatients(true);
        const patientsData = await getData('/patients');
        setPatients(patientsData);
      } catch (error) {
        setError('Erreur chargement patients: ' + error.message);
      } finally {
        setLoadingPatients(false);
      }
    };
    fetchPatients();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.patientId || !formData.motif) {
      setError('‚ùå S√©lectionnez un patient et ajoutez un motif');
      return;
    }

    try {
      setLoading(true);
      setError('');

      console.log('üì§ Envoi:', formData);
      
      await postData('/dossiers', formData);
      
      alert('‚úÖ Dossier cr√©√© avec succ√®s !');
      setFormData({ patientId: '', motif: '', description: '' });
    } catch (error) {
      console.error('‚ùå Erreur:', error.message);
      setError(error.message || 'Erreur cr√©ation dossier');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '700px' }}>
      <h2>üìÅ Nouveau dossier m√©dical</h2>

      {error && (
        <div style={{
          backgroundColor: '#fee',
          color: '#c33',
          padding: '12px',
          borderRadius: '6px',
          marginBottom: '20px',
          borderLeft: '4px solid #c33'
        }}>
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {/* ‚úÖ S√âLECTION PATIENT - DROPDOWN */}
        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>
            üë§ Patient <span style={{ color: 'red' }}>*</span>
          </label>
          
          {loadingPatients ? (
            <div style={{ padding: '12px', color: '#666' }}>
              ‚è≥ Chargement patients...
            </div>
          ) : patients.length === 0 ? (
            <div style={{ padding: '12px', color: '#c33' }}>
              Aucun patient trouv√©
            </div>
          ) : (
            <select
              name="patientId"
              value={formData.patientId}
              onChange={handleChange}
              required
              style={{
                width: '100%',
                padding: '12px',
                border: '2px solid #ddd',
                borderRadius: '6px',
                fontSize: '16px',
                backgroundColor: 'white'
              }}
            >
              <option value="">S√©lectionnez un patient...</option>
              {patients.map((patient) => (
                <option key={patient._id} value={patient._id}>
                  {patient.nom} {patient.prenom} ({patient.telephone || 'Pas de tel'})
                </option>
              ))}
            </select>
          )}
        </div>

        {/* MOTIF */}
        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            üìã Motif <span style={{ color: 'red' }}>*</span>
          </label>
          <input
            type="text"
            name="motif"
            placeholder="ex: Consultation annuelle, Douleurs abdominales..."
            value={formData.motif}
            onChange={handleChange}
            required
            style={{
              width: '100%',
              padding: '12px',
              border: '1px solid #ddd',
              borderRadius: '6px',
              fontSize: '16px'
            }}
          />
        </div>

        {/* DESCRIPTION */}
        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            üìù Description / Diagnostic
          </label>
          <textarea
            name="description"
            placeholder="Sympt√¥mes d√©taill√©s, ant√©c√©dents, observations..."
            value={formData.description}
            onChange={handleChange}
            rows={5}
            style={{
              width: '100%',
              padding: '12px',
              border: '1px solid #ddd',
              borderRadius: '6px',
              fontSize: '16px',
              fontFamily: 'monospace',
              resize: 'vertical'
            }}
          />
        </div>

        <button 
          type="submit" 
          disabled={loading || !formData.patientId || !formData.motif}
          style={{
            width: '100%',
            padding: '16px',
            backgroundColor: loading ? '#ccc' : '#28a745',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            fontSize: '18px',
            fontWeight: 'bold',
            cursor: loading ? 'not-allowed' : 'pointer',
            transition: 'background-color 0.2s'
          }}
        >
          {loading ? '‚è≥ Cr√©ation en cours...' : '‚úÖ Cr√©er le dossier m√©dical'}
        </button>
      </form>

      {/* DEBUG INFO */}
      {process.env.NODE_ENV === 'development' && (
        <details style={{ marginTop: '20px', padding: '10px', background: '#f8f9fa' }}>
          <summary>Debug (dev only)</summary>
          <pre>{JSON.stringify({ patientsCount: patients.length, formData }, null, 2)}</pre>
        </details>
      )}
    </div>
  );
};

export default CreateDossier;
