import React, { useState, useEffect } from "react";
import MesDossiersPatient from "./MesDossiersPatient";
import { axiosInstance } from "../api/endpoint";

const PatientDashboard = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Récupérer les infos patient
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axiosInstance.get("/users/me"); // Crée cette route backend pour infos patient
        setUser(response.data);
        setLoading(false);
      } catch (err) {
        console.error("Erreur récupération infos patient :", err);
        setError("Impossible de récupérer vos informations");
        setLoading(false);
      }
    };

    fetchUser();
  }, []);

  if (loading) return <p>Chargement du dashboard...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>Bienvenue {user?.fullName}</h1>

      <section style={{ marginTop: "20px" }}>
        <h2>Mes Informations</h2>
        <p><strong>Email:</strong> {user?.email}</p>
        <p><strong>Date de naissance:</strong> {user?.date_of_birth || "N/A"}</p>
        <p><strong>Sexe:</strong> {user?.sexe || "N/A"}</p>
      </section>

      <section style={{ marginTop: "20px" }}>
        <MesDossiersPatient />
      </section>

      <section style={{ marginTop: "20px" }}>
        <h2>Mes Rendez-vous</h2>
        {/* Ici on pourra créer un composant MesRendezVousPatient */}
        <p>Liste des rendez-vous à venir...</p>
      </section>
    </div>
  );
};

export default PatientDashboard;
