import React, { useState, useEffect, useCallback } from 'react';
import { Link, Routes, Route, useLocation } from 'react-router-dom';

const MedecinDashboard = () => {
  const location = useLocation();
  const [profile, setProfile] = useState(null);
  const [stats, setStats] = useState({
    patients: 0,
    dossiers: 0,
    rdvs: 0,
    consultations: 0
  });
  const [loading, setLoading] = useState(true);
  const [notifications, setNotifications] = useState([]);
  const [patientsList, setPatientsList] = useState([]);

  // API simulÃ©e (remplacez par votre vraie API)
  const apiCall = async (endpoint) => {
    // Mock API responses - Remplacez par vos vrais appels fetch/axios
    const mockData = {
      '/medecin/profile': {
        nom: 'NDIAYE',
        prenom: 'Moustapha',
        email: 'moustapha.ndiaye@govsanitaire.sn',
        specialite: 'Cardiologue',
        numeroOrdre: 'SN-MED-2024-001',
        etablissement: 'CHU de Fann',
        statut: 'ValidÃ©'
      },
      '/patients/medecin/count': 24,
      '/dossiers/medecin/count': 18,
      '/rendezvous/medecin/count': 5,
      '/consultations/medecin/count': 12,
      '/patients/medecin?limit=50': [
        { _id: '1', fullName: 'Aminata DIOP', email: 'aminata@test.com' },
        { _id: '2', fullName: 'Fatou SOW', email: 'fatou@test.com' },
        { _id: '3', fullName: 'Mamadou BA', email: 'mamadou@test.com' }
      ]
    };
    return new Promise((resolve) => {
      setTimeout(() => resolve(mockData[endpoint] || null), 500);
    });
  };

  const loadDashboardData = useCallback(async () => {
    try {
      setLoading(true);

      const [profileData, patientsCount, dossiersCount, rdvsCount, consultationsCount, patientsData] = await Promise.all([
        apiCall('/medecin/profile'),
        apiCall('/patients/medecin/count'),
        apiCall('/dossiers/medecin/count'),
        apiCall('/rendezvous/medecin/count'),
        apiCall('/consultations/medecin/count'),
        apiCall('/patients/medecin?limit=50')
      ]);

      setProfile(profileData);
      setStats({
        patients: patientsCount || 0,
        dossiers: dossiersCount || 0,
        rdvs: rdvsCount || 0,
        consultations: consultationsCount || 0
      });
      setPatientsList(patientsData || []);

      setLoading(false);
    } catch (error) {
      console.error('Erreur chargement:', error);
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadDashboardData();
  }, [loadDashboardData]);

  const addNotification = (message, type = 'success') => {
    const notif = { id: Date.now(), message, type, date: new Date().toLocaleTimeString('fr-FR') };
    setNotifications(prev => [notif, ...prev.slice(0, 4)]);
    setTimeout(() => setNotifications(prev => prev.filter(n => n.id !== notif.id)), 5000);
  };

  if (loading) {
    return <LoadingScreen />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      <Notifications notifications={notifications} />
      
      <aside className="hidden lg:block lg:w-72 lg:h-screen lg:fixed lg:inset-y-0 lg:z-40 bg-white/90 backdrop-blur-xl shadow-2xl border-r border-blue-100">
        <ProfileCard profile={profile} />
        <NavigationMenu activePath={location.pathname} stats={stats} />
      </aside>

      <main className="lg:ml-72 p-4 lg:p-8 pb-24 lg:pb-8">
        <Routes>
          <Route path="/" element={<DashboardHome stats={stats} profile={profile} addNotification={addNotification} />} />
          <Route path="/patients" element={<PatientsPageStub stats={stats} />} />
          <Route path="/patients/:id" element={<PatientDetailStub />} />
          <Route path="/dossiers" element={<DossiersPageStub stats={stats} />} />
          <Route path="/dossier/:id" element={<DossierDetailStub />} />
          <Route path="/creer-dossier" element={<CreerDossierForm patients={patientsList} addNotification={addNotification} />} />
          <Route path="/consultations" element={<ConsultationsPageStub stats={stats} />} />
          <Route path="/rendezvous" element={<RendezVousPageStub stats={stats} />} />
          <Route path="/ordonnances" element={<OrdonnancesPageStub stats={stats} />} />
          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </main>
    </div>
  );
};

// FORMULAIRE CRÃ‰ATION DOSSIER COMPLET âœ…
const CreerDossierForm = ({ patients, addNotification }) => {
  const [formData, setFormData] = useState({
    patientId: '',
    resumeMedical: '',
    groupeSanguin: '',
    antecedents: [],
    allergies: [],
    contactUrgence: { nom: '', telephone: '', lien: '' },
    statut: 'ACTIF'
  });
  const [newAntecedent, setNewAntecedent] = useState('');
  const [newAllergie, setNewAllergie] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith('contactUrgence.')) {
      const field = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        contactUrgence: { ...prev.contactUrgence, [field]: value }
      }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
    setError('');
  };

  const addAntecedent = () => {
    if (newAntecedent.trim()) {
      setFormData(prev => ({
        ...prev,
        antecedents: [...prev.antecedents, { antecedent: newAntecedent.trim() }]
      }));
      setNewAntecedent('');
    }
  };

  const removeAntecedent = (index) => {
    setFormData(prev => ({
      ...prev,
      antecedents: prev.antecedents.filter((_, i) => i !== index)
    }));
  };

  const addAllergie = () => {
    if (newAllergie.trim()) {
      setFormData(prev => ({
        ...prev,
        allergies: [...prev.allergies, { allergie: newAllergie.trim() }]
      }));
      setNewAllergie('');
    }
  };

  const removeAllergie = (index) => {
    setFormData(prev => ({
      ...prev,
      allergies: prev.allergies.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.patientId) {
      setError('Veuillez sÃ©lectionner un patient');
      return;
    }

    try {
      setLoading(true);
      setError('');

      // Simulation API POST (remplacez par votre vraie API)
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      console.log('Dossier crÃ©Ã©:', formData);
      addNotification('âœ… Dossier mÃ©dical crÃ©Ã© avec succÃ¨s !');
      
      // Reset form
      setFormData({
        patientId: '',
        resumeMedical: '',
        groupeSanguin: '',
        antecedents: [],
        allergies: [],
        contactUrgence: { nom: '', telephone: '', lien: '' },
        statut: 'ACTIF'
      });
    } catch (err) {
      setError('Erreur lors de la crÃ©ation du dossier');
      addNotification('âŒ Erreur crÃ©ation dossier', 'error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-8">
      <div className="bg-white/90 backdrop-blur-xl rounded-3xl shadow-2xl p-10 border border-emerald-100">
        <div className="text-center mb-12">
          <div className="text-6xl mx-auto mb-6">ðŸ“„</div>
          <h1 className="text-4xl font-bold text-gray-900 mb-2">CrÃ©er Dossier MÃ©dical</h1>
          <p className="text-xl text-emerald-700">Formulaire complet et sÃ©curisÃ©</p>
        </div>

        {error && (
          <div className="bg-red-50 border-2 border-red-200 text-red-800 p-6 rounded-2xl mb-8">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-8">
          {/* PATIENT */}
          <div>
            <label className="block text-lg font-bold text-gray-900 mb-4">ðŸ‘¤ Patient *</label>
            <select
              name="patientId"
              value={formData.patientId}
              onChange={handleChange}
              className="w-full p-5 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-emerald-100 focus:border-emerald-500 text-lg"
              required
            >
              <option value="">SÃ©lectionner un patient</option>
              {patients.map(patient => (
                <option key={patient._id} value={patient._id}>
                  {patient.fullName} - {patient.email}
                </option>
              ))}
            </select>
          </div>

          {/* GROUPE SANGUIN & RÃ‰SUMÃ‰ */}
          <div className="grid md:grid-cols-2 gap-8">
            <div>
              <label className="block text-lg font-bold text-gray-900 mb-4">ðŸ©º Groupe Sanguin</label>
              <select name="groupeSanguin" value={formData.groupeSanguin} onChange={handleChange} className="w-full p-5 border-2 border-gray-200 rounded-2xl">
                <option value="">Non spÃ©cifiÃ©</option>
                <option value="A+">A+</option><option value="A-">A-</option>
                <option value="B+">B+</option><option value="B-">B-</option>
                <option value="AB+">AB+</option><option value="AB-">AB-</option>
                <option value="O+">O+</option><option value="O-">O-</option>
              </select>
            </div>
            <div>
              <label className="block text-lg font-bold text-gray-900 mb-4">ðŸ“ RÃ©sumÃ© MÃ©dical</label>
              <textarea
                name="resumeMedical"
                value={formData.resumeMedical}
                onChange={handleChange}
                rows="4"
                className="w-full p-5 border-2 border-gray-200 rounded-2xl focus:ring-4 focus:ring-blue-100 resize-vertical"
                placeholder="AntÃ©cÃ©dents principaux, traitements en cours..."
              />
            </div>
          </div>

          {/* ANTECEDENTS + ALLERGIES */}
          <div className="grid md:grid-cols-2 gap-8">
            <div className="bg-red-50/50 p-8 rounded-3xl border-2 border-red-100">
              <label className="block text-xl font-bold text-red-800 mb-6">â¤ï¸ AntÃ©cÃ©dents</label>
              <div className="flex gap-4 mb-6">
                <input
                  value={newAntecedent}
                  onChange={(e) => setNewAntecedent(e.target.value)}
                  className="flex-1 p-4 border-2 border-red-200 rounded-2xl focus:ring-4 focus:ring-red-100"
                  placeholder="DiabÃ¨te, hypertension..."
                />
                <button type="button" onClick={addAntecedent} className="px-8 py-4 bg-red-600 text-white rounded-2xl font-bold hover:bg-red-700">
                  Ajouter
                </button>
              </div>
              <div className="space-y-3 max-h-40 overflow-y-auto">
                {formData.antecedents.map((ant, index) => (
                  <div key={index} className="flex items-center justify-between p-4 bg-white rounded-2xl border">
                    <span className="font-semibold text-red-800">{ant.antecedent}</span>
                    <button type="button" onClick={() => removeAntecedent(index)} className="text-red-500 hover:text-red-700 text-2xl font-bold">Ã—</button>
                  </div>
                ))}
              </div>
            </div>

            <div className="bg-orange-50/50 p-8 rounded-3xl border-2 border-orange-100">
              <label className="block text-xl font-bold text-orange-800 mb-6">âš ï¸ Allergies</label>
              <div className="flex gap-4 mb-6">
                <input
                  value={newAllergie}
                  onChange={(e) => setNewAllergie(e.target.value)}
                  className="flex-1 p-4 border-2 border-orange-200 rounded-2xl focus:ring-4 focus:ring-orange-100"
                  placeholder="PÃ©nicilline, arachides..."
                />
                <button type="button" onClick={addAllergie} className="px-8 py-4 bg-orange-600 text-white rounded-2xl font-bold hover:bg-orange-700">
                  Ajouter
                </button>
              </div>
              <div className="space-y-3 max-h-40 overflow-y-auto">
                {formData.allergies.map((all, index) => (
                  <div key={index} className="flex items-center justify-between p-4 bg-white rounded-2xl border">
                    <span className="font-semibold text-orange-800">{all.allergie}</span>
                    <button type="button" onClick={() => removeAllergie(index)} className="text-orange-500 hover:text-orange-700 text-2xl font-bold">Ã—</button>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* CONTACT URGENCE */}
          <div className="bg-blue-50/50 p-8 rounded-3xl border-2 border-blue-100">
            <label className="block text-xl font-bold text-blue-800 mb-6">ðŸ“± Contact d'Urgence</label>
            <div className="grid md:grid-cols-3 gap-6">
              <input name="contactUrgence.nom" value={formData.contactUrgence.nom} onChange={handleChange} placeholder="Nom complet" className="p-5 border-2 border-blue-200 rounded-2xl focus:ring-4 focus:ring-blue-100" />
              <input name="contactUrgence.telephone" value={formData.contactUrgence.telephone} onChange={handleChange} placeholder="TÃ©lÃ©phone" className="p-5 border-2 border-blue-200 rounded-2xl focus:ring-4 focus:ring-blue-100" />
              <input name="contactUrgence.lien" value={formData.contactUrgence.lien} onChange={handleChange} placeholder="Lien (PÃ¨re, MÃ¨re...)" className="p-5 border-2 border-blue-200 rounded-2xl focus:ring-4 focus:ring-blue-100" />
            </div>
          </div>

          {/* ACTIONS */}
          <div className="flex flex-col sm:flex-row gap-6 pt-12">
            <button
              type="submit"
              disabled={loading || !formData.patientId}
              className="flex-1 bg-gradient-to-r from-emerald-600 to-green-700 text-white py-6 px-12 rounded-3xl font-bold text-xl shadow-2xl hover:shadow-3xl hover:scale-[1.02] transition-all disabled:opacity-50"
            >
              {loading ? 'ðŸ’¾ CrÃ©ation en cours...' : 'ðŸ’¾ CrÃ©er le Dossier MÃ©dical'}
            </button>
            <Link to="/medecin/dossiers" className="px-12 py-6 border-2 border-gray-300 text-gray-800 rounded-3xl font-bold hover:bg-gray-50 hover:shadow-xl">
              â† Retour Dossiers
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

// TOUS LES COMPOSANTS INTÃ‰GRÃ‰S (aucun import manquant)
const ProfileCard = ({ profile }) => (
  <div className="p-8 border-b border-blue-100 bg-gradient-to-b from-blue-50 to-white">
    <div className="w-20 h-20 bg-gradient-to-br from-blue-500 to-indigo-600 text-white rounded-3xl flex items-center justify-center mx-auto mb-6 shadow-2xl font-bold text-2xl">
      ðŸ‘¨â€âš•ï¸
    </div>
    <h2 className="text-2xl font-bold text-gray-900 text-center mb-3">Dr. {profile?.prenom} {profile?.nom}</h2>
    <div className="bg-gradient-to-r from-blue-500/10 to-indigo-500/10 p-4 rounded-2xl border border-blue-100 mb-4">
      <p className="font-semibold text-blue-800 text-center text-lg">{profile?.specialite}</p>
      <p className="text-xs text-blue-600 text-center">{profile?.etablissement}</p>
    </div>
    <div className="text-center space-y-1 text-sm">
      <span className="inline-block px-3 py-1 bg-green-100 text-green-800 rounded-full text-xs font-semibold">{profile?.statut}</span>
      <p className="text-gray-600">{profile?.numeroOrdre}</p>
    </div>
  </div>
);

const NavigationMenu = ({ activePath, stats }) => {
  const navItems = [
    { path: '/medecin', icon: 'ðŸ“Š', label: 'Dashboard' },
    { path: '/medecin/patients', icon: 'ðŸ‘¥', label: 'Patients', count: stats.patients },
    { path: '/medecin/dossiers', icon: 'ðŸ“', label: 'Dossiers', count: stats.dossiers },
    { path: '/medecin/rendezvous', icon: 'ðŸ“…', label: 'Rendez-vous', count: stats.rdvs },
    { path: '/medecin/consultations', icon: 'ðŸ©º', label: 'Consultations', count: stats.consultations },
    { path: '/medecin/creer-dossier', icon: 'âœ¨', label: 'Nouveau Dossier' },
  ];

  return (
    <nav className="p-4 space-y-2 mt-6">
      {navItems.map(({ path, icon, label, count }) => {
        const isActive = activePath.includes(path.split('/')[2]) || activePath === path;
        return (
          <Link key={path} to={path} className={`group flex items-center p-4 rounded-2xl font-semibold transition-all duration-300 shadow-sm border hover:shadow-xl hover:-translate-x-1 ${
            isActive ? 'bg-gradient-to-r from-blue-500 to-indigo-600 text-white shadow-lg' : 'bg-white/70 hover:bg-blue-50 text-gray-800 border-gray-200'
          }`}>
            <span className={`mr-4 text-2xl ${isActive ? 'text-white' : 'text-blue-600 group-hover:scale-110 transition-transform'}`}>{icon}</span>
            <span className="flex-1">{label}</span>
            {count && <span className={`ml-2 px-3 py-1 rounded-full text-xs font-bold ${isActive ? 'bg-white/20 text-white' : 'bg-blue-100 text-blue-800'}`}>{count}</span>}
          </Link>
        );
      })}
    </nav>
  );
};

const DashboardHome = ({ stats, profile, addNotification }) => (
  <div className="max-w-7xl mx-auto space-y-8">
    <div className="bg-white/90 backdrop-blur-xl p-12 rounded-3xl shadow-2xl border">
      <div className="flex flex-col lg:flex-row lg:items-center gap-6">
        <div>
          <h1 className="text-5xl font-bold bg-gradient-to-r from-gray-900 to-gray-700 bg-clip-text text-transparent mb-4">
            Bonjour Dr. {profile?.prenom}
          </h1>
          <p className="text-xl text-gray-700">
            {stats.rdvs} rendez-vous â€¢ {stats.dossiers} dossiers â€¢ {stats.patients} patients
          </p>
        </div>
        <Link to="/medecin/creer-dossier" className="px-10 py-5 bg-gradient-to-r from-emerald-500 to-green-600 text-white font-bold text-lg rounded-2xl shadow-2xl hover:shadow-3xl self-start" onClick={() => addNotification('ðŸš€ CrÃ©ation dossier')}>
          âœ¨ Nouveau Dossier
        </Link>
      </div>
    </div>
    
    <div className="grid lg:grid-cols-4 gap-6">
      <StatCard icon="ðŸ‘¥" title={stats.patients} label="Patients" to="/medecin/patients" />
      <StatCard icon="ðŸ“" title={stats.dossiers} label="Dossiers" to="/medecin/dossiers" />
      <StatCard icon="ðŸ“…" title={stats.rdvs} label="RDV" to="/medecin/rendezvous" />
      <StatCard icon="ðŸ©º" title={stats.consultations} label="Consultations" to="/medecin/consultations" />
    </div>
  </div>
);

