import React, { useEffect, useState } from "react";
import * as api from "../api/endpoint";

const DoctorDashboard = () => {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    setLoading(true);
    try {
      const data = await api.getData("/patients"); // accessible aux médecins
      setPatients(data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError(err.message || "Erreur lors du chargement des patients");
      setLoading(false);
    }
  };

  const viewDossier = (patient) => {
    alert(`Dossier de ${patient.fullName}:\nDate de naissance: ${patient.date_of_birth || "Non renseignée"}\nSexe: ${patient.sexe || "Non renseigné"}`);
    // Ici tu peux naviguer vers une page détail dossier
  };

  return (
    <div className="doctor-interface">
      <header className="doctor-header">
        <h1>Dashboard Médecin</h1>
      </header>

      {loading && <p>Chargement des patients...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {!loading && !error && (
        <div className="patients-list">
          {patients.length === 0 ? (
            <p>Aucun patient enregistré.</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Nom</th>
                  <th>Email</th>
                  <th>Date de naissance</th>
                  <th>Sexe</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {patients.map((patient) => (
                  <tr key={patient._id}>
                    <td>{patient.fullName}</td>
                    <td>{patient.email}</td>
                    <td>{patient.date_of_birth ? new Date(patient.date_of_birth).toLocaleDateString() : "Non renseignée"}</td>
                    <td>{patient.sexe || "Non renseigné"}</td>
                    <td>
                      <button onClick={() => viewDossier(patient)}>Voir Dossier</button>
                      {/* Ici tu peux ajouter boutons pour consultations ou ordonnances */}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}
    </div>
  );
};

export default DoctorDashboard;
