import React, { useEffect, useState } from "react";
import { getData, deleteData, putData } from "../api/endpoint";
const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [editingUserId, setEditingUserId] = useState(null);
  const [formData, setFormData] = useState({ fullName: "", email: "", role: "" });

  // Charger tous les utilisateurs
  const loadUsers = async () => {
    try {
      const data = await getData("/users"); // <-- correspond à ton backend
      setUsers(data);
    } catch (error) {
      console.error("Erreur fetching users:", error);
    }
  };

  useEffect(() => {
    loadUsers();
  }, []);

  // Handle change du formulaire
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Commencer l'édition
  const handleEdit = (user) => {
    setEditingUserId(user._id);
    setFormData({
      fullName: user.fullName,
      email: user.email,
      role: user.role,
    });
  };

  // Annuler l'édition
  const handleCancel = () => {
    setEditingUserId(null);
    setFormData({ fullName: "", email: "", role: "" });
  };

  // Soumettre la mise à jour
  const handleUpdate = async () => {
    try {
      await putData(`/user/${editingUserId}`, formData);
      loadUsers(); // Recharger les utilisateurs
      handleCancel();
    } catch (error) {
      console.error("Erreur update user:", error);
    }
  };

  // Supprimer un utilisateur
  const handleDelete = async (id) => {
    if (!window.confirm("Voulez-vous vraiment supprimer cet utilisateur ?")) return;
    try {
      await deleteData(`/user/${id}`);
      loadUsers();
    } catch (error) {
      console.error("Erreur delete user:", error);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">Dashboard Admin</h2>
      <table className="w-full border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            <th className="border px-2 py-1">Nom</th>
            <th className="border px-2 py-1">Email</th>
            <th className="border px-2 py-1">Rôle</th>
            <th className="border px-2 py-1">Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td className="border px-2 py-1">
                {editingUserId === user._id ? (
                  <input
                    type="text"
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleChange}
                  />
                ) : (
                  user.fullName
                )}
              </td>
              <td className="border px-2 py-1">
                {editingUserId === user._id ? (
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                  />
                ) : (
                  user.email
                )}
              </td>
              <td className="border px-2 py-1">
                {editingUserId === user._id ? (
                  <select name="role" value={formData.role} onChange={handleChange}>
                    <option value="Patient">Patient</option>
                    <option value="Medecin">Médecin</option>
                    <option value="Admin">Admin</option>
                    <option value="SuperAdmin">Super Admin</option>
                  </select>
                ) : (
                  user.role
                )}
              </td>
              <td className="border px-2 py-1 space-x-2">
                {editingUserId === user._id ? (
                  <>
                    <button onClick={handleUpdate} className="bg-green-500 text-white px-2 rounded">Save</button>
                    <button onClick={handleCancel} className="bg-gray-400 text-white px-2 rounded">Cancel</button>
                  </>
                ) : (
                  <>
                    <button onClick={() => handleEdit(user)} className="bg-blue-500 text-white px-2 rounded">Edit</button>
                    <button onClick={() => handleDelete(user._id)} className="bg-red-500 text-white px-2 rounded">Delete</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
