import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PatientDashboard = () => {
  const [user, setUser] = useState(null);
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Récupérer le token depuis localStorage
  const token = localStorage.getItem('token');

  // Config axios avec token
  const axiosConfig = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  // Récupérer profil patient
  const fetchUser = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/users/me', axiosConfig);
      setUser(res.data);
    } catch (err) {
      console.error("Erreur récupération infos patient :", err);
      setError('Impossible de récupérer vos informations.');
    }
  };

  // Récupérer dossiers patient
  const fetchDossiers = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/patient/dossiers', axiosConfig);
      setDossiers(res.data);
    } catch (err) {
      console.error("Erreur récupération dossiers :", err);
      setError('Impossible de récupérer vos dossiers.');
    }
  };

  // Initialisation
  useEffect(() => {
    const init = async () => {
      setLoading(true);
      await fetchUser();
      await fetchDossiers();
      setLoading(false);
    };
    init();
  }, []);

  if (loading) return <p>Chargement...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h1>Bienvenue, {user?.fullName}</h1>
      <h2>Vos informations :</h2>
      <ul>
        <li>Email : {user?.email}</li>
        <li>Sexe : {user?.sexe}</li>
        <li>Date de naissance : {user?.date_of_birth}</li>
        <li>Statut : {user?.statut}</li>
      </ul>

      <h2>Vos dossiers médicaux :</h2>
      {dossiers.length === 0 ? (
        <p>Aucun dossier trouvé.</p>
      ) : (
        <ul>
          {dossiers.map(d => (
            <li key={d._id}>
              <strong>{d.patientName}</strong> - Motif : {d.motif} - Diagnostic : {d.diagnostic} - Date : {new Date(d.date).toLocaleDateString()}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default PatientDashboard;
