import React, { useState } from 'react';

const MedecinDashboard = () => {
  const [activeTab, setActiveTab] = useState('ordonnances');
  const [patients, setPatients] = useState([
    { id: 1, name: 'Diop Aminata', age: 32, lastVisit: '08/02/2026', status: 'actif', phone: '+221 77 123 4567', email: 'aminata@email.com' },
    { id: 2, name: 'Ndiaye Fatou', age: 45, lastVisit: '07/02/2026', status: 'actif', phone: '+221 77 234 5678', email: 'fatou@email.com' },
    { id: 3, name: 'Sow Mamadou', age: 28, lastVisit: '06/02/2026', status: 'en attente', phone: '+221 77 345 6789', email: 'mamadou@email.com' }
  ]);
  
  const [ordonnances, setOrdonnances] = useState([
    { id: 1, patientId: 1, patientName: 'Diop Aminata', date: '08/02/2026', medicaments: ['ParacÃ©tamol 1g', 'Amoxicilline 500mg'], statut: 'complÃ©tÃ©e' },
    { id: 2, patientId: 2, patientName: 'Ndiaye Fatou', date: '07/02/2026', medicaments: ['IbuprofÃ¨ne 400mg'], statut: 'en attente' },
    { id: 3, patientId: 3, patientName: 'Sow Mamadou', date: '06/02/2026', medicaments: ['PÃ©nicilline', 'Doliprane'], statut: 'complÃ©tÃ©e' }
  ]);

  const [rendezVous, setRendezVous] = useState([
    { id: 1, patientId: 1, patientName: 'Diop Aminata', date: '09/02/2026', heure: '10:00', motif: 'Consultation gÃ©nÃ©rale', statut: 'confirmÃ©' },
    { id: 2, patientId: 2, patientName: 'Ndiaye Fatou', date: '09/02/2026', heure: '14:00', motif: 'Suivi hypertension', statut: 'confirmÃ©' },
    { id: 3, patientId: 3, patientName: 'Sow Mamadou', date: '10/02/2026', heure: '11:00', motif: 'Visite de contrÃ´le', statut: 'en attente' },
    { id: 4, patientId: 1, patientName: 'Diop Aminata', date: '08/02/2026', heure: '09:00', motif: 'Consultation urgente', statut: 'complÃ©tÃ©' }
  ]);
  
  const [ordonnance, setOrdonnance] = useState({
    patient: '',
    medicaments: [
      { nom: 'ParacÃ©tamol 1g', dose: '1cp', frequence: '3x/jour', duree: '5 jours' },
      { nom: 'Amoxicilline 500mg', dose: '1cp', frequence: '2x/jour', duree: '7 jours' }
    ]
  });

  const addMedicament = () => {
    setOrdonnance({
      ...ordonnance,
      medicaments: [...ordonnance.medicaments, { nom: '', dose: '', frequence: '', duree: '' }]
    });
  };

  const updateMedicament = (index, field, value) => {
    const newMeds = [...ordonnance.medicaments];
    newMeds[index][field] = value;
    setOrdonnance({ ...ordonnance, medicaments: newMeds });
  };

  const deleteMedicament = (index) => {
    const newMeds = ordonnance.medicaments.filter((_, i) => i !== index);
    setOrdonnance({ ...ordonnance, medicaments: newMeds });
  };

  const saveOrdonnance = () => {
    if (!ordonnance.patient) {
      alert('âŒ Veuillez sÃ©lectionner un patient !');
      return;
    }
    const newOrd = {
      id: ordonnances.length + 1,
      patientId: parseInt(ordonnance.patient),
      patientName: patients.find(p => p.id === parseInt(ordonnance.patient))?.name,
      date: new Date().toLocaleDateString('fr-FR'),
      medicaments: ordonnance.medicaments.map(m => m.nom),
      statut: 'en attente'
    };
    setOrdonnances([...ordonnances, newOrd]);
    alert('âœ… ORDONNANCE GÃ‰NÃ‰RÃ‰E ET ENVOYÃ‰E !');
    setOrdonnance({ patient: '', medicaments: [{ nom: '', dose: '', frequence: '', duree: '' }] });
  };

  const generatePDF = () => {
    alert('ðŸ“„ GÃ©nÃ©ration du PDF en cours...\nâœ… Fichier tÃ©lÃ©chargÃ©');
  };

  const sendSMS = () => {
    if (!ordonnance.patient) {
      alert('âŒ Veuillez sÃ©lectionner un patient !');
      return;
    }
    alert('ðŸ“± SMS envoyÃ© avec succÃ¨s Ã  : ' + ordonnance.patient);
  };

  const updatePatient = (value) => {
    setOrdonnance({ ...ordonnance, patient: value });
  };

  const stats = {
    patients: patients.length,
    ordonnances: ordonnances.length,
    rdvsToday: rendezVous.filter(r => r.date === '08/02/2026').length,
    revenus: 1250000
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #10b981 0%, #059669 50%, #047857 100%)',
      padding: '20px',
      fontFamily: 'system-ui, -apple-system, sans-serif',
      color: 'white'
    }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        
        {/* HEADER */}
        <div style={{
          background: 'rgba(255,255,255,0.15)',
          backdropFilter: 'blur(20px)',
          borderRadius: '24px',
          boxShadow: '0 25px 50px rgba(0,0,0,0.2)',
          border: '4px solid rgba(255,255,255,0.3)',
          padding: '30px',
          marginBottom: '30px'
        }}>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
            <h1 style={{
              fontSize: '48px',
              fontWeight: '900',
              background: 'linear-gradient(135deg, white 0%, #f0fdf4 100%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              margin: 0
            }}>
              ðŸ©º DOCTORAT - GOVAIMED
            </h1>
            <div style={{
              display: 'flex', flexDirection: 'column', gap: '12px',
              fontSize: '20px', background: 'rgba(255,255,255,0.2)',
              padding: '24px', borderRadius: '16px', width: '100%'
            }}>
              <div style={{ display: 'flex', gap: '24px', flexWrap: 'wrap' }}>
                <span>ðŸ‘¨â€âš•ï¸ Dr Ndiaye Mamadou</span>
                <span>ðŸ¥ Clinique Dakar</span>
              </div>
              <div style={{ display: 'flex', gap: '24px', flexWrap: 'wrap' }}>
                <span>ðŸ“ž +221 77 123 4567</span>
                <span>ðŸ©º MÃ©decine GÃ©nÃ©rale</span>
                <span style={{ padding: '8px 16px', background: 'rgba(255,255,255,0.3)', borderRadius: '12px' }}>âœ… En ligne</span>
              </div>
            </div>
            <div style={{ textAlign: 'right', width: '100%' }}>
              <div style={{ fontSize: '36px', fontWeight: '900', color: '#f0fdf4' }}>
                {stats.revenus.toLocaleString()} FCFA
              </div>
              <div style={{ fontSize: '18px' }}>Consultations ce mois</div>
            </div>
          </div>
        </div>

        {/* NAVIGATION */}
        <div style={{
          background: 'rgba(255,255,255,0.15)',
          backdropFilter: 'blur(20px)',
          borderRadius: '24px',
          boxShadow: '0 25px 50px rgba(0,0,0,0.2)',
          border: '4px solid rgba(255,255,255,0.3)',
          padding: '20px',
          marginBottom: '30px',
          display: 'flex',
          gap: '15px',
          flexWrap: 'wrap'
        }}>
          <TabButton label="ðŸ’Š Ordonnances" active={activeTab === 'ordonnances'} onClick={() => setActiveTab('ordonnances')} />
          <TabButton label={`ðŸ‘¥ Patients (${stats.patients})`} active={activeTab === 'patients'} onClick={() => setActiveTab('patients')} />
          <TabButton label={`ðŸ“… RDV (${stats.rdvsToday})`} active={activeTab === 'rdv'} onClick={() => setActiveTab('rdv')} />
          <TabButton label="ðŸ“Š Statistiques" active={activeTab === 'stats'} onClick={() => setActiveTab('stats')} />
        </div>

        {/* STATS */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '24px',
          marginBottom: '40px'
        }}>
          <StatBox title="ðŸ‘¥ Mes Patients" value={stats.patients.toString()} color="#f0fdf4" textColor="#166534" />
          <StatBox title="ðŸ’Š Ordonnances" value={stats.ordonnances.toString()} color="#dbeafe" textColor="#1e40af" />
          <StatBox title="ðŸ“… RDV Aujourd'hui" value={stats.rdvsToday.toString()} color="#fef3c7" textColor="#92400e" />
          <StatBox title="ðŸ’° Revenus" value={stats.revenus.toLocaleString()} color="#d1fae5" textColor="#065f46" />
        </div>

        {/* CONTENU DYNAMIQUE */}
        {activeTab === 'ordonnances' && (
          <OrdonnancesSection 
            ordonnance={ordonnance}
            addMedicament={addMedicament}
            updateMedicament={updateMedicament}
            deleteMedicament={deleteMedicament}
            saveOrdonnance={saveOrdonnance}
            generatePDF={generatePDF}
            sendSMS={sendSMS}
            updatePatient={updatePatient}
            patients={patients}
            ordonnancesListe={ordonnances}
          />
        )}
        {activeTab === 'patients' && <PatientsSection patients={patients} />}
        {activeTab === 'rdv' && <RdvSection rendezVous={rendezVous} />}
        {activeTab === 'stats' && <StatsSection stats={stats} />}
      </div>
    </div>
  );
};

const OrdonnancesSection = ({ ordonnance, addMedicament, updateMedicament, deleteMedicament, saveOrdonnance, generatePDF, sendSMS, updatePatient, patients, ordonnancesListe }) => (
  <div style={{
    background: 'rgba(255,255,255,0.15)',
    backdropFilter: 'blur(20px)',
    borderRadius: '24px',
    boxShadow: '0 25px 50px rgba(0,0,0,0.2)',
    border: '4px solid rgba(255,255,255,0.3)',
    padding: '30px',
    marginBottom: '30px'
  }}>
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px', flexDirection: 'column', gap: '20px' }}>
      <div style={{ textAlign: 'center' }}>
        <h2 style={{ fontSize: '40px', fontWeight: '900', color: 'white', margin: 0 }}>
          ðŸ’Š Nouvelle Ordonnance
        </h2>
        <p style={{ fontSize: '24px', margin: '5px 0 0 0' }}>Prescription sÃ©curisÃ©e et numÃ©rique</p>
      </div>
    </div>

    {/* SÃ‰LECTION PATIENT */}
    <div style={{ marginBottom: '30px' }}>
      <label style={{ display: 'block', fontSize: '20px', marginBottom: '12px', color: 'white' }}>
        ðŸ‘¤ Patient :
      </label>
      <select 
        value={ordonnance.patient}
        onChange={(e) => updatePatient(e.target.value)}
        style={{
          width: '100%', padding: '16px 20px', fontSize: '18px',
          border: '2px solid rgba(255,255,255,0.3)',
          borderRadius: '16px', background: 'rgba(255,255,255,0.9)',
          color: '#1f2937'
        }}
      >
        <option value="">SÃ©lectionner un patient...</option>
        {patients.map(p => (
          <option key={p.id} value={p.id}>{p.name} - {p.age} ans</option>
        ))}
      </select>
    </div>

    {/* LISTE MÃ‰DICAMENTS */}
    <div style={{ marginBottom: '30px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h3 style={{ fontSize: '28px', color: 'white', margin: 0 }}>ðŸ’Š MÃ©dicaments</h3>
        <button onClick={addMedicament} style={{
          padding: '12px 24px', background: 'rgba(255,255,255,0.9)',
          color: '#1f2937', fontWeight: '700', borderRadius: '12px',
          border: 'none', cursor: 'pointer'
        }}>
          âž• Ajouter
        </button>
      </div>
      
      {ordonnance.medicaments.map((med, index) => (
        <MedicamentRow 
          key={index}
          medicament={med}
          index={index}
          onUpdate={updateMedicament}
          onDelete={deleteMedicament}
        />
      ))}
    </div>

    {/* BOUTONS ACTION */}
    <div style={{ display: 'flex', gap: '20px', justifyContent: 'center', flexWrap: 'wrap', marginBottom: '40px' }}>
      <button style={primaryBtnStyle} onClick={saveOrdonnance}>
        âœ… FINALISER ORDONNANCE
      </button>
      <button style={secondaryBtnStyle} onClick={generatePDF}>
        ðŸ“„ PDF
      </button>
      <button style={secondaryBtnStyle} onClick={sendSMS}>
        ðŸ“± SMS Patient
      </button>
    </div>

    {/* LISTE DES ORDONNANCES */}
    <div style={{ marginTop: '40px', borderTop: '2px solid rgba(255,255,255,0.2)', paddingTop: '30px' }}>
      <h3 style={{ fontSize: '28px', color: 'white', marginBottom: '20px' }}>ðŸ“‹ Mes Ordonnances</h3>
      <div style={{ display: 'grid', gap: '15px' }}>
        {ordonnancesListe.map((ord) => (
          <div key={ord.id} style={{
            background: 'rgba(255,255,255,0.1)', padding: '20px', borderRadius: '16px',
            border: '2px solid rgba(255,255,255,0.2)'
          }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap', gap: '15px' }}>
              <div>
                <p style={{ margin: '0 0 8px 0', fontSize: '18px', fontWeight: '700' }}>{ord.patientName}</p>
                <p style={{ margin: '5px 0', fontSize: '14px' }}>ðŸ“… {ord.date}</p>
                <p style={{ margin: '5px 0', fontSize: '14px' }}>ðŸ’Š {ord.medicaments.join(', ')}</p>
              </div>
              <div style={{ textAlign: 'right' }}>
                <span style={{
                  padding: '8px 16px',
                  background: ord.statut === 'complÃ©tÃ©e' ? '#10b981' : '#f59e0b',
                  color: 'white',
                  borderRadius: '12px',
                  fontSize: '14px',
                  fontWeight: '700'
                }}>
                  {ord.statut}
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  </div>
);

const MedicamentRow = ({ medicament, index, onUpdate, onDelete }) => (
  <div style={{
    display: 'grid', gridTemplateColumns: '2fr 1fr 1fr 1fr 60px',
    gap: '16px', padding: '20px', background: 'rgba(255,255,255,0.1)',
    borderRadius: '16px', marginBottom: '12px', alignItems: 'center'
  }}>
    <input 
      placeholder="Nom du mÃ©dicament"
      value={medicament.nom}
      onChange={(e) => onUpdate(index, 'nom', e.target.value)}
      style={inputStyle}
    />
    <input placeholder="Dose" value={medicament.dose} onChange={(e) => onUpdate(index, 'dose', e.target.value)} style={inputStyle} />
    <input placeholder="FrÃ©q." value={medicament.frequence} onChange={(e) => onUpdate(index, 'frequence', e.target.value)} style={inputStyle} />
    <input placeholder="DurÃ©e" value={medicament.duree} onChange={(e) => onUpdate(index, 'duree', e.target.value)} style={inputStyle} />
    <button onClick={() => onDelete(index)} style={{
      padding: '12px', background: '#ef4444', color: 'white',
      border: 'none', borderRadius: '12px', cursor: 'pointer', fontSize: '18px'
    }}>ðŸ—‘ï¸</button>
  </div>
);

const PatientsSection = ({ patients }) => (
  <div style={{
    background: 'rgba(255,255,255,0.15)',
    backdropFilter: 'blur(20px)',
    borderRadius: '24px',
    boxShadow: '0 25px 50px rgba(0,0,0,0.2)',
    border: '4px solid rgba(255,255,255,0.3)',
    padding: '30px'
  }}>
    <h2 style={{ fontSize: '40px', color: 'white', marginBottom: '30px', margin: 0, marginBottom: '30px' }}>ðŸ‘¥ Mes Patients</h2>
    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(350px, 1fr))', gap: '20px' }}>
      {patients.map((patient) => (
        <div key={patient.id} style={{
          background: 'rgba(255,255,255,0.1)',
          padding: '25px',
          borderRadius: '16px',
          border: '2px solid rgba(255,255,255,0.2)',
          transition: 'all 0.3s ease',
          cursor: 'pointer',
          _hover: { background: 'rgba(255,255,255,0.15)' }
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '15px' }}>
            <div style={{
              width: '60px',
              height: '60px',
              borderRadius: '50%',
              background: 'linear-gradient(135deg, #10b981, #059669)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '28px'
            }}>
              ðŸ‘¤
            </div>
            <div>
              <p style={{ margin: '0', fontSize: '20px', fontWeight: '700' }}>{patient.name}</p>
              <p style={{ margin: '5px 0 0 0', fontSize: '14px', opacity: '0.9' }}>ID: {patient.id}</p>
            </div>
          </div>
          <div style={{ borderTop: '2px solid rgba(255,255,255,0.2)', paddingTop: '15px' }}>
            <p style={{ margin: '10px 0', fontSize: '15px' }}>ðŸ“… Ã‚ge: <strong>{patient.age} ans</strong></p>
            <p style={{ margin: '10px 0', fontSize: '15px' }}>ðŸ“ž {patient.phone}</p>
            <p style={{ margin: '10px 0', fontSize: '15px' }}>ðŸ“§ {patient.email}</p>
            <p style={{ margin: '10px 0', fontSize: '15px' }}>ðŸ¥ DerniÃ¨re visite: {patient.lastVisit}</p>
            <div style={{ marginTop: '15px' }}>
              <span style={{
                padding: '8px 16px',
                background: patient.status === 'actif' ? '#10b981' : '#f59e0b',
                color: 'white',
                borderRadius: '12px',
                fontSize: '14px',
                fontWeight: '700'
              }}>
                âœ“ {patient.status}
              </span>
            </div>
          </div>
        </div>
      ))}
    </div>
  </div>
);

const RdvSection = ({ rendezVous }) => (
  <div style={{
    background: 'rgba(255,255,255,0.15)',
    backdropFilter: 'blur(20px)',
    borderRadius: '24px',
    boxShadow: '0 25px 50px rgba(0,0,0,0.2)',
    border: '4px solid rgba(255,255,255,0.3)',
    padding: '30px'
  }}>
    <h2 style={{ fontSize: '40px', color: 'white', marginBottom: '30px', margin: 0, marginBottom: '30px' }}>ðŸ“… Mes Rendez-Vous</h2>
    <div style={{ display: 'grid', gap: '15px' }}>
      {rendezVous.map((rdv) => (
        <div key={rdv.id} style={{
          background: 'rgba(255,255,255,0.1)',
          padding: '20px',
          borderRadius: '16px',
          border: '2px solid rgba(255,255,255,0.2)',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          flexWrap: 'wrap',
          gap: '20px'
        }}>
          <div style={{ flex: 1, minWidth: '250px' }}>
            <p style={{ margin: '0 0 10px 0', fontSize: '18px', fontWeight: '700' }}>ðŸ‘¤ {rdv.patientName}</p>
            <p style={{ margin: '8px 0', fontSize: '15px' }}>ðŸ“… {rdv.date} Ã  <strong>{rdv.heure}</strong></p>
            <p style={{ margin: '8px 0', fontSize: '15px' }}>ðŸ“ Motif: <strong>{rdv.motif}</strong></p>
          </div>
          <div style={{ textAlign: 'right' }}>
            <span style={{
              display: 'inline-block',
              padding: '10px 20px',
              background: rdv.statut === 'confirmÃ©' ? '#10b981' : rdv.statut === 'complÃ©tÃ©' ? '#6366f1' : '#f59e0b',
              color: 'white',
              borderRadius: '12px',
              fontSize: '14px',
              fontWeight: '700'
            }}>
              {rdv.statut === 'confirmÃ©' ? 'âœ“ ConfirmÃ©' : rdv.statut === 'complÃ©tÃ©' ? 'âœ“ ComplÃ©tÃ©' : 'â³ En attente'}
            </span>
          </div>
        </div>
      ))}
    </div>
  </div>
);

const StatsSection = ({ stats }) => (
  <div style={{
    background: 'rgba(255,255,255,0.15)',
    backdropFilter: 'blur(20px)',
    borderRadius: '24px',
    boxShadow: '0 25px 50px rgba(0,0,0,0.2)',
    border: '4px solid rgba(255,255,255,0.3)',
    padding: '30px'
  }}>
    <h2 style={{ fontSize: '40px', color: 'white', marginBottom: '40px', margin: 0, marginBottom: '40px' }}>ðŸ“Š Statistiques</h2>
    
    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '20px', marginBottom: '40px' }}>
      <StatCard title="ðŸ‘¥ Patients Actifs" value={stats.patients} icon="ðŸ©º" color="#10b981" />
