const ExamenForm = ({ patientId, onSubmit, patients }) => {
  const patient = patients.find(p => p.id === patientId);
  const [formData, setFormData] = useState({ type: '', titre: '', resultat: '' });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(patientId, formData);
  };

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-900 mb-8">ðŸ©º Nouveau RÃ©sultat d'Examen</h2>
      <div className="bg-orange-50 p-6 rounded-xl mb-8">
        <h3 className="font-bold text-lg text-orange-800">Patient: {patient?.prenom} {patient?.nom}</h3>
      </div>
      <form onSubmit={handleSubmit} className="space-y-6">
        <input type="text" placeholder="Type (scanner, analyse, etc.)" value={formData.type} onChange={e => setFormData({...formData, type: e.target.value})} className="w-full p-4 border rounded-xl" required />
        <input type="text" placeholder="Titre de l'examen" value={formData.titre} onChange={e => setFormData({...formData, titre: e.target.value})} className="w-full p-4 border rounded-xl" required />
        <textarea rows="3" placeholder="RÃ©sultat" value={formData.resultat} onChange={e => setFormData({...formData, resultat: e.target.value})} className="w-full p-4 border rounded-xl" required />
        <button type="submit" className="w-full bg-orange-600 text-white py-4 rounded-xl font-bold text-lg hover:bg-orange-700">ðŸ©º Ajouter RÃ©sultat</button>
      </form>
    </div>
  );
};

