import React, { useState, useEffect } from "react";
import api from "../api/endpoint";

const PatientDashboard = () => {
  const [user, setUser] = useState(null);
  const [dossiers, setDossiers] = useState([]);
  const [error, setError] = useState(null);

  const fetchUser = async () => {
    try {
      const res = await api.get("/users/me");
      setUser(res.data);
    } catch (err) {
      console.error("Erreur récupération infos patient:", err);
      setError(err.response?.data?.message || "Erreur serveur");
    }
  };

  const fetchDossiers = async () => {
    try {
      const res = await api.get("/patient/dossiers"); // ou /medecin/dossiers selon rôle
      setDossiers(res.data);
    } catch (err) {
      console.error("Erreur récupération dossiers:", err);
      setError(err.response?.data?.message || "Erreur serveur");
    }
  };

  useEffect(() => {
    fetchUser();
    fetchDossiers();
  }, []);

  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Bienvenue {user?.fullName}</h1>
      <h2>Dossiers</h2>
      <ul>
        {dossiers.map((d) => (
          <li key={d._id}>{d.patientName} - {d.motif}</li>
        ))}
      </ul>
    </div>
  );
};

export default PatientDashboard;
