import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MedecinDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDossiers = async () => {
      try {
        setLoading(true);
        setError('');

        const token = localStorage.getItem('token');

        const response = await axios.get(
          'http://localhost:5000/api/medecin/dossiers',
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        setDossiers(response.data);
      } catch (err) {
        console.error('Erreur chargement dossiers :', err);

        if (err.response?.status === 403) {
          setError('‚õî Acc√®s refus√© : r√©serv√© aux m√©decins.');
        } else if (err.response?.status === 401) {
          setError('üîê Session expir√©e. Veuillez vous reconnecter.');
        } else {
          setError('‚ùå Impossible de charger les dossiers.');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchDossiers();
  }, []);

  /* ‚è≥ Chargement */
  if (loading) {
    return (
      <div style={{ padding: '20px' }}>
        <p>‚è≥ Chargement des dossiers m√©dicaux...</p>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>ü©∫ Mes dossiers m√©dicaux</h2>

      {/* ‚ùå Erreur */}
      {error && (
        <div
          style={{
            backgroundColor: '#e3f2fd',
            color: '#0d47a1',
            padding: '15px',
            borderRadius: '8px',
            borderLeft: '5px solid #1976d2',
            marginBottom: '20px',
          }}
        >
          {error}
        </div>
      )}

      {/* üì≠ Aucun dossier */}
      {!error && dossiers.length === 0 && (
        <p>üì≠ Aucun dossier m√©dical trouv√©.</p>
      )}

      {/* üìÇ Liste des dossiers */}
      {!error &&
        dossiers.map((dossier) => (
          <div
            key={dossier._id}
            style={{
              backgroundColor: '#f4f8ff',
              borderRadius: '12px',
              padding: '16px',
              marginBottom: '15px',
              border: '1px solid #cfd8ff',
            }}
          >
            <p>
              <strong>üë§ Patient :</strong>{' '}
              {dossier.patientId?.fullName || '‚Äî'}
            </p>
            <p>
              <strong>ü©∏ Groupe sanguin :</strong>{' '}
              {dossier.groupeSanguin || '‚Äî'}
            </p>
            <p>
              <strong>üìÑ R√©sum√© m√©dical :</strong>{' '}
              {dossier.resumeMedical || '‚Äî'}
            </p>
            <p>
              <strong>üìå Statut :</strong>{' '}
              {dossier.statut}
            </p>
          </div>
        ))}
    </div>
  );
};

export default MedecinDossiers;
