import React, { useState, useEffect } from 'react';
import { Link, useNavigate, Routes, Route, useLocation } from 'react-router-dom';
import { 
  getAuthToken, 
  decodeToken, 
  removeAuthToken 
} from '../api/endpoint';
import {
  FiUsers, 
  FiActivity, 
  FiCalendar, 
  FiDollarSign, 
  FiBarChart, 
  FiBell, 
  FiSettings, 
  FiLogOut, 
  FiMenu, 
  FiX
} from 'react-icons/fi';

// Import des pages admin
import Patients from './admin/Patients';
import Medecins from './admin/Medecins';
import Services from './admin/Services';
import Rdv from './admin/Rdv';
import Finances from './admin/Finances';
import Moderateurs from './admin/Moderateurs';

// Import de recharts pour les graphiques
import {
  LineChart,
  Line,
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from 'recharts';

// Composant Dashboard principal (page d'accueil admin)
const DashboardHome = ({ stats, recentActivity }) => {
  // Données pour graphique de tendance
  const tendanceData = [
    { mois: 'Août', patients: 1100, medecins: 85, rdv: 420 },
    { mois: 'Sept', patients: 1150, medecins: 86, rdv: 430 },
    { mois: 'Oct', patients: 1180, medecins: 87, rdv: 440 },
    { mois: 'Nov', patients: 1200, medecins: 88, rdv: 450 },
    { mois: 'Déc', patients: 1220, medecins: 88, rdv: 455 },
    { mois: 'Jan', patients: 1247, medecins: 89, rdv: 456 },
  ];

  // Données pour graphique de revenus
  const revenusData = [
    { mois: 'Août', revenus: 2100000 },
    { mois: 'Sept', revenus: 2200000 },
    { mois: 'Oct', revenus: 2300000 },
    { mois: 'Nov', revenus: 2350000 },
    { mois: 'Déc', revenus: 2400000 },
    { mois: 'Jan', revenus: 2450000 },
  ];

  return (
    <>
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900">Dashboard Admin</h2>
        <p className="text-gray-600 mt-2">Bienvenue dans le panneau de gestion complet</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-10">
        <div className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Patients</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">{stats.totalPatients}</p>
              <p className="text-sm text-green-600 mt-1 font-medium">+12% ce mois</p>
            </div>
            <FiUsers size={32} className="text-blue-500 group-hover:scale-110 transition-transform" />
          </div>
        </div>

        <div className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Médecins</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">{stats.totalMedecins}</p>
              <p className="text-sm text-green-600 mt-1 font-medium">+3 nouveaux</p>
            </div>
            <FiUsers size={32} className="text-green-500 group-hover:scale-110 transition-transform" />
          </div>
        </div>

        <div className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Rendez-vous</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">{stats.totalRdv}</p>
              <p className="text-sm text-blue-600 mt-1 font-medium">+28 aujourd'hui</p>
            </div>
            <FiCalendar size={32} className="text-purple-500 group-hover:scale-110 transition-transform" />
          </div>
        </div>

        <div className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Revenus</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">{stats.revenue.toLocaleString()} CFA</p>
              <p className="text-sm text-green-600 mt-1 font-medium">+15% ce mois</p>
            </div>
            <FiDollarSign size={32} className="text-yellow-500 group-hover:scale-110 transition-transform" />
          </div>
        </div>
      </div>

      {/* Graphiques de tendance */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-10">
        {/* Graphique Évolution Patients/Médecins/RDV */}
        <div className="bg-white rounded-2xl shadow-lg p-8 border border-gray-100">
          <h3 className="text-2xl font-bold text-gray-900 mb-6">Évolution (6 mois)</h3>
          <ResponsiveContainer width="100%" height={300}>
            <AreaChart data={tendanceData}>
              <defs>
                <linearGradient id="colorPatients" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.8}/>
                  <stop offset="95%" stopColor="#3b82f6" stopOpacity={0}/>
                </linearGradient>
                <linearGradient id="colorMedecins" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#10b981" stopOpacity={0.8}/>
                  <stop offset="95%" stopColor="#10b981" stopOpacity={0}/>
                </linearGradient>
                <linearGradient id="colorRdv" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#8b5cf6" stopOpacity={0.8}/>
                  <stop offset="95%" stopColor="#8b5cf6" stopOpacity={0}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
              <XAxis dataKey="mois" stroke="#6b7280" />
              <YAxis stroke="#6b7280" />
              <Tooltip 
                contentStyle={{ 
                  backgroundColor: '#fff', 
                  border: '1px solid #e5e7eb', 
                  borderRadius: '8px',
                  boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
                }}
              />
              <Legend />
              <Area 
                type="monotone" 
                dataKey="patients" 
                stroke="#3b82f6" 
                fillOpacity={1} 
                fill="url(#colorPatients)"
                name="Patients"
              />
              <Area 
                type="monotone" 
                dataKey="medecins" 
                stroke="#10b981" 
                fillOpacity={1} 
                fill="url(#colorMedecins)"
                name="Médecins"
              />
              <Area 
                type="monotone" 
                dataKey="rdv" 
                stroke="#8b5cf6" 
                fillOpacity={1} 
                fill="url(#colorRdv)"
                name="Rendez-vous"
              />
            </AreaChart>
          </ResponsiveContainer>
        </div>

        {/* Graphique Évolution Revenus */}
        <div className="bg-white rounded-2xl shadow-lg p-8 border border-gray-100">
          <h3 className="text-2xl font-bold text-gray-900 mb-6">Évolution des Revenus</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={revenusData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
              <XAxis dataKey="mois" stroke="#6b7280" />
              <YAxis 
                stroke="#6b7280"
                tickFormatter={(value) => `${(value / 1000000).toFixed(1)}M`}
              />
              <Tooltip 
                formatter={(value) => `${value.toLocaleString()} CFA`}
                contentStyle={{ 
                  backgroundColor: '#fff', 
                  border: '1px solid #e5e7eb', 
                  borderRadius: '8px',
                  boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
                }}
              />
              <Legend />
              <Line 
                type="monotone" 
                dataKey="revenus" 
                stroke="#f59e0b" 
                strokeWidth={3}
                dot={{ fill: '#f59e0b', r: 6 }}
                activeDot={{ r: 8 }}
                name="Revenus (CFA)"
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Recent Activity + Quick Actions */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Recent Activity */}
        <div className="lg:col-span-2 bg-white rounded-2xl shadow-lg p-8 border border-gray-100">
          <h3 className="text-2xl font-bold text-gray-900 mb-6 flex items-center">
            Activité récente
            <span className="ml-2 px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">Live</span>
          </h3>
          <div className="space-y-4">
            {recentActivity.map((activity) => (
              <div key={activity.id} className="flex items-start p-4 bg-gray-50 rounded-xl hover:bg-gray-100 transition-all group">
                <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl flex items-center justify-center mr-4 flex-shrink-0">
                  <span className="text-white font-semibold">✓</span>
                </div>
                <div className="flex-1 min-w-0">
                  <p className="font-medium text-gray-900 truncate">{activity.action}</p>
                  <p className="text-sm text-gray-500 truncate">{activity.user}</p>
                </div>
                <span className="text-xs text-gray-400 ml-4 whitespace-nowrap">{activity.time}</span>
              </div>
            ))}
          </div>
        </div>

        {/* Quick Actions */}
        <div className="bg-white rounded-2xl shadow-lg p-8 border border-gray-100">
          <h3 className="text-2xl font-bold text-gray-900 mb-6">Actions rapides</h3>
          <div className="space-y-3">
            <Link to="/admin/patients" className="block w-full bg-gradient-to-r from-blue-500 to-blue-600 text-white p-4 rounded-xl hover:shadow-lg hover:scale-105 hover:from-blue-600 hover:to-blue-700 transition-all text-center font-medium">
              ➕ Nouvel utilisateur
            </Link>
            <Link to="/admin/services" className="block w-full border-2 border-blue-100 bg-blue-50 text-blue-700 p-4 rounded-xl hover:bg-blue-100 hover:border-blue-200 hover:shadow-md transition-all text-center font-medium">
              ⚙️ Gérer services
            </Link>
            <Link to="/admin/rdv" className="block w-full border-2 border-green-100 bg-green-50 text-green-700 p-4 rounded-xl hover:bg-green-100 hover:border-green-200 hover:shadow-md transition-all text-center font-medium">
              📅 Tous les RDV
            </Link>
          </div>
        </div>
      </div>
    </>
  );
};

// Composant Layout Admin qui accepte des children
export const AdminLayoutWrapper = ({ children }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = getAuthToken();
    if (!token) {
      navigate('/login');
      return;
    }

    const decoded = decodeToken(token);
    if (decoded?.role !== 'Admin') {
      navigate('/dashboard');
      return;
    }
    
    setUser(decoded);
  }, [navigate]);

  const logout = () => {
    removeAuthToken();
    navigate('/login');
  };

  const navigation = [
    { name: 'Dashboard', icon: FiBarChart, href: '/admin', exact: true },
    { name: 'Patients', icon: FiUsers, href: '/admin/patients' },
    { name: 'Médecins', icon: FiUsers, href: '/admin/medecins' },
    { name: 'Rendez-vous', icon: FiCalendar, href: '/admin/rdv' },
    { name: 'Services', icon: FiActivity, href: '/admin/services' },
    { name: 'Finances', icon: FiDollarSign, href: '/admin/finances' },
    { name: 'Modérateurs', icon: FiUsers, href: '/admin/moderateurs' },
    { name: 'Paramètres', icon: FiSettings, href: '/admin/settings' }
  ];

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Mobile Sidebar Overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 z-40 lg:hidden" 
          onClick={() => setSidebarOpen(false)}
        >
          <div className="absolute inset-0 bg-black bg-opacity-25" />
        </div>
      )}
      
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-64 bg-white shadow-lg transform ${
        sidebarOpen ? 'translate-x-0' : '-translate-x-full'
      } lg:translate-x-0 lg:static lg:inset-0 transition-transform duration-200 ease-in-out`}>
        <div className="flex items-center justify-between h-16 px-6 border-b border-gray-200">
          <h1 className="text-xl font-bold text-gray-900">Admin Panel</h1>
          <button 
            onClick={() => setSidebarOpen(false)} 
            className="lg:hidden p-2 rounded-md text-gray-400 hover:text-gray-500"
          >
            <FiX size={20} />
          </button>
        </div>
        
        <nav className="mt-8 px-4 space-y-2">
          {navigation.map((item) => {
            const isActive = item.exact 
              ? location.pathname === item.href 
              : location.pathname.startsWith(item.href);
            return (
              <Link
                key={item.name}
                to={item.href}
                className={`flex items-center px-4 py-3 rounded-lg transition-all group ${
                  isActive 
                    ? 'bg-blue-100 text-blue-700 font-semibold' 
                    : 'text-gray-700 hover:bg-gray-100 hover:text-blue-600'
                }`}
              >
                <item.icon size={20} className="mr-3 flex-shrink-0" />
                <span>{item.name}</span>
              </Link>
            );
          })}
        </nav>

        <div className="absolute bottom-6 left-6 w-56">
          <button
            onClick={logout}
            className="flex items-center w-full px-4 py-3 text-red-600 hover:bg-red-50 rounded-lg transition-all group"
          >
            <FiLogOut size={20} className="mr-3 group-hover:-translate-x-1 transition-transform" />
            Déconnexion
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="lg:ml-64">
        {/* Topbar */}
        <header className="bg-white shadow-sm border-b border-gray-200">
          <div className="flex items-center justify-between h-16 px-6">
            <button
              className="lg:hidden p-2 rounded-md text-gray-400 hover:text-gray-500"
              onClick={() => setSidebarOpen(true)}
            >
              <FiMenu size={20} />
            </button>
            
            <div className="flex items-center space-x-4">
              <button className="p-2 text-gray-400 hover:text-gray-500 rounded-lg relative">
                <FiBell size={20} />
                <span className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full"></span>
              </button>
              
              <div className="flex items-center space-x-3">
                <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full flex items-center justify-center">
                  <span className="text-white font-semibold text-sm">
                    {user?.email?.[0]?.toUpperCase() || 'A'}
                  </span>
                </div>
                <span className="font-medium text-gray-700 hidden md:block">
                  {user?.email || 'Admin'}
                </span>
              </div>
            </div>
          </div>
        </header>

        {/* Content avec children */}
        <main style={{ padding: '20px', minHeight: 'calc(100vh - 64px)' }}>
          {children}
        </main>
      </div>
    </div>
  );
};

// Layout principal AdminDashboard avec sidebar (version avec Routes internes)
const AdminDashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState(null);
  const [stats] = useState({
    totalPatients: 1247,
    totalMedecins: 89,
    totalRdv: 456,
    revenue: 2450000
  });
  const [recentActivity, setRecentActivity] = useState([]);

  // ✅ useEffect CORRIGÉ - DANS le composant
  useEffect(() => {
    const token = getAuthToken();
    if (!token) {
      navigate('/login');
      return;
    }

    const decoded = decodeToken(token);
    if (decoded?.role !== 'Admin') {
      navigate('/dashboard');
      return;
    }
    
    setUser(decoded);
    loadAdminData();
  }, [navigate]);

  const loadAdminData = async () => {
    setRecentActivity([
      { id: 1, action: 'Nouveau patient inscrit', user: 'Mamadou Diop', time: '2min' },
      { id: 2, action: 'Médecin ajouté', user: 'Dr. Awa Ndiaye', time: '15min' },
      { id: 3, action: 'RDV confirmé', user: 'Service Pédiatrie', time: '1h' }
    ]);
  };

  const logout = () => {
    removeAuthToken();
    navigate('/login');
  };

  const navigation = [
    { name: 'Dashboard', icon: FiBarChart, href: '/admin', exact: true },
    { name: 'Patients', icon: FiUsers, href: '/admin/patients' },
    { name: 'Médecins', icon: FiUsers, href: '/admin/medecins' },
    { name: 'Rendez-vous', icon: FiCalendar, href: '/admin/rdv' },
    { name: 'Services', icon: FiActivity, href: '/admin/services' },
    { name: 'Finances', icon: FiDollarSign, href: '/admin/finances' },
    { name: 'Modérateurs', icon: FiUsers, href: '/admin/moderateurs' },
    { name: 'Paramètres', icon: FiSettings, href: '/admin/settings' }
  ];

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Mobile Sidebar Overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 z-40 lg:hidden" 
          onClick={() => setSidebarOpen(false)}
        >
          <div className="absolute inset-0 bg-black bg-opacity-25" />
        </div>
      )}
      
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-64 bg-white shadow-lg transform ${
        sidebarOpen ? 'translate-x-0' : '-translate-x-full'
      } lg:translate-x-0 lg:static lg:inset-0 transition-transform duration-200 ease-in-out`}>
        <div className="flex items-center justify-between h-16 px-6 border-b border-gray-200">
          <h1 className="text-xl font-bold text-gray-900">Admin Panel</h1>
          <button 
            onClick={() => setSidebarOpen(false)} 
            className="lg:hidden p-2 rounded-md text-gray-400 hover:text-gray-500"
          >
            <FiX size={20} />
          </button>
        </div>
        
        <nav className="mt-8 px-4 space-y-2">
          {navigation.map((item) => {
            const isActive = item.exact 
              ? location.pathname === item.href 
              : location.pathname.startsWith(item.href);
            return (
              <Link
                key={item.name}
                to={item.href}
                className={`flex items-center px-4 py-3 rounded-lg transition-all group ${
                  isActive 
                    ? 'bg-blue-100 text-blue-700 font-semibold' 
                    : 'text-gray-700 hover:bg-gray-100 hover:text-blue-600'
                }`}
              >
                <item.icon size={20} className="mr-3 flex-shrink-0" />
                <span>{item.name}</span>
              </Link>
            );
          })}
        </nav>

        <div className="absolute bottom-6 left-6 w-56">
          <button
            onClick={logout}
            className="flex items-center w-full px-4 py-3 text-red-600 hover:bg-red-50 rounded-lg transition-all group"
          >
            <FiLogOut size={20} className="mr-3 group-hover:-translate-x-1 transition-transform" />
            Déconnexion
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="lg:ml-64">
        {/* Topbar */}
        <header className="bg-white shadow-sm border-b border-gray-200">
          <div className="flex items-center justify-between h-16 px-6">
            <button
              className="lg:hidden p-2 rounded-md text-gray-400 hover:text-gray-500"
              onClick={() => setSidebarOpen(true)}
            >
              <FiMenu size={20} />
            </button>
            
            <div className="flex items-center space-x-4">
              <button className="p-2 text-gray-400 hover:text-gray-500 rounded-lg relative">
                <FiBell size={20} />
                <span className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full"></span>
              </button>
              
              <div className="flex items-center space-x-3">
                <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full flex items-center justify-center">
                  <span className="text-white font-semibold text-sm">
                    {user?.email?.[0]?.toUpperCase() || 'A'}
                  </span>
                </div>
                <span className="font-medium text-gray-700 hidden md:block">
                  {user?.email || 'Admin'}
                </span>
              </div>
            </div>
          </div>
        </header>

        {/* Content avec Routes */}
        <main style={{ padding: '20px', minHeight: 'calc(100vh - 64px)' }}>
          <Routes>
            <Route path="/" element={<DashboardHome stats={stats} recentActivity={recentActivity} />} />
            <Route path="/patients" element={<Patients />} />
            <Route path="/medecins" element={<Medecins />} />
            <Route path="/rdv" element={<Rdv />} />
            <Route path="/services" element={<Services />} />
            <Route path="/finances" element={<Finances />} />
            <Route path="/moderateurs" element={<Moderateurs />} />
            <Route path="*" element={<DashboardHome stats={stats} recentActivity={recentActivity} />} />
          </Routes>
        </main>
      </div>
    </div>
  );
};

// Export du layout pour utilisation dans App.js
export const AdminLayout = AdminDashboard;
export { DashboardHome };
export default AdminDashboard;
