import React, { useState, useEffect } from 'react';
import { postData, getData } from '../api/endpoint';

const CreateDossier = () => {
  const [formData, setFormData] = useState({
    patientId: '',
    motif: '',
    description: ''
  });
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingPatients, setLoadingPatients] = useState(true);
  const [error, setError] = useState('');
  const [selectedPatient, setSelectedPatient] = useState(null);

  // ‚úÖ CHARGEMENT AUTO DES PATIENTS R√âELS
  useEffect(() => {
    const fetchPatients = async () => {
      try {
        setLoadingPatients(true);
        const patientsData = await getData('/patients');
        console.log('üë• Patients charg√©s:', patientsData);
        setPatients(patientsData);
      } catch (error) {
        console.error('‚ùå Patients:', error);
        setError('Erreur chargement patients: ' + error.message);
      } finally {
        setLoadingPatients(false);
      }
    };
    fetchPatients();
  }, []);

  // ‚úÖ D√âTAIL PATIENT S√âLECTIONN√â
  useEffect(() => {
    const patient = patients.find(p => p._id === formData.patientId);
    setSelectedPatient(patient);
  }, [formData.patientId, patients]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.patientId || !formData.motif) {
      setError('‚ùå S√©lectionnez un patient et ajoutez un motif');
      return;
    }

    try {
      setLoading(true);
      console.log('üì§ Envoi VRAI:', formData);
      
      const result = await postData('/dossiers', formData);
      console.log('‚úÖ R√©ponse backend:', result);
      
      alert('‚úÖ Dossier cr√©√© avec succ√®s !');
      
      // Reset PARTIEL - garde patient s√©lectionn√©
      setFormData(prev => ({
        ...prev,
        motif: '',
        description: ''
      }));
    } catch (error) {
      console.error('‚ùå Erreur compl√®te:', error);
      setError(error.message || 'Erreur cr√©ation dossier');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '700px', margin: '0 auto' }}>
      <h2>üìÅ Nouveau dossier m√©dical</h2>

      {error && (
        <div style={{
          backgroundColor: '#fee',
          color: '#c33',
          padding: '15px',
          borderRadius: '8px',
          marginBottom: '20px',
          borderLeft: '4px solid #c33',
          fontWeight: '500'
        }}>
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {/* ‚úÖ PATIENT S√âLECTION + AFFICHAGE R√âEL */}
        <div style={{ marginBottom: '20px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold', fontSize: '16px' }}>
            üë§ Patient <span style={{ color: 'red' }}>*</span>
          </label>
          
          {loadingPatients ? (
            <div style={{ padding: '20px', textAlign: 'center', color: '#666' }}>
              ‚è≥ Chargement patients...
            </div>
          ) : patients.length === 0 ? (
            <div style={{ padding: '20px', color: '#c33', textAlign: 'center' }}>
              ‚ùå Aucun patient trouv√© dans la base
            </div>
          ) : (
            <>
              <select
                name="patientId"
                value={formData.patientId}
                onChange={handleChange}
                required
                style={{
                  width: '100%',
                  padding: '15px',
                  border: `2px solid ${formData.patientId ? '#28a745' : '#ddd'}`,
                  borderRadius: '8px',
                  fontSize: '16px',
                  backgroundColor: 'white',
                  transition: 'border-color 0.2s'
                }}
              >
                <option value="">üîç S√©lectionnez un patient...</option>
                {patients.map((patient) => (
                  <option key={patient._id} value={patient._id}>
                    {patient.nom?.toUpperCase()} {patient.prenom} 
                    {patient.telephone && ` ‚Ä¢ üìû ${patient.telephone}`}
                    {patient.email && ` ‚Ä¢ ‚úâÔ∏è ${patient.email}`}
                  </option>
                ))}
              </select>

              {/* ‚úÖ INFO PATIENT S√âLECTIONN√â */}
              {selectedPatient && (
                <div style={{
                  marginTop: '10px',
                  padding: '12px',
                  backgroundColor: '#e8f5e8',
                  borderRadius: '6px',
                  borderLeft: '4px solid #28a745',
                  fontSize: '14px'
                }}>
                  ‚úÖ S√©lectionn√©: <strong>{selectedPatient.nom} {selectedPatient.prenom}</strong>
                  {selectedPatient.telephone && ` ‚Ä¢ ${selectedPatient.telephone}`}
                </div>
              )}
            </>
          )}
        </div>

        {/* MOTIF */}
        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            üìã Motif de consultation <span style={{ color: 'red' }}>*</span>
          </label>
          <input
            type="text"
            name="motif"
            placeholder="Ex: Consultation annuelle, Douleurs abdominales, Suivi diab√®te..."
            value={formData.motif}
            onChange={handleChange}
            required
            style={{
              width: '100%',
              padding: '15px',
              border: `1px solid ${formData.motif ? '#28a745' : '#ddd'}`,
              borderRadius: '8px',
              fontSize: '16px',
              transition: 'border-color 0.2s'
            }}
          />
        </div>

        {/* DESCRIPTION */}
        <div style={{ marginBottom: '25px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>
            üìù Description / Observations
          </label>
          <textarea
            name="description"
            placeholder="Sympt√¥mes d√©taill√©s, ant√©c√©dents, diagnostic pr√©liminaire, prescription..."
            value={formData.description}
            onChange={handleChange}
            rows={6}
            style={{
              width: '100%',
              padding: '15px',
              border: '1px solid #ddd',
              borderRadius: '8px',
              fontSize: '16px',
              fontFamily: 'inherit',
              resize: 'vertical',
              lineHeight: '1.5'
            }}
          />
        </div>

        {/* ‚úÖ BOUTON INTELLIGENT */}
        <button 
          type="submit" 
          disabled={loading || !formData.patientId || !formData.motif}
          style={{
            width: '100%',
            padding: '18px',
            backgroundColor: loading 
              ? '#ccc' 
              : (!formData.patientId || !formData.motif 
                  ? '#6c757d' 
                  : '#28a745'),
            color: 'white',
            border: 'none',
            borderRadius: '10px',
            fontSize: '18px',
            fontWeight: 'bold',
            cursor: loading ? 'not-allowed' : 'pointer',
            transition: 'all 0.2s'
          }}
        >
          {loading ? '‚è≥ Cr√©ation en cours...' : 
           !formData.patientId ? 'üë§ S√©lectionnez un patient' :
           !formData.motif ? 'üìã Ajoutez un motif' : 
           '‚úÖ Cr√©er le dossier m√©dical'}
        </button>
      </form>

      {/* üîß DEBUG R√âEL */}
      {process.env.NODE_ENV === 'development' && (
        <details style={{ marginTop: '25px', padding: '15px', background: '#f8f9fa', borderRadius: '8px' }}>
          <summary style={{ cursor: 'pointer', fontWeight: 'bold' }}>üîç Debug Infos R√©elles</summary>
          <pre style={{ fontSize: '13px', maxHeight: '200px', overflow: 'auto' }}>
{JSON.stringify({ 
  patientsCount: patients.length, 
  selectedPatientId: formData.patientId,
  selectedPatient: selectedPatient,
  formData 
}, null, 2)}
          </pre>
        </details>
      )}
    </div>
  );
};

export default CreateDossier;
