// src/patient/PatientDashboard.js
import React, { useState, useEffect } from 'react';
import { getAuthToken, decodeToken, getData } from '../api/endpoint';
import './PatientDashboard.css';
const PatientDashboard = () => {
  const [dossiers, setDossiers] = useState([]);
  const [rendezvous, setRendezvous] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Récupération de l'id patient depuis le token
  const token = getAuthToken();
  const decoded = decodeToken(token);
  const patientId = decoded?._id || decoded?.id; // selon ton payload

  const loadPatientData = async () => {
    if (!patientId) {
      setError('Utilisateur non identifié. Connectez-vous.');
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      // Dossiers médicaux
      const dossiersData = await getData(`/mes-dossiers`); 
      setDossiers(dossiersData);

      // Rendez-vous
      const rendezvousData = await getData(`/rendezvous?patientId=${patientId}`);
      setRendezvous(rendezvousData);

      setError('');
    } catch (err) {
      console.error("Erreur chargement patient :", err.message);
      setError(err.message || 'Erreur serveur');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPatientData();
  }, []);

  if (loading) return <div className="p-4">Chargement...</div>;
  if (error) return <div className="p-4 text-red-600">Erreur: {error}</div>;

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Bienvenue sur votre tableau de bord</h1>

      <section className="mb-6">
        <h2 className="text-xl font-semibold mb-2">Mes Dossiers Médicaux</h2>
        {dossiers.length === 0 ? (
          <p>Aucun dossier disponible</p>
        ) : (
          <table className="w-full border border-gray-300">
            <thead>
              <tr className="bg-gray-200">
                <th className="border px-2 py-1">Date</th>
                <th className="border px-2 py-1">Médecin</th>
                <th className="border px-2 py-1">Description</th>
              </tr>
            </thead>
            <tbody>
              {dossiers.map(d => (
                <tr key={d._id}>
                  <td className="border px-2 py-1">{new Date(d.createdAt).toLocaleDateString()}</td>
                  <td className="border px-2 py-1">{d.medecin?.fullName || 'Non spécifié'}</td>
                  <td className="border px-2 py-1">{d.description || 'Aucune description'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      <section>
        <h2 className="text-xl font-semibold mb-2">Mes Rendez-Vous</h2>
        {rendezvous.length === 0 ? (
          <p>Aucun rendez-vous prévu</p>
        ) : (
          <table className="w-full border border-gray-300">
            <thead>
              <tr className="bg-gray-200">
                <th className="border px-2 py-1">Date</th>
                <th className="border px-2 py-1">Heure</th>
                <th className="border px-2 py-1">Médecin</th>
                <th className="border px-2 py-1">Statut</th>
              </tr>
            </thead>
            <tbody>
              {rendezvous.map(r => (
                <tr key={r._id}>
                  <td className="border px-2 py-1">{new Date(r.date).toLocaleDateString()}</td>
                  <td className="border px-2 py-1">{r.heure || 'Non précisée'}</td>
                  <td className="border px-2 py-1">{r.medecin?.fullName || 'Non spécifié'}</td>
                  <td className="border px-2 py-1">{r.statut || 'En attente'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      <button
        className="mt-4 px-4 py-2 bg-blue-600 text-white rounded"
        onClick={loadPatientData}
      >
        Rafraîchir
      </button>
    </div>
  );
};

export default PatientDashboard;
