import React, { useState, useEffect } from 'react';
import api from '../../api/endpoint';
import { FiHeart, FiCalendar, FiUser, FiThermometer, FiAlertCircle } from 'react-icons/fi';

const MesConsultations = () => {
  const [consultations, setConsultations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadConsultations();
  }, []);

  const loadConsultations = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await api.getData('/consultations/patient');
      setConsultations(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Erreur consultations:', err);
      setError(err.message || 'Impossible de charger les consultations.');
      setConsultations([]);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (statut) => {
    switch (statut) {
      case 'TERMINEE': return 'bg-green-100 text-green-800';
      case 'EN_COURS': return 'bg-yellow-100 text-yellow-800';
      case 'ANNULEE': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
          <p>Chargement des consultations...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Mes Consultations</h1>
        <p className="text-gray-600">Suivez l'historique de vos consultations médicales</p>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-800 px-6 py-4 rounded-xl mb-6">
          <div className="flex items-center">
            <FiAlertCircle className="h-5 w-5 mr-2" />
            {error}
          </div>
        </div>
      )}

      {consultations.length === 0 ? (
        <div className="text-center py-20 bg-white rounded-xl shadow-sm border border-gray-100">
          <FiCalendar className="h-16 w-16 text-gray-300 mx-auto mb-4" />
          <h3 className="text-xl font-semibold text-gray-900 mb-2">Aucune consultation</h3>
          <button onClick={loadConsultations} className="bg-blue-600 text-white px-6 py-2 rounded-xl hover:bg-blue-700">
            Actualiser
          </button>
        </div>
      ) : (
        <div className="grid gap-4">
          {consultations.map((consultation) => (
            <div key={consultation.id || Math.random()} className="bg-white p-6 rounded-xl shadow-sm border hover:shadow-md">
              <div className="flex justify-between items-start mb-4">
                <span className={`px-3 py-1 rounded-full text-xs font-medium ${getStatusColor(consultation.statut)}`}>
                  {consultation.statut || 'EN_COURS'}
                </span>
              </div>
              <h3 className="text-lg font-semibold">{consultation.medecin || 'Consultation'}</h3>
              <p className="text-gray-600 mt-2">{consultation.notes || 'Détails non disponibles'}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MesConsultations;
