import React, { useState } from 'react';

const MedecinDashboard = () => {
  const [activeTab, setActiveTab] = useState('ordonnances');
  const [ordonnance, setOrdonnance] = useState({
    patient: '',
    medicaments: [
      { nom: 'Parac√©tamol 1g', dose: '1cp', frequence: '3x/jour', duree: '5 jours' },
      { nom: 'Amoxicilline 500mg', dose: '1cp', frequence: '2x/jour', duree: '7 jours' }
    ]
  });
  const [patients] = useState([
    { id: 1, name: 'Diop Aminata', age: 32 },
    { id: 2, name: 'Ndiaye Fatou', age: 45 },
    { id: 3, name: 'Sow Mamadou', age: 28 }
  ]);

  // ‚úÖ TOUTES LES FONCTIONS
  const addMedicament = () => {
    setOrdonnance({
      ...ordonnance,
      medicaments: [...ordonnance.medicaments, { nom: '', dose: '', frequence: '', duree: '' }]
    });
  };

  const updateMedicament = (index, field, value) => {
    const newMeds = [...ordonnance.medicaments];
    newMeds[index][field] = value;
    setOrdonnance({ ...ordonnance, medicaments: newMeds });
  };

  const deleteMedicament = (index) => {
    const newMeds = ordonnance.medicaments.filter((_, i) => i !== index);
    setOrdonnance({ ...ordonnance, medicaments: newMeds });
  };

  const saveOrdonnance = () => {
    if (!ordonnance.patient) {
      alert('‚ùå S√©lectionnez un patient d\'abord !');
      return;
    }
    alert('‚úÖ ORDONNANCE TERMIN√âE !\n\n' + 
      `PATIENT: ${ordonnance.patient}\n\n` +
      ordonnance.medicaments.map((m, i) => 
        `${i+1}. ${m.nom} | ${m.dose} | ${m.frequence} | ${m.duree}`
      ).join('\n')
    );
  };

  const stats = { patients: 156, ordonnances: 47, rdvsToday: 8, revenus: 1250000 };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #0f766e 0%, #059669 50%, #047857 100%)',
      padding: '20px',
      fontFamily: '-apple-system, BlinkMacSystemFont, sans-serif'
    }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        
        {/* HEADER - STYLE AM√âLIOR√â */}
        <HeaderSection />
        
        {/* NAVIGATION - STYLE AM√âLIOR√â */}
        <NavigationTabs activeTab={activeTab} setActiveTab={setActiveTab} stats={stats} />
        
        {/* STATS - STYLE AM√âLIOR√â */}
        <StatsGrid stats={stats} />

        {/* CONTENU PRINCIPAL */}
        {activeTab === 'ordonnances' && (
          <OrdonnancesSection 
            ordonnance={ordonnance}
            patients={patients}
            addMedicament={addMedicament}
            updateMedicament={updateMedicament}
            deleteMedicament={deleteMedicament}
            saveOrdonnance={saveOrdonnance}
          />
        )}
      </div>
    </div>
  );
};

// ‚úÖ HEADER
const HeaderSection = () => (
  <div style={{
    background: 'rgba(255,255,255,0.12)',
    backdropFilter: 'blur(20px)',
    borderRadius: '28px',
    boxShadow: '0 32px 64px rgba(0,0,0,0.25)',
    border: '3px solid rgba(255,255,255,0.2)',
    padding: '40px',
    marginBottom: '32px'
  }}>
    <div style={{ display: 'flex', flexDirection: 'column', gap: '24px' }}>
      <h1 style={{
        fontSize: '52px',
        fontWeight: '900',
        background: 'linear-gradient(135deg, #ffffff 0%, #ecfdf5 100%)',
        WebkitBackgroundClip: 'text',
        WebkitTextFillColor: 'transparent',
        margin: 0,
        lineHeight: '1.1'
      }}>
        ü©∫ CABINET M√âDICAL - GOVAIMED
      </h1>
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        gap: '16px',
        fontSize: '22px',
        color: '#f0fdf4',
        background: 'rgba(255,255,255,0.15)',
        padding: '28px',
        borderRadius: '20px'
      }}>
        <div style={{ display: 'flex', gap: '28px', flexWrap: 'wrap' }}>
          <span>üë®‚Äç‚öïÔ∏è Dr Ndiaye Mamadou</span>
          <span>üè• Clinique Dakar Plateau</span>
        </div>
        <div style={{ display: 'flex', gap: '28px', flexWrap: 'wrap', alignItems: 'center' }}>
          <span>üìû +221 77 123 4567</span>
          <span>ü©∫ M√©decine G√©n√©rale</span>
          <span style={{ 
            padding: '12px 24px', 
            background: 'rgba(34, 197, 94, 0.3)', 
            color: 'white', 
            borderRadius: '16px',
            fontWeight: '800'
          }}>‚óè En ligne</span>
        </div>
      </div>
      <div style={{ textAlign: 'right' }}>
        <div style={{ 
          fontSize: '42px', 
          fontWeight: '900', 
          background: 'linear-gradient(135deg, #22c55e, #16a34a)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent'
        }}>
          1 250 000 FCFA
        </div>
        <div style={{ fontSize: '20px', color: '#d1fae5' }}>Consultations f√©vrier</div>
      </div>
    </div>
  </div>
);

// ‚úÖ NAVIGATION
const NavigationTabs = ({ activeTab, setActiveTab, stats }) => (
  <div style={{
    background: 'rgba(255,255,255,0.12)',
    backdropFilter: 'blur(20px)',
    borderRadius: '28px',
    boxShadow: '0 20px 40px rgba(0,0,0,0.2)',
    border: '3px solid rgba(255,255,255,0.2)',
    padding: '24px',
    marginBottom: '32px',
    display: 'flex',
    gap: '16px',
    flexWrap: 'wrap'
  }}>
    <TabButton 
      label="üíä Ordonnances" 
      active={activeTab === 'ordonnances'}
      onClick={() => setActiveTab('ordonnances')}
    />
    <TabButton 
      label={`üë• Patients (${stats.patients})`} 
      active={activeTab === 'patients'}
      onClick={() => setActiveTab('patients')}
    />
    <TabButton label="üìÖ RDV" active={activeTab === 'rdv'} onClick={() => setActiveTab('rdv')} />
    <TabButton label="üìä Stats" active={activeTab === 'stats'} onClick={() => setActiveTab('stats')} />
  </div>
);

// ‚úÖ STATS
const StatsGrid = ({ stats }) => (
  <div style={{
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))',
    gap: '28px',
    marginBottom: '40px'
  }}>
    <StatBox title="üë• Patients actifs" value={stats.patients.toString()} color="#ecfdf5" textColor="#166534" />
    <StatBox title="üíä Ordonnances" value={stats.ordonnances.toString()} color="#dbeafe" textColor="#1e40af" />
    <StatBox title="üìÖ RDV aujourd'hui" value={stats.rdvsToday.toString()} color="#fef3c7" textColor="#92400e" />
    <StatBox title="üí∞ Revenus" value={stats.revenus.toLocaleString()} color="#d1fae5" textColor="#065f46" />
  </div>
);

// ‚úÖ ORDONNANCES - TOUS BOUTONS FONCTIONNELS
const OrdonnancesSection = ({ ordonnance, patients, addMedicament, updateMedicament, deleteMedicament, saveOrdonnance }) => (
  <div style={{
    background: 'rgba(255,255,255,0.12)',
    backdropFilter: 'blur(20px)',
    borderRadius: '28px',
    boxShadow: '0 32px 64px rgba(0,0,0,0.25)',
    border: '3px solid rgba(255,255,255,0.2)',
    padding: '40px'
  }}>
    <div style={{ textAlign: 'center', marginBottom: '40px' }}>
      <h2 style={{ fontSize: '44px', fontWeight: '900', color: 'white', margin: 0, mb: '12px' }}>
        üíä NOUVELLE ORDONNANCE
      </h2>
      <p style={{ fontSize: '24px', color: '#d1fae5' }}>Prescription √©lectronique s√©curis√©e</p>
    </div>

    {/* PATIENT */}
    <div style={{ marginBottom: '32px' }}>
      <label style={{ display: 'block', fontSize: '20px', marginBottom: '16px', color: '#f0fdf4' }}>
        üë§ Patient :
      </label>
      <select 
        style={inputStyleBig}
        onChange={(e) => {/* Simplifi√© pour d√©mo */}}
      >
        <option value="">üîç S√©lectionner un patient...</option>
        {patients.map(p => (
          <option key={p.id}>{p.name} - {p.age} ans</option>
        ))}
      </select>
    </div>

    {/* M√âDICAMENTS */}
    <div style={{ marginBottom: '40px' }}>
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        marginBottom: '24px' 
      }}>
        <h3 style={{ fontSize: '32px', color: 'white', margin: 0 }}>üíä M√©dicaments prescrits</h3>
        <button onClick={addMedicament} style={addBtnStyle}>
          ‚ûï AJOUTER
        </button>
      </div>
      
      <div style={{ display: 'flex', flexDirection: 'column', gap: '16px' }}>
        {ordonnance.medicaments.map((med, index) => (
          <MedicamentRow 
            key={index}
            medicament={med}
            index={index}
            onUpdate={updateMedicament}
            onDelete={() => deleteMedicament(index)}
          />
        ))}
      </div>
    </div>

    {/* ACTIONS - TOUS FONCTIONNELS */}
    <div style={{ 
      display: 'flex', 
      gap: '24px', 
      justifyContent: 'center', 
      flexWrap: 'wrap' 
    }}>
      <button onClick={saveOrdonnance} style={primaryBtnBig}>
        ‚úÖ FINALISER ORDONNANCE
      </button>
      <button onClick={() => alert('üìÑ PDF g√©n√©r√© !')} style={secondaryBtnBig}>
        üìÑ G√âN√âRER PDF
      </button>
      <button onClick={() => alert('üì± SMS envoy√© au patient !')} style={secondaryBtnBig}>
        üì± ENVOYER SMS
      </button>
      <button onClick={() => alert('üñ®Ô∏è Imprim√©e !')} style={secondaryBtnBig}>
        üñ®Ô∏è IMPRIMER
      </button>
    </div>
  </div>
);

// ‚úÖ ROW M√âDICAMENT
const MedicamentRow = ({ medicament, index, onUpdate, onDelete }) => (
  <div style={{
    display: 'grid',
    gridTemplateColumns: '2fr 1fr 1fr 1fr 1fr 56px',
    gap: '20px',
    padding: '24px',
    background: 'rgba(255,255,255,0.08)',
    borderRadius: '20px',
    border: '2px solid rgba(255,255,255,0.1)',
    alignItems: 'center'
  }}>
    <input 
      placeholder="Nom du m√©dicament (ex: Parac√©tamol 1g)"
      value={medicament.nom}
      onChange={(e) => onUpdate(index, 'nom', e.target.value)}
      style={inputStyle}
    />
    <input 
      placeholder="Dose (1cp)" 
      value={medicament.dose}
      onChange={(e) => onUpdate(index, 'dose', e.target.value)}
      style={inputStyle}
    />
    <input 
      placeholder="Fr√©q. (3x/j)" 
      value={medicament.frequence}
      onChange={(e) => onUpdate(index, 'frequence', e.target.value)}
      style={inputStyle}
    />
    <input 
      placeholder="Dur√©e (5j)" 
      value={medicament.duree}
      onChange={(e) => onUpdate(index, 'duree', e.target.value)}
      style={inputStyle}
    />
    <input 
      placeholder="Qt√©" 
      style={inputStyle}
    />
    <button 
      onClick={onDelete}
      style={{
        width: '56px', height: '56px',
        background: '#ef4444', color: 'white',
        border: 'none', borderRadius: '16px',
        fontSize: '20px', fontWeight: '900',
        cursor: 'pointer',
        boxShadow: '0 8px 24px rgba(239,68,68,0.4)'
      }}
    >
      üóëÔ∏è
    </button>
  </div>
);

// ‚úÖ COMPOSANTS UTILITAIRES
const TabButton = ({ label, active, onClick }) => (
  <div 
    style={{
      padding: '24px 36px',
      fontSize: '22px',
      fontWeight: '900',
      borderRadius: '24px',
      cursor: 'pointer',
      background: active ? 'rgba(255,255,255,0.25)' : 'rgba(255,255,255,0.08)',
      color: 'white',
      boxShadow: active ? '0 12px 32px rgba(255,255,255,0.25)' : '0 4px 16px rgba(0,0,0,0.2)',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      whiteSpace: 'nowrap',
      border: active ? '3px solid rgba(255,255,255,0.4)' : 'none'
    }} 
    onClick={onClick}
  >
    {label}
  </div>
);

const StatBox = ({ title, value, color, textColor }) => (
  <div style={{
    padding: '36px',
    background: color,
    borderRadius: '28px',
    border: '4px solid rgba(255,255,255,0.3)',
    cursor: 'pointer',
    boxShadow: '0 20px 40px rgba(0,0,0,0.2)',
    transition: 'all 0.3s ease'
  }}
  onMouseEnter={(e) => {
    e.currentTarget.style.transform = 'translateY(-12px)';
    e.currentTarget.style.boxShadow = '0 32px 64px rgba(0,0,0,0.3)';
  }}
  onMouseLeave={(e) => {
    e.currentTarget.style.transform = 'translateY(0)';
    e.currentTarget.style.boxShadow = '0 20px 40px rgba(0,0,0,0.2)';
  }}>
    <h3 style={{ fontSize: '20px', fontWeight: '700', color: textColor, margin: '0 0 20px 0', display: 'flex', alignItems: 'center', gap: '12px' }}>
      {title}
    </h3>
    <p style={{ fontSize: '52px', fontWeight: '900', color: textColor, margin: 0 }}>{value}</p>
  </div>
);

// ‚úÖ STYLES
const inputStyle = {
  padding: '20px 24px',
  fontSize: '18px',
  border: '2px solid rgba(255,255,255,0.3)',
  borderRadius: '16px',
  background: 'rgba(255,255,255,0.95)',
  color: '#1f2937',
  outline: 'none',
  width: '100%',
  fontWeight: '600',
  transition: 'all 0.2s ease'
};

const inputStyleBig = {
  ...inputStyle,
  padding: '24px 28px',
  fontSize: '20px',
  borderRadius: '20px'
};

const primaryBtnBig = {
  padding: '24px 48px',
  background: 'linear-gradient(135deg, #22c55e, #16a34a)',
  color: 'white',
  fontSize: '22px',
  fontWeight: '900',
  borderRadius: '28px',
  border: 'none',
  boxShadow: '0 16px 40px rgba(34,197,94,0.4)',
  cursor: 'pointer',
  transition: 'all 0.3s ease'
};

const secondaryBtnBig = {
  padding: '24px 36px',
  background: 'rgba(255,255,255,0.2)',
  color: 'white',
  fontSize: '20px',
  fontWeight: '800',
  borderRadius: '24px',
  border: '2px solid rgba(255,255,255,0.3)',
  cursor: 'pointer',
  backdropFilter: 'blur(10px)',
  transition: 'all 0.3s ease'
};

const addBtnStyle = {
  padding: '16px 32px',
  background: 'rgba(255,255,255,0.95)',
  color: '#1f2937',
  fontSize: '18px',
  fontWeight: '800',
  borderRadius: '20px',
  border: 'none',
  cursor: 'pointer',
  boxShadow: '0 12px 32px rgba(255,255,255,0.3)'
};

export default MedecinDashboard;
