import React, { useState, useEffect } from "react";
import { axiosInstance } from "../api/endpoint";

const CreateDossier = () => {
  const [patients, setPatients] = useState([]);
  const [formData, setFormData] = useState({
    patientId: "",
    resumeMedical: "",
    groupeSanguin: "",
    antecedents: "",
    allergies: "",
    contactUrgence: { nom: "", telephone: "", lien: "" }
  });

  // Charger la liste des patients
  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const response = await axiosInstance.get("/patients");
        setPatients(response.data);
      } catch (err) {
        console.error("Erreur récupération patients:", err);
        alert("Impossible de charger les patients. Vérifiez que vous êtes connecté en tant que médecin ou admin.");
      }
    };
    fetchPatients();
  }, []);

  // Gestion des champs du formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith("contact")) {
      const key = name.split(".")[1];
      setFormData({
        ...formData,
        contactUrgence: { ...formData.contactUrgence, [key]: value }
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  // Soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.patientId) {
      alert("⚠️ Veuillez sélectionner un patient !");
      return;
    }

    try {
      const payload = {
        ...formData,
        antecedents: formData.antecedents
          .split(",")
          .map(a => ({ libelle: a.trim() }))
          .filter(Boolean),
        allergies: formData.allergies
          .split(",")
          .map(a => ({ libelle: a.trim() }))
          .filter(Boolean)
      };

      await axiosInstance.post("/dossiers", payload);

      alert("✅ Dossier créé avec succès !");
      setFormData({
        patientId: "",
        resumeMedical: "",
        groupeSanguin: "",
        antecedents: "",
        allergies: "",
        contactUrgence: { nom: "", telephone: "", lien: "" }
      });

    } catch (err) {
      console.error("Erreur création dossier:", err);
      alert(err.response?.data?.message || "❌ Erreur serveur");
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "0 auto" }}>
      <h2>Créer un Dossier Patient</h2>
      <form onSubmit={handleSubmit}>
        {/* Sélection du patient */}
        <label>Patient</label><br/>
        <select name="patientId" value={formData.patientId} onChange={handleChange} required>
          <option value="">-- Sélectionner un patient --</option>
          {patients.map((p) => (
            <option key={p._id} value={p._id}>
              {p.fullName} ({p.email})
            </option>
          ))}
        </select>
        <br/><br/>

        {/* Résumé médical */}
        <textarea
          name="resumeMedical"
          placeholder="Résumé Médical"
          value={formData.resumeMedical}
          onChange={handleChange}
          rows={4}
          style={{ width: "100%" }}
        /><br/><br/>

        {/* Groupe sanguin */}
        <input
          name="groupeSanguin"
          placeholder="Groupe Sanguin (ex: A+)"
          value={formData.groupeSanguin}
          onChange={handleChange}
        /><br/><br/>

        {/* Antécédents */}
        <input
          name="antecedents"
          placeholder="Antécédents (séparés par ,)"
          value={formData.antecedents}
          onChange={handleChange}
        /><br/><br/>

        {/* Allergies */}
        <input
          name="allergies"
          placeholder="Allergies (séparées par ,)"
          value={formData.allergies}
          onChange={handleChange}
        /><br/><br/>

        {/* Contact d’urgence */}
        <h4>Contact d’urgence</h4>
        <input
          name="contact.nom"
          placeholder="Nom"
          value={formData.contactUrgence.nom}
          onChange={handleChange}
        /><br/><br/>
        <input
          name="contact.telephone"
          placeholder="Téléphone"
          value={formData.contactUrgence.telephone}
          onChange={handleChange}
        /><br/><br/>
        <input
          name="contact.lien"
          placeholder="Lien avec le patient"
          value={formData.contactUrgence.lien}
          onChange={handleChange}
        /><br/><br/>

        <button type="submit">Créer Dossier</button>
      </form>
    </div>
  );
};

export default CreateDossier;
