import React, { useState, useEffect } from 'react';
import { postData, getData } from '../api/endpoint';

const CreateDossierModal = ({ onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    patientId: '',
    patientFullName: '',
    sexe: '',
    groupeSanguin: '',
    resumeMedical: '',
    allergies: ''
  });
  
  // ‚úÖ VOTRE MEDECIN_ID ICI
  const MEDECIN_ID = "PASTE_YOUR_MONGO_ID_HERE"; 
  
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showPatients, setShowPatients] = useState(false);

  useEffect(() => {
    getData('/patients')
      .then(data => setPatients(Array.isArray(data) ? data : []))
      .catch(() => setPatients([]));
  }, []);

  const selectPatient = (patient) => {
    setFormData({
      patientId: patient._id,
      patientFullName: patient.fullName || patient.nom,
      sexe: patient.sexe || '',
      groupeSanguin: '',
      resumeMedical: '',
      allergies: ''
    });
    setShowPatients(false);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.patientId || !formData.resumeMedical) {
      alert('Patient et r√©sum√© obligatoires');
      return;
    }

    setLoading(true);
    
    try {
      const data = {
        patientId: formData.patientId,
        medecinId: MEDECIN_ID,
        resumeMedical: formData.resumeMedical,
        groupeSanguin: formData.groupeSanguin || null,
        sexe: formData.sexe,
        allergies: formData.allergies ? [{ allergie: formData.allergies }] : []
      };

      await postData('/dossier', data);
      alert('‚úÖ Dossier cr√©√© !');
      onSuccess();
      
    } catch (error) {
      localStorage.setItem('dossiersMedecin', JSON.stringify([{
        _id: Date.now().toString(),
        ...formData,
        medecinId: MEDECIN_ID
      }]));
      alert('‚úÖ Sauvegard√© localement');
      onSuccess();
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ STYLES OBJETS UNIQUES (AUCUN ARRAY/STRING)
  const styles = {
    modal: {
      position: 'fixed',
      zIndex: 10000,
      inset: '0px',
      backgroundColor: 'rgba(0,0,0,0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    container: {
      backgroundColor: 'white',
      padding: '2rem',
      borderRadius: '16px',
      maxWidth: '600px',
      width: '90%',
      maxHeight: '90vh',
      overflowY: 'auto'
    },
    header: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '1.5rem'
    },
    title: {
      color: '#1976d2',
      margin: 0,
      fontSize: '1.8rem'
    },
    closeBtn: {
      fontSize: '1.5rem',
      cursor: 'pointer',
      background: 'none',
      border: 'none',
      padding: '0.5rem'
    },
    patientBtn: {
      width: '100%',
      padding: '1.2rem',
      border: 'none',
      borderRadius: '12px',
      fontSize: '1.1rem',
      fontWeight: 'bold',
      cursor: 'pointer',
      marginBottom: '1rem',
      color: 'white'
    },
    patientBtnSelected: {
      ...styles.patientBtn,
      backgroundColor: '#4caf50'
    },
    patientBtnDefault: {
      ...styles.patientBtn,
      backgroundColor: '#2196f3'
    },
    patientItem: {
      padding: '1rem',
      cursor: 'pointer',
      borderBottom: '1px solid #eee',
      backgroundColor: 'transparent'
    },
    patientItemSelected: {
      padding: '1rem',
      cursor: 'pointer',
      borderBottom: '1px solid #eee',
      backgroundColor: '#e3f2fd'
    },
    input: {
      padding: '1rem 1.2rem',
      border: '2px solid #e0e0e0',
      borderRadius: '8px',
      fontSize: '1rem',
      width: '100%',
      boxSizing: 'border-box'
    },
    textarea: {
      padding: '1rem 1.2rem',
      border: '2px solid #e0e0e0',
      borderRadius: '8px',
      fontSize: '1rem',
      width: '100%',
      boxSizing: 'border-box',
      minHeight: '100px',
      resize: 'vertical'
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: '1fr 1fr',
      gap: '1rem',
      marginBottom: '1rem'
    },
    actions: {
      display: 'flex',
      gap: '1rem',
      marginTop: '1.5rem'
    },
    btn: {
      flex: 1,
      padding: '1rem',
      border: 'none',
      borderRadius: '8px',
      fontWeight: 'bold',
      fontSize: '1rem',
      cursor: 'pointer',
      color: 'white'
    },
    btnPrimary: {
      backgroundColor: '#4caf50'
    },
    btnDisabled: {
      backgroundColor: '#ccc',
      cursor: 'not-allowed'
    },
    btnCancel: {
      backgroundColor: '#f44336'
    },
    confirmation: {
      padding: '1rem',
      backgroundColor: '#e8f5e8',
      borderRadius: '8px',
      margin: '1rem 0'
    }
  };

  return (
    <div style={styles.modal}>
      <div style={styles.container}>
        <div style={styles.header}>
          <h2 style={styles.title}>‚ûï Nouveau Dossier</h2>
          <button style={styles.closeBtn} onClick={onClose}>√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          {/* ‚úÖ BOUTON PATIENT CORRIG√â */}
          <button 
            type="button" 
            onClick={() => setShowPatients(!showPatients)}
            style={
              formData.patientId 
                ? styles.patientBtnSelected 
                : styles.patientBtnDefault
            }
            disabled={loading}
          >
            {formData.patientId ? `‚úÖ ${formData.patientFullName}` : 'üë§ Choisir patient'}
          </button>

          {/* ‚úÖ LISTE PATIENTS CORRIG√âE */}
          {showPatients && patients.map(patient => (
            <div
              key={patient._id}
              onClick={() => selectPatient(patient)}
              style={
                formData.patientId === patient._id
                  ? styles.patientItemSelected
                  : styles.patientItem
              }
            >
              {patient.fullName || patient.nom} - {patient.email}
            </div>
          ))}

          {/* ‚úÖ CONFIRMATION CORRIG√âE */}
          {formData.patientId && (
            <div style={styles.confirmation}>
              ‚úÖ Patient s√©lectionn√©: <strong>{formData.patientFullName}</strong>
            </div>
          )}

          {/* ‚úÖ FORMULAIRE CORRIG√â */}
          {formData.patientId && (
            <>
              <div style={styles.grid}>
                <div>
                  <label style={{display: 'block', marginBottom: '0.5rem', fontWeight: '600'}}>
                    ü©∏ Groupe sanguin
                  </label>
                  <select 
                    name="groupeSanguin" 
                    value={formData.groupeSanguin} 
                    onChange={handleChange}
                    style={styles.input}
                  >
                    <option value="">Choisir...</option>
                    <option value="A+">A+</option><option value="A-">A-</option>
                    <option value="B+">B+</option><option value="B-">B-</option>
                    <option value="AB+">AB+</option><option value="AB-">AB-</option>
                    <option value="O+">O+</option><option value="O-">O-</option>
                  </select>
                </div>
                <div>
                  <label style={{display: 'block', marginBottom: '0.5rem', fontWeight: '600'}}>
                    ‚öïÔ∏è Sexe
