import React, { useState, useEffect } from 'react';
import { postData, getData } from '../api/endpoint';

const CreateDossierModal = ({ onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    patientFullName: '',
    patientEmail: '',
    dateOfBirth: '',
    sexe: 'M',
    adresse: '',
    telephone: '',
    groupeSanguin: '',
    resumeMedical: '',
    antecedents: '',
    allergies: '',
    medicaments: ''
  });
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showPatientSelect, setShowPatientSelect] = useState(false);

  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    try {
      const data = await getData('/patients');
      setPatients(data || []);
    } catch (err) {
      console.error('Erreur patients:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await postData('/dossier', formData);
      onSuccess();
    } catch (err) {
      alert('Erreur crÃ©ation dossier: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSelectPatient = (patient) => {
    setFormData({
      patientFullName: patient.fullName,
      patientEmail: patient.email,
      dateOfBirth: patient.date_of_birth,
      sexe: patient.sexe,
      adresse: patient.adresse || '',
      telephone: patient.telephone || ''
    });
    setShowPatientSelect(false);
  };

  return (
    <div style={{
      position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
      background: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center',
      zIndex: 1000
    }}>
      <div style={{
        background: 'white', padding: '2rem', borderRadius: '12px',
        maxWidth: '600px', maxHeight: '90vh', overflowY: 'auto', width: '90%'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '1.5rem' }}>
          <h2>âž• Nouveau Dossier Patient</h2>
          <button onClick={onClose} style={{
            background: 'none', border: 'none', fontSize: '1.5rem',
            cursor: 'pointer', color: '#666'
          }}>
            Ã—
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          {/* Bouton sÃ©lection rapide patient existant */}
          <div style={{ marginBottom: '1rem' }}>
            <button 
              type="button"
              onClick={() => setShowPatientSelect(!showPatientSelect)}
              style={{
                padding: '0.8rem 1.5rem', background: '#2196f3', color: 'white',
                border: 'none', borderRadius: '6px', cursor: 'pointer'
              }}
            >
              ðŸ‘¥ SÃ©lectionner patient existant
            </button>
          </div>

          {showPatientSelect && (
            <div style={{ marginBottom: '1rem', maxHeight: '200px', overflowY: 'auto' }}>
              {patients.map(patient => (
                <div key={patient._id} className="patient-item" style={{
                  padding: '1rem', border: '1px solid #ddd', marginBottom: '0.5rem',
                  borderRadius: '6px', cursor: 'pointer', background: '#f8f9fa'
                }}
                onClick={() => handleSelectPatient(patient)}
                >
                  <strong>{patient.fullName}</strong> - {patient.email}
                </div>
              ))}
            </div>
          )}

          {/* Informations patient */}
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', marginBottom: '1.5rem' }}>
            <input
              required
              placeholder="Nom complet *"
              value={formData.patientFullName}
              onChange={(e) => setFormData({...formData, patientFullName: e.target.value})}
              style={inputStyle}
            />
            <input
              type="email"
              placeholder="Email *"
              value={formData.patientEmail}
              onChange={(e) => setFormData({...formData, patientEmail: e.target.value})}
              style={inputStyle}
            />
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', marginBottom: '1.5rem' }}>
            <input
              type="date"
              required
              value={formData.dateOfBirth}
              onChange={(e) => setFormData({...formData, dateOfBirth: e.target.value})}
              style={inputStyle}
            />
            <select
              required
              value={formData.sexe}
              onChange={(e) => setFormData({...formData, sexe: e.target.value})}
              style={inputStyle}
            >
              <option value="M">Masculin</option>
              <option value="F">FÃ©minin</option>
            </select>
          </div>

          {/* Infos mÃ©dicales */}
          <div style={{ marginBottom: '1rem' }}>
            <label>Groupe sanguin</label>
            <select
              value={formData.groupeSanguin}
              onChange={(e) => setFormData({...formData, groupeSanguin: e.target.value})}
              style={inputStyle}
            >
              <option value="">SÃ©lectionner...</option>
              <option value="A+">A+</option>
              <option value="A-">A-</option>
              <option value="B+">B+</option>
              <option value="B-">B-</option>
              <option value="AB+">AB+</option>
              <option value="AB-">AB-</option>
              <option value="O+">O+</option>
              <option value="O-">O-</option>
            </select>
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label>RÃ©sumÃ© mÃ©dical</label>
            <textarea
              placeholder="AntÃ©cÃ©dents importants, diagnostics actuels..."
              value={formData.resumeMedical}
              onChange={(e) => setFormData({...formData, resumeMedical: e.target.value})}
              rows={3}
              style={{ ...inputStyle, width: '100%' }}
            />
          </div>

          <div style={{ marginBottom: '1.5rem' }}>
            <label>Allergies / MÃ©dicaments</label>
            <textarea
              placeholder="Aucun connu / Liste des traitements actuels"
              value={formData.allergies}
              onChange={(e) => setFormData({...formData, allergies: e.target.value})}
              rows={2}
              style={{ ...inputStyle, width: '100%' }}
            />
          </div>

          <div style={{ display: 'flex', gap: '1rem' }}>
            <button 
              type="submit" 
              disabled={loading}
              style={{
                flex: 1, padding: '1rem', background: '#4caf50', color: 'white',
                border: 'none', borderRadius: '8px', fontSize: '1.1rem', cursor: 'pointer'
              }}
            >
              {loading ? 'CrÃ©ation...' : 'ðŸ’¾ CrÃ©er Dossier'}
            </button>
            <button 
              type="button"
              onClick={onClose}
              style={{
                flex: 1, padding: '1rem', background: '#f44336', color: 'white',
                border: 'none', borderRadius: '8px', cursor: 'pointer'
              }}
            >
              Annuler
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const inputStyle = {
  padding: '1rem', border: '1px solid #ddd', borderRadius: '6px',
  fontSize: '1rem', width: '100%'
};

export default CreateDossierModal;
