import React, { useEffect, useState } from 'react';
import { getData } from '../api/endpoint';

const PatientDossiers = () => {
  const [dossiers, setDossiers] = useState([]);

  useEffect(() => {
    const fetchDossiers = async () => {
      try {
        const data = await getData('/dossiers/patient');
        setDossiers(data);
      } catch (e) {
        console.error(e);
      }
    };

    fetchDossiers();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h2>ðŸ“‚ Mes dossiers mÃ©dicaux</h2>

      {dossiers.length === 0 && <p>Aucun dossier</p>}

      {dossiers.map((d) => (
        <div key={d.id} style={{
          border: '1px solid #ddd',
          padding: '10px',
          marginBottom: '10px'
        }}>
          <strong>Motif :</strong> {d.motif}<br />
          <strong>Date :</strong> {d.createdAt}
        </div>
      ))}
    </div>
  );
};

export default PatientDossiers;
