import React, { useState, useEffect } from 'react';
import api, { deleteData } from '../../api/endpoint'; // ✅ CORRIGÉ
import { FiFileText, FiSearch, FiTrash2, FiEye } from 'react-icons/fi';

const Moderateurs = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [moderateurs, setModerateurs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadModerateurs();
  }, []);

  const loadModerateurs = async () => {
    try {
      setLoading(true);
      setError(null);
      const users = await api.getData('/users'); // ✅ CORRIGÉ
      const moderateursList = users.filter(user => user.role === 'Moderateur');
      setModerateurs(moderateursList);
    } catch (err) {
      console.error('Erreur chargement modérateurs:', err);
      setError(err.message || 'Impossible de charger les modérateurs.');
      setModerateurs([]);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (modId) => {
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer ce modérateur ?')) return;
    try {
      await deleteData(`/users/${modId}`); // ✅ ENDPOINT STANDARD
      loadModerateurs();
    } catch (err) {
      alert(err.message || 'Erreur suppression');
    }
  };

  const getSectionsModerees = (moderateur) => {
    if (moderateur.moderatorDetails?.moderatedSections) {
      return Object.keys(moderateur.moderatorDetails.moderatedSections);
    }
    return ['Toutes'];
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const date = new Date(dateString);
    return date.toLocaleDateString('fr-FR');
  };

  const filteredModerateurs = moderateurs.filter(mod =>
    mod.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    mod.email?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) {
    return <div className="flex items-center justify-center h-64"><div>Chargement...</div></div>;
  }

  // ✅ JSX IDENTIQUE (PARFAIT)
  // ✅ TEMPLATE CORRIGÉ POUR CHAQUE PAGE
const NomPage = () => {
  // ... VOS useState() + useEffect() ✅

  if (loading) return <div className="flex items-center justify-center h-64 p-6"><div className="text-lg">Chargement...</div></div>;

  return (
    <div className="p-6 max-w-7xl mx-auto">
      {/* VOTRE TITRE */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Votre Titre</h1>
        <p className="text-gray-600 mt-2">Description</p>
      </div>

      {/* VOTRE TABLEAU / FORMULAIRE */}
      <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
        {/* CONTENU */}
      </div>
    </div>
  );
};


};

export default Moderateurs;
