import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useLocation } from 'react-router-dom';
// COMMENTAIRE les imports probl√©matiques
// import CreerDossier from './CreerDossier'; 
// import api from '../../api/endpoint';

const MedecinDashboard = () => {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const location = useLocation();

  useEffect(() => {
    // Mock data DIRECT - pas d'API
    setTimeout(() => {
      setPatients([
        {_id: '1', fullName: 'Mamadou Sow', email: 'mamadou@email.com'},
        {_id: '2', fullName: 'Aminata Diallo', email: 'aminata@email.com'}
      ]);
      setLoading(false);
    }, 1000);
  }, []);

  const navItems = [
    { path: '/dashboard/medecin', label: 'Tableau de bord', icon: 'üìä' },
    { path: '/dashboard/medecin/patients', label: 'Patients', icon: 'üë•' }
  ];

  if (loading) return <div>Chargement...</div>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-8">
      <nav className="bg-white rounded-3xl shadow-2xl p-8 mb-8">
        <div className="max-w-4xl mx-auto flex justify-between items-center">
          <h1 className="text-3xl font-bold text-gray-900">üè• Dashboard M√©decin</h1>
          <div className="flex space-x-4">
            {navItems.map(item => (
              <Link
                key={item.path}
                to={item.path}
                className={`px-6 py-3 rounded-2xl font-semibold transition-all ${
                  location.pathname === item.path
                    ? 'bg-blue-600 text-white shadow-lg'
                    : 'bg-gray-100 hover:bg-blue-100 text-gray-700'
                }`}
              >
                {item.icon} {item.label}
              </Link>
            ))}
          </div>
        </div>
      </nav>

      <div className="max-w-6xl mx-auto">
        <Routes>
          <Route path="/" element={
            <div>
              <h2 className="text-4xl font-bold mb-8 text-gray-900">Bienvenue Docteur</h2>
              <div className="grid md:grid-cols-4 gap-6">
                <div className="bg-white p-8 rounded-3xl shadow-xl">Patients: {patients.length}</div>
                <div className="bg-white p-8 rounded-3xl shadow-xl">RDV: 3</div>
                <div className="bg-white p-8 rounded-3xl shadow-xl">Consultations: 47</div>
                <div className="bg-white p-8 rounded-3xl shadow-xl">Actifs</div>
              </div>
            </div>
          } />
          <Route path="/patients" element={
            <div>
              <h2 className="text-4xl font-bold mb-8">Patients</h2>
              <div className="bg-white rounded-3xl shadow-xl p-8">
                {patients.map(patient => (
                  <div key={patient._id} className="p-6 border-b">
                    {patient.fullName} - {patient.email}
                  </div>
                ))}
              </div>
            </div>
          } />
        </Routes>
      </div>
    </div>
  );
};

export default MedecinDashboard;
