import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import getData, { postData } from '../../api/endpoint';
import { FiFileText, FiUser, FiAlertCircle, FiHeart } from 'react-icons/fi';

const CreerDossier = () => {
  const navigate = useNavigate();
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [formData, setFormData] = useState({
    patientId: '',
    resumeMedical: '',
    groupeSanguin: '',
    antecedents: [],
    allergies: [],
    contactUrgence: {
      nom: '',
      telephone: '',
      lien: ''
    },
    statut: 'ACTIF'
  });
  const [newAntecedent, setNewAntecedent] = useState('');
  const [newAllergie, setNewAllergie] = useState('');

  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    try {
      // Utiliser la route spécifique pour les médecins
      const [patientsData, dossiersData] = await Promise.all([
        getData('/patients'),
        getData('/mes-dossiers')
      ]);
      
      // Filtrer les patients qui n'ont pas déjà un dossier actif
      const patientsAvecDossier = dossiersData
        .filter(d => d.statut === 'ACTIF')
        .map(d => d.patientId?._id || d.patientId);
      
      const patientsDisponibles = patientsData.filter(p => !patientsAvecDossier.includes(p._id));
      setPatients(patientsDisponibles);
    } catch (err) {
      console.error('Erreur lors du chargement des patients:', err);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith('contactUrgence.')) {
      const field = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        contactUrgence: {
          ...prev.contactUrgence,
          [field]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
    setError(null);
  };

  const addAntecedent = () => {
    if (newAntecedent.trim()) {
      setFormData(prev => ({
        ...prev,
        antecedents: [...prev.antecedents, { antecedent: newAntecedent.trim() }]
      }));
      setNewAntecedent('');
    }
  };

  const removeAntecedent = (index) => {
    setFormData(prev => ({
      ...prev,
      antecedents: prev.antecedents.filter((_, i) => i !== index)
    }));
  };

  const addAllergie = () => {
    if (newAllergie.trim()) {
      setFormData(prev => ({
        ...prev,
        allergies: [...prev.allergies, { allergie: newAllergie.trim() }]
      }));
      setNewAllergie('');
    }
  };

  const removeAllergie = (index) => {
    setFormData(prev => ({
      ...prev,
      allergies: prev.allergies.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(false);

    if (!formData.patientId) {
      setError('Veuillez sélectionner un patient.');
      return;
    }

    try {
      setLoading(true);
      
      // Préparer les données à envoyer
      const dataToSend = {
        patientId: formData.patientId,
        resumeMedical: formData.resumeMedical || undefined,
        groupeSanguin: formData.groupeSanguin || undefined,
        antecedents: formData.antecedents || [],
        allergies: formData.allergies || [],
        statut: formData.statut || 'ACTIF'
      };

      // Ajouter contactUrgence seulement si au moins un champ est rempli
      if (formData.contactUrgence && (
        formData.contactUrgence.nom || 
        formData.contactUrgence.telephone || 
        formData.contactUrgence.lien
      )) {
        dataToSend.contactUrgence = {
          nom: formData.contactUrgence.nom || undefined,
          telephone: formData.contactUrgence.telephone || undefined,
          lien: formData.contactUrgence.lien || undefined
        };
      }

      const response = await postData('/new-dossier', dataToSend);
      setSuccess(true);
      setTimeout(() => {
        navigate('/medecin/dossiers');
      }, 2000);
    } catch (err) {
      console.error('Erreur lors de la création du dossier:', err);
      setError(err.message || 'Erreur lors de la création du dossier.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8 px-4">
      <div className="max-w-4xl mx-auto">
        <div className="mb-8 text-center">
          <h2 className="text-4xl font-bold text-gray-900 mb-2">Créer un Dossier Médical</h2>
          <p className="text-gray-600 text-lg">Créez un nouveau dossier médical pour un patient</p>
        </div>

        {error && (
          <div className="bg-red-50 border-l-4 border-red-500 text-red-700 p-4 rounded-lg mb-6 shadow-md">
            <div className="flex items-center">
              <FiAlertCircle className="mr-2" size={20} />
              <p className="font-medium">{error}</p>
            </div>
          </div>
        )}

        {success && (
          <div className="bg-green-50 border-l-4 border-green-500 text-green-700 p-4 rounded-lg mb-6 shadow-md">
            <div className="flex items-center">
              <svg className="mr-2" fill="currentColor" viewBox="0 0 20 20" width="20" height="20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
              </svg>
              <p className="font-medium">Dossier créé avec succès ! Redirection en cours...</p>
            </div>
          </div>
        )}

        <form onSubmit={handleSubmit} className="bg-white rounded-2xl shadow-xl p-8 border border-gray-100">
        {/* Sélection du patient */}
        <div className="mb-6">
          <label className="block text-sm font-semibold text-gray-700 mb-2">
            <FiUser className="inline mr-2" size={18} />
            Patient *
          </label>
          <select
            name="patientId"
            value={formData.patientId}
            onChange={handleChange}
            required
            className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all bg-white"
          >
            <option value="">Sélectionner un patient</option>
            {patients.map(patient => (
              <option key={patient._id} value={patient._id}>
                {patient.fullName} - {patient.email}
              </option>
            ))}
          </select>
          {patients.length === 0 && (
            <p className="text-sm text-amber-600 mt-2 bg-amber-50 p-2 rounded">Tous les patients ont déjà un dossier actif.</p>
          )}
        </div>

        {/* Groupe sanguin */}
        <div className="mb-6">
          <label className="block text-sm font-semibold text-gray-700 mb-2">Groupe Sanguin</label>
          <select
            name="groupeSanguin"
            value={formData.groupeSanguin}
            onChange={handleChange}
            className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all bg-white"
          >
            <option value="">Sélectionner un groupe sanguin</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
          </select>
        </div>

        {/* Résumé médical */}
        <div className="mb-6">
          <label className="block text-sm font-semibold text-gray-700 mb-2">
            <FiFileText className="inline mr-2" size={18} />
            Résumé Médical
          </label>
          <textarea
            name="resumeMedical"
            value={formData.resumeMedical}
            onChange={handleChange}
            rows={4}
            className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all resize-none"
            placeholder="Résumé médical du patient..."
          />
        </div>

        {/* Antécédents */}
        <div className="mb-6">
          <label className="block text-sm font-semibold text-gray-700 mb-2">
            <FiAlertCircle className="inline mr-2" size={18} />
            Antécédents Médicaux
          </label>
          <div className="flex gap-2 mb-3">
            <input
              type="text"
              value={newAntecedent}
              onChange={(e) => setNewAntecedent(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addAntecedent())}
              placeholder="Ajouter un antécédent"
              className="flex-1 px-4 py-2 border-2 border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all"
            />
            <button
              type="button"
              onClick={addAntecedent}
              className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-all font-medium shadow-md hover:shadow-lg"
            >
              Ajouter
            </button>
          </div>
          <div className="space-y-2 max-h-40 overflow-y-auto">
            {formData.antecedents.map((antecedent, index) => (
              <div key={index} className="flex items-center justify-between bg-blue-50 px-4 py-2 rounded-lg border border-blue-200">
                <span className="text-gray-700">{antecedent.antecedent}</span>
                <button
                  type="button"
                  onClick={() => removeAntecedent(index)}
                  className="text-red-600 hover:text-red-800 font-medium transition-colors"
                >
                  ×
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Allergies */}
        <div className="mb-6">
          <label className="block text-sm font-semibold text-gray-700 mb-2">
            <FiHeart className="inline mr-2" size={18} />
            Allergies
          </label>
          <div className="flex gap-2 mb-3">
            <input
              type="text"
              value={newAllergie}
              onChange={(e) => setNewAllergie(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addAllergie())}
              placeholder="Ajouter une allergie"
              className="flex-1 px-4 py-2 border-2 border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all"
            />
            <button
              type="button"
              onClick={addAllergie}
              className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-all font-medium shadow-md hover:shadow-lg"
            >
              Ajouter
            </button>
          </div>
          <div className="space-y-2 max-h-40 overflow-y-auto">
            {formData.allergies.map((allergie, index) => (
              <div key={index} className="flex items-center justify-between bg-red-50 px-4 py-2 rounded-lg border border-red-200">
                <span className="text-gray-700">{allergie.allergie}</span>
                <button
                  type="button"
                  onClick={() => removeAllergie(index)}
                  className="text-red-600 hover:text-red-800 font-medium transition-colors"
                >
                  ×
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Contact d'urgence */}
        <div className="mb-6 p-4 bg-gray-50 rounded-lg border border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Contact d'Urgence</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">Nom</label>
              <input
                type="text"
                name="contactUrgence.nom"
                value={formData.contactUrgence.nom}
                onChange={handleChange}
                className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all bg-white"
              />
            </div>
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">Téléphone</label>
              <input
                type="tel"
                name="contactUrgence.telephone"
                value={formData.contactUrgence.telephone}
                onChange={handleChange}
                className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all bg-white"
              />
            </div>
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">Lien</label>
              <input
                type="text"
                name="contactUrgence.lien"
                value={formData.contactUrgence.lien}
                onChange={handleChange}
                placeholder="ex: Époux, Parent, etc."
                className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all bg-white"
              />
            </div>
          </div>
        </div>

        {/* Boutons */}
        <div className="flex gap-4 pt-4 border-t border-gray-200">
          <button
            type="submit"
            disabled={loading}
            className="flex-1 bg-gradient-to-r from-blue-500 to-blue-600 text-white py-3 rounded-lg hover:from-blue-600 hover:to-blue-700 transition-all font-semibold shadow-md hover:shadow-lg disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? (
              <span className="flex items-center justify-center">
                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Création en cours...
              </span>
            ) : (
              'Créer le dossier'
            )}
          </button>
          <button
            type="button"
            onClick={() => navigate('/medecin/dossiers')}
            className="px-8 py-3 border-2 border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-all font-medium"
          >
            Annuler
          </button>
        </div>
      </form>
      </div>
    </div>
  );
};

export default CreerDossier;
