import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useLocation } from 'react-router-dom';

const PatientDashboard = () => {
  const location = useLocation();
  const [profile, setProfile] = useState(null);
  const [rendezVous, setRendezVous] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showFormRDV, setShowFormRDV] = useState(false);

  useEffect(() => {
    const savedRDV = JSON.parse(localStorage.getItem('patientRDV') || '[]');
    const savedNotifs = JSON.parse(localStorage.getItem('patientNotifications') || '[]');
    
    setTimeout(() => {
      setProfile({
        nom: 'Aminata DIOP', prenom: 'Aminata', email: 'aminata.diop@email.com',
        telephone: '+221 78 123 4567', age: 35, sexe: 'FÃ©minin', 
        groupeSanguin: 'O+', allergies: 'Aucune connue'
      });
      setRendezVous(savedRDV);
      setNotifications(savedNotifs);
      setLoading(false);
    }, 800);
  }, []);

  useEffect(() => {
    localStorage.setItem('patientRDV', JSON.stringify(rendezVous));
    localStorage.setItem('patientNotifications', JSON.stringify(notifications));
  }, [rendezVous, notifications]);

  if (loading) return <LoadingScreen />;

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
      {/* Notifications Toast - PROPRE */}
      <NotificationsToast notifications={notifications} />
      
      {/* Sidebar GOUVERNEMENTALE */}
      <aside className="hidden lg:block lg:w-64 lg:h-screen lg:fixed lg:inset-y-0 lg:z-40 bg-white shadow-lg border-r border-gray-200">
        <ProfileSidebar profile={profile} />
        <Navigation activePath={location.pathname} rdvCount={rendezVous.length} />
      </aside>

      {/* Contenu principal */}
      <main className="lg:ml-64 p-6 lg:p-8 pb-20">
        <Routes>
          <Route path="/" element={<DashboardHome profile={profile} rdv={rendezVous} notifications={notifications} />} />
          <Route path="/rdv" element={<RDVPage rdv={rendezVous} />} />
          <Route path="/rdv/nouveau" element={<NouveauRDVForm rdv={rendezVous} setRdv={setRendezVous} setNotifications={setNotifications} setShowForm={setShowFormRDV} />} />
          <Route path="/dossier" element={<DossierPage />} />
          <Route path="/ordonnances" element={<OrdonnancesPage />} />
          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </main>

      {/* Modal Formulaire */}
      {showFormRDV && (
        <div className="fixed inset-0 bg-black/50 z-50 lg:hidden flex items-center justify-center p-4" onClick={() => setShowFormRDV(false)}>
          <div className="w-full max-w-lg bg-white rounded-2xl shadow-2xl p-8 max-h-[90vh] overflow-y-auto" onClick={e => e.stopPropagation()}>
            <NouveauRDVFormMobile rdv={rendezVous} setRdv={setRendezVous} setNotifications={setNotifications} setShowForm={setShowFormRDV} />
          </div>
        </div>
      )}
    </div>
  );
};

// ðŸ”¥ FORMULAIRE PROPRE
const NouveauRDVForm = ({ rdv, setRdv, setNotifications, setShowForm }) => {
  const [formData, setFormData] = useState({ medecin: '', specialite: '', date: '', heure: '', motif: '' });
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  const medecins = [
    { nom: 'Dr. Moustapha NDIAYE', specialite: 'Cardiologue' },
    { nom: 'Dr. Awa SOW', specialite: 'GynÃ©cologue' },
    { nom: 'Dr. Fatou DIA', specialite: 'PÃ©diatre' },
    { nom: 'Dr. Ibrahima BA', specialite: 'MÃ©decin gÃ©nÃ©raliste' }
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    setTimeout(() => {
      const nouveauRDV = {
        _id: Date.now().toString(),
        medecin: { nom: formData.medecin },
        specialite: formData.specialite,
        date: `${formData.date} ${formData.heure}`,
        statut: 'ConfirmÃ©',
        motif: formData.motif,
        createdAt: new Date().toISOString()
      };

      setRdv(prev => [nouveauRDV, ...prev]);
      
      const notification = {
        id: Date.now(),
        title: 'Rendez-vous confirmÃ©',
        message: `Consultation avec ${formData.medecin} le ${formData.date} Ã  ${formData.heure}`,
        type: 'success',
        date: new Date().toLocaleString('fr-FR'),
        read: false
      };
      setNotifications(prev => [notification, ...prev]);

      setSuccess(true);
      setLoading(false);
      setTimeout(() => {
        setShowForm(false);
        setSuccess(false);
      }, 2500);
    }, 1200);
  };

  if (success) {
    return (
      <div className="text-center p-8">
        <div className="w-24 h-24 bg-green-500 text-white rounded-full flex items-center justify-center mx-auto mb-6 text-4xl font-bold shadow-lg">
          âœ“
        </div>
        <h3 className="text-2xl font-bold text-green-800 mb-4">Rendez-vous confirmÃ© !</h3>
        <p className="text-lg text-gray-700 mb-6">Consultez votre agenda</p>
        <Link to="/patient/rdv" className="w-full bg-blue-600 text-white font-bold py-3 px-6 rounded-xl hover:bg-blue-700 transition-colors inline-block">
          Voir l'agenda
        </Link>
      </div>
    );
  }

  return (
    <div>
      <div className="flex justify-between items-center mb-8">
        <h2 className="text-2xl font-bold text-gray-900">ðŸ“… Nouveau rendez-vous</h2>
