import React, { useEffect, useState } from "react";
import { getData } from "../api/endpoint";

const PatientDashboard = () => {
  const [rendezVous, setRendezVous] = useState([]);
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const token = localStorage.getItem("token");
  const decoded = token ? JSON.parse(atob(token.split(".")[1])) : null;
  const patientId = decoded?._id;

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        // Rendez-vous du patient
        const rdvData = await getData(`/rendezvous?patientId=${patientId}`);
        setRendezVous(rdvData);

        // Dossiers du patient
        const dossiersData = await getData(`/mes-dossiers`);
        setDossiers(dossiersData);

        setLoading(false);
      } catch (err) {
        console.error("Erreur chargement patient :", err);
        setError(err.message);
        setLoading(false);
      }
    };

    fetchData();
  }, [patientId]);

  if (loading) return <p className="text-center mt-10">Chargement des données...</p>;
  if (error) return <p className="text-center mt-10 text-red-600">Erreur: {error}</p>;

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">Bienvenue sur votre espace patient</h1>

      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Rendez-vous à venir</h2>
        {rendezVous.length === 0 ? (
          <p>Aucun rendez-vous à venir.</p>
        ) : (
          <ul className="border rounded p-4">
            {rendezVous.map((rdv) => (
              <li key={rdv._id} className="mb-2">
                <strong>Date :</strong> {rdv.date} <br />
                <strong>Heure :</strong> {rdv.heure} <br />
                <strong>Médecin :</strong> {rdv.medecin?.fullName || "Non défini"} <br />
                <strong>Service :</strong> {rdv.service || "Non défini"}
              </li>
            ))}
          </ul>
        )}
      </section>

      <section>
        <h2 className="text-2xl font-semibold mb-4">Dossiers médicaux</h2>
        {dossiers.length === 0 ? (
          <p>Aucun dossier disponible.</p>
        ) : (
          <ul className="border rounded p-4">
            {dossiers.map((dossier) => (
              <li key={dossier._id} className="mb-2">
                <strong>Titre :</strong> {dossier.titre} <br />
                <strong>Date :</strong> {dossier.date} <br />
                <strong>Médecin :</strong> {dossier.medecin?.fullName || "Non défini"}
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
};

export default PatientDashboard;
