import React, { useEffect, useState } from "react";
import * as api from "../api/endpoint";
import "./PatientDashboard.css";

const PatientDashboard = () => {
  const [patientData, setPatientData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    loadPatientData();
  }, []);

  const loadPatientData = async () => {
    setLoading(true);
    try {
      const data = await api.getData("/patient/me"); // endpoint pour récupérer les infos du patient connecté
      setPatientData(data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError(err.message || "Erreur lors du chargement des données");
      setLoading(false);
    }
  };

  if (loading) return <p>Chargement de vos informations...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div className="patient-dashboard">
      <h2>Bienvenue, {patientData.fullName}</h2>

      <section className="info-section">
        <h3>Informations personnelles</h3>
        <p><strong>Email :</strong> {patientData.email}</p>
        <p><strong>Date de naissance :</strong> {patientData.dateOfBirth}</p>
        <p><strong>Numéro :</strong> {patientData.phone || "Non renseigné"}</p>
      </section>

      <section className="appointments-section">
        <h3>Rendez-vous</h3>
        {patientData.appointments && patientData.appointments.length > 0 ? (
          <ul>
            {patientData.appointments.map((rdv) => (
              <li key={rdv._id}>
                {rdv.date} - {rdv.doctorName} - {rdv.status}
              </li>
            ))}
          </ul>
        ) : (
          <p>Aucun rendez-vous prévu</p>
        )}
      </section>

      <section className="prescriptions-section">
        <h3>Ordonnances</h3>
        {patientData.prescriptions && patientData.prescriptions.length > 0 ? (
          <ul>
            {patientData.prescriptions.map((presc) => (
              <li key={presc._id}>
                {presc.date} - {presc.medications.join(", ")}
              </li>
            ))}
          </ul>
        ) : (
          <p>Aucune ordonnance</p>
        )}
      </section>

      <section className="nutrition-section">
        <h3>Nutrition & Activité</h3>
        <p>{patientData.nutrition || "Aucune information nutritionnelle"}</p>
      </section>
    </div>
  );
};

export default PatientDashboard;
