import React, { useEffect, useState } from "react";
import * as api from "../api/endpoint";
import "./PatientInterface.css";
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from "recharts";

const PatientInterface = () => {
  const [patientData, setPatientData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    loadPatientData();
  }, []);

  const loadPatientData = async () => {
    setLoading(true);
    try {
      const data = await api.getData("/patient/me");
      setPatientData(data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError(err.message || "Erreur lors du chargement des données");
      setLoading(false);
    }
  };

  const cancelRendezVous = async (rdvId) => {
    if (!window.confirm("Voulez-vous vraiment annuler ce rendez-vous ?")) return;
    try {
      await api.deleteData(`/rendezvous/${rdvId}`);
      alert("Rendez-vous annulé !");
      loadPatientData(); // recharge les données
    } catch (err) {
      console.error(err);
      alert("Erreur : " + err.message);
    }
  };

  if (loading) return <p>Chargement des informations...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div className="patient-interface">
      <header className="patient-header">
        <h1>Bienvenue, {patientData.fullName}</h1>
      </header>

      <div className="cards-container">
        {/* Dossier Médical */}
        <div className="card">
          <h3>Dossier Médical</h3>
          <p><strong>Email :</strong> {patientData.email}</p>
          <p><strong>Date de naissance :</strong> {patientData.date_of_birth}</p>
          <p><strong>Sexe :</strong> {patientData.sexe || "Non renseigné"}</p>
        </div>

        {/* Rendez-vous */}
        <div className="card">
          <h3>Rendez-vous</h3>
          {patientData.rendezvous && patientData.rendezvous.length > 0 ? (
            <ul>
              {patientData.rendezvous.map((rdv) => (
                <li key={rdv._id}>
                  {rdv.date} - {rdv.doctorName} - {rdv.status}{" "}
                  {rdv.status === "Prévu" && (
                    <button onClick={() => cancelRendezVous(rdv._id)}>Annuler</button>
                  )}
                </li>
              ))}
            </ul>
          ) : (
            <p>Aucun rendez-vous prévu</p>
          )}
        </div>

        {/* Ordonnances */}
        <div className="card">
          <h3>Ordonnances</h3>
          {patientData.ordonnances && patientData.ordonnances.length > 0 ? (
            <ul>
              {patientData.ordonnances.map((presc) => (
                <li key={presc._id}>
                  {presc.date} - {presc.medications.join(", ")}
                  {/* ici tu peux ajouter un bouton pour télécharger en PDF */}
                </li>
              ))}
            </ul>
          ) : (
            <p>Aucune ordonnance</p>
          )}
        </div>

        {/* Nutrition & Activité */}
        <div className="card">
          <h3>Nutrition & Activité</h3>
          {patientData.activityData && patientData.activityData.length > 0 ? (
            <ResponsiveContainer width="100%" height={200}>
              <LineChart data={patientData.activityData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis />
                <Tooltip />
                <Line type="monotone" dataKey="weight" stroke="#8884d8" />
                <Line type="monotone" dataKey="calories" stroke="#82ca9d" />
              </LineChart>
            </ResponsiveContainer>
          ) : (
            <p>Aucune donnée nutritionnelle disponible</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default PatientInterface;
