import React from 'react';
import { Link, Routes, Route, useLocation } from 'react-router-dom';
import { 
  usePatientProfile, 
  usePatientRDV, 
  usePatientDossier, 
  usePatientOrdonnances 
} from '../hooks/usePatientData';

const PatientDashboard = () => {
  const location = useLocation();
  const { profile, loading: profileLoading } = usePatientProfile();
  const { rdv: prochainsRDV, loading: rdvLoading } = usePatientRDV();
  const { dossier, loading: dossierLoading } = usePatientDossier();
  const { ordonnances, loading: ordonnancesLoading } = usePatientOrdonnances();

  // Loading global
  if (profileLoading || rdvLoading || dossierLoading || ordonnancesLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="text-center">
          <div className="w-24 h-24 border-4 border-blue-200 border-t-blue-500 rounded-full animate-spin mx-auto mb-8"></div>
          <h1 className="text-4xl font-black text-gray-900 mb-4">Chargement Dashboard Patient...</h1>
          <p className="text-xl text-blue-600">Connexion MongoDB en cours</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      {/* Sidebar Navigation */}
      <div className="hidden lg:block lg:w-80 lg:fixed lg:inset-y-0 lg:z-50 bg-white/95 backdrop-blur-2xl shadow-2xl border-r-4 border-blue-200/50 h-screen overflow-y-auto">
        <div className="p-8 border-b-4 border-blue-100/50 bg-gradient-to-b from-blue-50/80 to-white/60 sticky top-0 z-10">
          <div className="w-28 h-28 bg-gradient-to-br from-blue-500 via-indigo-500 to-purple-500 rounded-3xl flex items-center justify-center mx-auto mb-8 shadow-2xl ring-4 ring-white/50">
            <span className="text-4xl text-white drop-shadow-lg">üë§</span>
          </div>
          <h2 className="text-4xl font-black text-gray-900 text-center mb-6 leading-tight">
            {profile?.nom} {profile?.prenom}
          </h2>
          <div className="text-center space-y-2 bg-blue-50/50 p-4 rounded-2xl backdrop-blur-sm">
            <p className="text-2xl font-bold text-blue-700">
              {profile?.age} ans ‚Ä¢ {profile?.sexe}
            </p>
            <p className="text-xl text-gray-600 flex items-center justify-center gap-2 flex-wrap">
              <span>{profile?.groupeSanguin}</span>
              <span>‚Ä¢</span>
              <span className="text-sm">{profile?.allergies || 'Aucune'}</span>
            </p>
          </div>
        </div>
        
        <nav className="p-8 space-y-4 mt-8">
          {[
            { path: '/patient', icon: 'üè†', label: 'Dashboard', color: 'blue' },
            { path: '/patient/rdv', icon: 'üìÖ', label: 'Rendez-vous', color: 'emerald' },
            { path: '/patient/dossier', icon: 'üìÑ', label: 'Dossier m√©dical', color: 'purple' },
            { path: '/patient/ordonnances', icon: 'üíä', label: 'Ordonnances', color: 'orange' }
          ].map(({ path, icon, label, color }, i) => (
            <Link 
              key={i}
              to={path}
              className={`flex items-center p-8 rounded-3xl w-full transition-all duration-300 font-bold text-2xl group hover:shadow-2xl hover:-translate-x-2 hover:scale-[1.02] ${
                location.pathname === path 
                  ? `bg-gradient-to-r from-${color}-500 to-${color}-600 text-white shadow-2xl shadow-${color}-200/50 scale-105`
                  : `text-gray-700 hover:bg-${color}-50/80 hover:shadow-xl hover:border-${color}-200 border-2 border-transparent`
              }`}
            >
              <span className="mr-8 text-4xl group-hover:scale-110 transition-transform">{icon}</span>
              {label}
            </Link>
          ))}
        </nav>
      </div>

      {/* Contenu principal */}
      <div className={`lg:ml-80 p-8 pb-24 transition-all duration-300 ${location.pathname !== '/patient' ? 'pt-24 lg:pt-8' : ''}`}>
        <Routes>
          {/* üè† DASHBOARD PRINCIPAL */}
          <Route path="/" element={
            <div>
              {/* PROFIL PRINCIPAL */}
              <div className="bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-blue-200/50 p-16 mb-20 max-w-7xl mx-auto">
                <div className="text-center mb-16">
                  <h1 className="text-7xl font-black bg-gradient-to-r from-gray-900 via-blue-900 to-indigo-900 bg-clip-text text-transparent mb-8 leading-tight">
                    Bienvenue {profile?.nom} üëã
                  </h1>
                  <p className="text-3xl text-blue-700 font-semibold">{profile?.email}</p>
                </div>
                
                {/* Infos rapides */}
                <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-8">
                  <div className="group p-12 bg-gradient-to-br from-blue-50 to-indigo-50 rounded-4xl border-4 border-blue-100 hover:shadow-3xl hover:-translate-y-3 transition-all cursor-pointer">
                    <div className="text-6xl mb-6">{profile?.telephone ? 'üì±' : 'üìû'}</div>
                    <p className="text-5xl font-black text-blue-800 mb-4 leading-tight">{profile?.telephone || 'Non renseign√©'}</p>
                    <p className="text-2xl text-blue-600 font-bold">Contact</p>
                  </div>
                  <div className="group p-12 bg-gradient-to-br from-emerald-50 to-teal-50 rounded-4xl border-4 border-emerald-100 hover:shadow-3xl hover:-translate-y-3 transition-all cursor-pointer">
                    <div className="text-6xl mb-6">ü©∫</div>
                    <p className="text-5xl font-black text-emerald-800 mb-4">{profile?.groupeSanguin || 'NR'}</p>
                    <p className="text-2xl text-emerald-600 font-bold">Groupe sanguin</p>
                  </div>
                  <div className="group p-12 bg-gradient-to-br from-purple-50 to-pink-50 rounded-4xl border-4 border-purple-100 hover:shadow-3xl hover:-translate-y-3 transition-all cursor-pointer">
                    <div className="text-6xl mb-6">‚ö†Ô∏è</div>
                    <p className="text-3xl font-bold text-purple-800 mb-4">{profile?.allergies || 'Aucune'}</p>
                    <p className="text-2xl text-purple-600 font-bold">Allergies</p>
                  </div>
                  <div className="group p-12 bg-gradient-to-br from-emerald-50 to-green-50 rounded-4xl border-4 border-emerald-100 hover:shadow-3xl hover:-translate-y-3 transition-all">
                    <div className="w-32 h-32 bg-gradient-to-br from-emerald-400 to-teal-500 rounded-4xl flex items-center justify-center mx-auto mb-8 shadow-2xl group-hover:scale-110 transition-all">
                      <span className="text-5xl text-white">‚úì</span>
                    </div>
                    <p className="text-2xl text-emerald-700 font-bold text-center">Compte v√©rifi√©</p>
                  </div>
                </div>
              </div>

              {/* Stats principales */}
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 mb-20 max-w-7xl mx-auto">
                {/* RDV */}
                <div className="bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-emerald-200/50 p-16">
                  <h2 className="text-6xl font-black text-gray-900 mb-12 flex items-center gap-8">
                    üìÖ Rendez-vous √† venir
                    <span className="text-4xl bg-emerald-100 text-emerald-800 px-8 py-4 rounded-4xl font-bold">
                      {prochainsRDV?.length || 0}
                    </span>
                  </h2>
                  <div className="space-y-8 max-h-96 overflow-y-auto">
                    {prochainsRDV?.slice(0, 3).map(rdv => (
                      <Link 
                        key={rdv._id} 
                        to={`/patient/rdv/${rdv._id}`}
                        className="group p-12 bg-gradient-to-r from-emerald-50/80 to-blue-50/80 rounded-4xl border-4 border-emerald-200/50 hover:shadow-4xl hover:-translate-y-4 transition-all cursor-pointer backdrop-blur-xl"
                      >
                        <div className="flex items-start lg:items-center gap-12">
                          <div className="w-36 h-36 bg-gradient-to-br from-emerald-500 to-teal-600 rounded-4xl flex flex-col items-center justify-center flex-shrink-0 shadow-3xl group-hover:scale-110 transition-all">
                            <span className="text-4xl font-black text-white">{rdv.date?.split(' ')[1] || 'TBD'}</span>
                            <span className="text-2xl font-bold text-white">{rdv.date?.split(' ')[0] || 'TBD'}</span>
                          </div>
                          <div className="flex-1 min-w-0">
                            <h4 className="text-5xl font-black text-gray-900 mb-6 break-words">{rdv.medecin?.nom || 'M√©decin'}</h4>
                            <p className="text-4xl text-emerald-700 mb-8 font-bold">{rdv.specialite || 'Sp√©cialit√©'}</p>
                            <div className="flex items-center gap-6">
                              <span className={`px-10 py-6 rounded-4xl font-black text-3xl shadow-2xl ${
                                rdv.statut === 'Confirm√©' 
                                  ? 'bg-emerald-500 text-white' 
                                  : rdv.statut === 'Annul√©' 
                                  ? 'bg-orange-500 text-white' 
                                  : 'bg-blue-500 text-white'
                              }`}>
                                {rdv.statut || 'Planifi√©'}
                              </span>
                              {rdv.pharmacie && (
                                <span className="px-8 py-4 bg-gray-100 text-gray-800 font-bold text-2xl rounded-3xl shadow-lg">
                                  üè• {rdv.pharmacie.nom}
                                </span>
                              )}
                            </div>
                          </div>
                        </div>
                      </Link>
                    ))}
                  </div>
                </div>

                {/* R√©sum√© sant√© */}
                <div className="bg-white/90 backdrop-blur-xl rounded-4xl shadow-3xl border-4 border-purple-200/50 p-16">
                  <h2 className="text-6xl font-black text-gray-900 mb-12">üìä R√©sum√© sant√©</h2>
                  
                  {/* Derni√®re consultation */}
                  <div className="mb-16 p-12 bg-gradient-to-r from-purple-50/80 to-indigo-50/80 rounded-4xl border-4 border-purple-200/50 backdrop-blur-xl">
                    <h4 className="text-4xl font-black text-purple-800 mb-8 flex items-center gap-6">
                      ü©∫ Derni√®re consultation
                    </h4>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-8 text-2xl">
                      <div className="p-8 bg-white/50 rounded-3xl">
                        <p className="font-bold text-gray-700 mb-4">Date</p>
                        <p className="font-black text-4xl text-purple-600">{dossier?.derniereConsultation?.date || 'Aucune'}</p>
                      </div>
                      <div className="p-8 bg-white/50 rounded-3xl">
                        <p className="font-bold text-gray-700 mb-4">M√©decin</p>
