import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { 
  getAuthToken, 
  decodeToken, 
  removeAuthToken 
} from '../api/endpoint';
import {
  FiActivity, 
  FiUsers, 
  FiCalendar,
  FiBarChart, 
  FiBell, 
  FiSettings, 
  FiLogOut, 
  FiMenu, 
  FiX
} from 'react-icons/fi';

// Composant Dashboard principal (page d'accueil service)
const DashboardHome = ({ user }) => {
  const [stats, setStats] = useState({
    services: 0,
    medecins: 0,
    rendezVous: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadStats();
  }, []);

  const loadStats = async () => {
    try {
      setLoading(true);
      const [servicesData, usersData, rdvData] = await Promise.all([
        getData('/services').catch(() => []),
        getData('/users').catch(() => []),
        getData('/rendezvous').catch(() => [])
      ]);

      const medecins = usersData.filter(u => u.role === 'Medecin');

      setStats({
        services: servicesData.length,
        medecins: medecins.length,
        rendezVous: rdvData.length
      });
    } catch (err) {
      console.error('Erreur lors du chargement des statistiques:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900">Tableau de Bord Service</h2>
        <p className="text-gray-600 mt-2">Bienvenue dans l'espace de gestion des services</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
        <div className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Services</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.services}
              </p>
              <p className="text-sm text-indigo-600 mt-1 font-medium">Actifs</p>
            </div>
            <FiActivity size={32} className="text-indigo-500 group-hover:scale-110 transition-transform" />
          </div>
        </div>

        <div className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">M√©decins</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.medecins}
              </p>
              <p className="text-sm text-green-600 mt-1 font-medium">Total</p>
            </div>
            <FiUsers size={32} className="text-green-500 group-hover:scale-110 transition-transform" />
          </div>
        </div>

        <div className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group transform hover:scale-105">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Rendez-vous</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">
                {loading ? '...' : stats.rendezVous}
              </p>
              <p className="text-sm text-blue-600 mt-1 font-medium">Total</p>
            </div>
            <FiCalendar size={32} className="text-blue-500 group-hover:scale-110 transition-transform" />
