import React, { useState, useEffect } from 'react';
import { Link, useNavigate, Routes, Route, useLocation } from 'react-router-dom';
import { 
  getAuthToken, 
  decodeToken, 
  removeAuthToken 
} from '../api/endpoint';
import {
  FiUsers, 
  FiFileText, 
  FiCalendar, 
  FiActivity,
  FiBarChart, 
  FiBell, 
  FiSettings, 
  FiLogOut, 
  FiMenu, 
  FiX
} from 'react-icons/fi';

// Import des pages medecin
import MesPatients from './medecin/MesPatients';
import MesConsultations from './medecin/MesConsultations';
import CreerDossier from './medecin/CreerDossier';
import MesRendezVous from './medecin/MesRendezVous';

// Composant Dashboard principal (page d'accueil medecin)
const DashboardHome = ({ user }) => {
  return (
    <>
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-gray-900">Tableau de Bord MÃ©decin</h2>
        <p className="text-gray-600 mt-2">Bienvenue, Dr. {user?.fullName || user?.email || 'MÃ©decin'}</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-10">
        <Link to="/medecin/patients" className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Mes Patients</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">-</p>
              <p className="text-sm text-blue-600 mt-1 font-medium">Voir tous</p>
            </div>
            <FiUsers size={32} className="text-blue-500 group-hover:scale-110 transition-transform" />
          </div>
        </Link>

        <Link to="/medecin/consultations" className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Consultations</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">-</p>
              <p className="text-sm text-green-600 mt-1 font-medium">Aujourd'hui</p>
            </div>
            <FiActivity size={32} className="text-green-500 group-hover:scale-110 transition-transform" />
          </div>
        </Link>

        <Link to="/medecin/rendez-vous" className="bg-white p-8 rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-100 group">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Rendez-vous</p>
              <p className="text-3xl font-bold text-gray-900 mt-2">-</p>
