  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = getAuthToken();
    if (!token) {
      navigate('/login');
      return;
    }

    const decoded = decodeToken(token);
    if (decoded?.role !== 'Medecin') {
      navigate('/');
      return;
    }
    
    setUser(decoded);
  }, [navigate]);

  const logout = () => {
    removeAuthToken();
    navigate('/login');
  };

  const navigation = [
    { name: 'Dashboard', icon: FiBarChart, href: '/medecin', exact: true },
