                <div className="w-8 h-8 bg-gradient-to-r from-green-500 to-green-600 rounded-full flex items-center justify-center">
                  <span className="text-white font-semibold text-sm">
                    {user?.fullName?.[0]?.toUpperCase() || user?.email?.[0]?.toUpperCase() || 'M'}
                  </span>
                </div>
                <span className="font-medium text-gray-700 hidden md:block">
                  Dr. {user?.fullName || user?.email || 'MÃ©decin'}
                </span>
              </div>
            </div>
          </div>
        </header>

        {/* Content avec children */}
        <main style={{ padding: '20px', minHeight: 'calc(100vh - 64px)' }}>
          {children}
        </main>
