import React, { useEffect, useState } from 'react';
import { getData } from '../api/endpoint';

const PatientDossiers = () => {
  const [dossiers, setDossiers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDossiers = async () => {
      try {
        // ‚ö†Ô∏è Adapter la route selon TON backend
        const data = await getData('/patients/me/dossiers');
        setDossiers(data);
      } catch (err) {
        console.error('Erreur fetch dossiers:', err);
        setError('Impossible de charger vos dossiers');
      } finally {
        setLoading(false);
      }
    };

    fetchDossiers();
  }, []);

  if (loading) {
    return <p>Chargement des dossiers...</p>;
  }

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <div>
      <h3>üìÅ Mes dossiers m√©dicaux</h3>

      {dossiers.length === 0 ? (
        <p>Aucun dossier disponible</p>
      ) : (
        <ul>
          {dossiers.map((dossier) => (
            <li key={dossier._id}>
              <strong>{dossier.resumeMedical || 'Dossier m√©dical'}</strong>
              {dossier.statut && <span> - Statut: {dossier.statut}</span>}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default PatientDossiers;
