import React, { useState, useEffect } from 'react';
import getData, { deleteData, postData, putData } from '../../api/endpoint';
import { FiFileText, FiSearch, FiPlus, FiEdit, FiTrash2, FiEye } from 'react-icons/fi';

const Ordonnances = () => {
  const [ordonnances, setOrdonnances] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [selectedOrdonnance, setSelectedOrdonnance] = useState(null);
  const [formData, setFormData] = useState({
    patientId: '',
    medecinId: '',
    medicaments: [],
    instructionsSupplementaires: ''
  });
  const [newMedicament, setNewMedicament] = useState({
    nomMedicament: '',
    dosage: '',
    frequence: '',
    duree: ''
  });
  const [patients, setPatients] = useState([]);
  const [medecins, setMedecins] = useState([]);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [ordonnancesData, usersData] = await Promise.all([
        getData('/ordonnances'),
        getData('/users')
      ]);
      setOrdonnances(ordonnancesData);
      setPatients(usersData.filter(u => u.role === 'Patient'));
      setMedecins(usersData.filter(u => u.role === 'Medecin'));
    } catch (err) {
      console.error('Erreur lors du chargement:', err);
      setError('Impossible de charger les données.');
    } finally {
      setLoading(false);
    }
  };

  const handleView = (ordonnance) => {
    setSelectedOrdonnance(ordonnance);
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer cette ordonnance ?')) return;
    try {
      await deleteData(`/ordonnance/id?id=${id}`);
      alert('Ordonnance supprimée avec succès');
      loadData();
    } catch (err) {
      alert('Erreur lors de la suppression');
    }
  };

  const handleOpenForm = (ordonnance = null) => {
    if (ordonnance) {
      setFormData({
        patientId: ordonnance.patientId?._id || ordonnance.patientId || '',
        medecinId: ordonnance.medecinId?._id || ordonnance.medecinId || '',
        medicaments: ordonnance.medicaments || [],
        instructionsSupplementaires: ordonnance.instructionsSupplementaires || ''
      });
      setSelectedOrdonnance(ordonnance);
    } else {
      setFormData({
        patientId: '',
        medecinId: '',
        medicaments: [],
        instructionsSupplementaires: ''
      });
      setSelectedOrdonnance(null);
    }
    setShowForm(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedOrdonnance) {
        await putData(`/ordonnance/id?id=${selectedOrdonnance._id}`, formData);
        alert('Ordonnance mise à jour avec succès');
      } else {
        await postData('/new-ordonnance', formData);
        alert('Ordonnance créée avec succès');
      }
      setShowForm(false);
      loadData();
    } catch (err) {
      alert('Erreur lors de la sauvegarde');
    }
  };

  const addMedicament = () => {
    if (newMedicament.nomMedicament.trim()) {
      setFormData(prev => ({
        ...prev,
        medicaments: [...prev.medicaments, { ...newMedicament }]
      }));
      setNewMedicament({ nomMedicament: '', dosage: '', frequence: '', duree: '' });
    }
  };

  const removeMedicament = (index) => {
    setFormData(prev => ({
      ...prev,
      medicaments: prev.medicaments.filter((_, i) => i !== index)
    }));
  };

  const filteredOrdonnances = ordonnances.filter(ord =>
    ord.patientId?.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ord.medecinId?.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ord.medicaments?.some(m => m.nomMedicament?.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  if (loading) {
    return <div className="flex items-center justify-center h-64"><div>Chargement...</div></div>;
  }

  return (
    <div>
      <div className="mb-8 flex items-center justify-between">
        <div>
          <h2 className="text-3xl font-bold text-gray-900">Gestion des Ordonnances</h2>
          <p className="text-gray-600 mt-2">Gérez toutes les ordonnances médicales</p>
        </div>
        <button
          onClick={() => handleOpenForm()}
          className="bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600 transition-all font-medium flex items-center"
        >
          <FiPlus className="mr-2" /> Nouvelle ordonnance
        </button>
      </div>

      {/* Barre de recherche */}
      <div className="mb-6">
        <div className="relative">
          <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
          <input
            type="text"
            placeholder="Rechercher une ordonnance..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
        </div>
      </div>

      {/* Tableau */}
      <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Date</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Patient</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Médecin</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Médicaments</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {filteredOrdonnances.map((ordonnance) => (
              <tr key={ordonnance._id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {new Date(ordonnance.dateOrdonnance || ordonnance.createdAt).toLocaleDateString('fr-FR')}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {ordonnance.patientId?.fullName || ordonnance.patientId || 'N/A'}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {ordonnance.medecinId?.fullName || ordonnance.medecinId || 'N/A'}
                </td>
                <td className="px-6 py-4 text-sm text-gray-900">
                  {ordonnance.medicaments?.length || 0} médicament(s)
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                  <div className="flex gap-2">
                    <button onClick={() => handleView(ordonnance)} className="text-blue-600 hover:text-blue-900">
                      <FiEye size={18} />
                    </button>
                    <button onClick={() => handleOpenForm(ordonnance)} className="text-green-600 hover:text-green-900">
                      <FiEdit size={18} />
                    </button>
                    <button onClick={() => handleDelete(ordonnance._id)} className="text-red-600 hover:text-red-900">
                      <FiTrash2 size={18} />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal de détails */}
      {showModal && selectedOrdonnance && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl p-8 max-w-2xl w-full mx-4 max-h-[90vh] overflow-y-auto">
            <h3 className="text-2xl font-bold mb-4">Détails de l'Ordonnance</h3>
            <div className="space-y-4">
              <div><strong>Date:</strong> {new Date(selectedOrdonnance.dateOrdonnance || selectedOrdonnance.createdAt).toLocaleDateString('fr-FR')}</div>
              <div><strong>Patient:</strong> {selectedOrdonnance.patientId?.fullName || selectedOrdonnance.patientId}</div>
              <div><strong>Médecin:</strong> {selectedOrdonnance.medecinId?.fullName || selectedOrdonnance.medecinId}</div>
              {selectedOrdonnance.medicaments?.length > 0 && (
                <div>
                  <strong>Médicaments:</strong>
                  <ul className="ml-4 mt-2 space-y-2">
                    {selectedOrdonnance.medicaments.map((med, i) => (
                      <li key={i} className="border-l-4 border-blue-500 pl-4">
                        <strong>{med.nomMedicament}</strong>
                        {med.dosage && <div>Dosage: {med.dosage}</div>}
                        {med.frequence && <div>Fréquence: {med.frequence}</div>}
                        {med.duree && <div>Durée: {med.duree}</div>}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
              {selectedOrdonnance.instructionsSupplementaires && (
                <div><strong>Instructions:</strong> {selectedOrdonnance.instructionsSupplementaires}</div>
              )}
            </div>
            <button onClick={() => setShowModal(false)} className="mt-6 bg-gray-500 text-white px-6 py-2 rounded-lg">Fermer</button>
          </div>
        </div>
      )}

      {/* Formulaire */}
      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl p-8 max-w-3xl w-full mx-4 max-h-[90vh] overflow-y-auto">
            <h3 className="text-2xl font-bold mb-4">{selectedOrdonnance ? 'Modifier' : 'Nouvelle'} Ordonnance</h3>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">Patient *</label>
                  <select
                    value={formData.patientId}
                    onChange={(e) => setFormData({ ...formData, patientId: e.target.value })}
                    required
                    className="w-full px-4 py-2 border rounded-lg"
                  >
                    <option value="">Sélectionner</option>
                    {patients.map(p => <option key={p._id} value={p._id}>{p.fullName}</option>)}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Médecin *</label>
                  <select
                    value={formData.medecinId}
                    onChange={(e) => setFormData({ ...formData, medecinId: e.target.value })}
                    required
                    className="w-full px-4 py-2 border rounded-lg"
                  >
                    <option value="">Sélectionner</option>
                    {medecins.map(m => <option key={m._id} value={m._id}>Dr. {m.fullName}</option>)}
                  </select>
                </div>
              </div>
              
              <div className="border-t pt-4">
                <h4 className="font-semibold mb-4">Ajouter un médicament</h4>
                <div className="grid grid-cols-2 gap-4 mb-4">
                  <div>
                    <label className="block text-sm font-medium mb-2">Nom du médicament *</label>
                    <input
                      type="text"
                      value={newMedicament.nomMedicament}
                      onChange={(e) => setNewMedicament({ ...newMedicament, nomMedicament: e.target.value })}
                      className="w-full px-4 py-2 border rounded-lg"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-2">Dosage</label>
                    <input
                      type="text"
                      value={newMedicament.dosage}
                      onChange={(e) => setNewMedicament({ ...newMedicament, dosage: e.target.value })}
                      className="w-full px-4 py-2 border rounded-lg"
                      placeholder="ex: 500mg"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-2">Fréquence</label>
                    <input
                      type="text"
                      value={newMedicament.frequence}
                      onChange={(e) => setNewMedicament({ ...newMedicament, frequence: e.target.value })}
                      className="w-full px-4 py-2 border rounded-lg"
                      placeholder="ex: 2 fois par jour"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-2">Durée</label>
                    <input
                      type="text"
                      value={newMedicament.duree}
                      onChange={(e) => setNewMedicament({ ...newMedicament, duree: e.target.value })}
                      className="w-full px-4 py-2 border rounded-lg"
                      placeholder="ex: 7 jours"
                    />
                  </div>
                </div>
                <button type="button" onClick={addMedicament} className="bg-blue-500 text-white px-4 py-2 rounded-lg mb-4">
                  Ajouter le médicament
                </button>
                
                {formData.medicaments.length > 0 && (
                  <div className="space-y-2">
                    <h5 className="font-medium">Médicaments ajoutés:</h5>
                    {formData.medicaments.map((med, i) => (
                      <div key={i} className="bg-gray-50 p-3 rounded-lg flex items-center justify-between">
                        <div>
                          <strong>{med.nomMedicament}</strong>
                          {med.dosage && <span className="ml-2 text-sm text-gray-600">- {med.dosage}</span>}
                          {med.frequence && <span className="ml-2 text-sm text-gray-600">- {med.frequence}</span>}
                          {med.duree && <span className="ml-2 text-sm text-gray-600">- {med.duree}</span>}
                        </div>
                        <button type="button" onClick={() => removeMedicament(i)} className="text-red-600">×</button>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">Instructions supplémentaires</label>
                <textarea
                  value={formData.instructionsSupplementaires}
                  onChange={(e) => setFormData({ ...formData, instructionsSupplementaires: e.target.value })}
                  className="w-full px-4 py-2 border rounded-lg"
                  rows={3}
                />
              </div>

              <div className="flex gap-4">
                <button type="submit" className="flex-1 bg-blue-500 text-white py-3 rounded-lg hover:bg-blue-600">Enregistrer</button>
                <button type="button" onClick={() => setShowForm(false)} className="px-6 py-3 border rounded-lg">Annuler</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Ordonnances;
