import React, { useEffect, useState } from "react";
import * as api from "../api/endpoint";
import "./DoctorDashboard.css"; // Cr√©ez ce fichier CSS similaire √† Admin

const DoctorDashboard = () => {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Form state pour edit patient
  const [editingPatient, setEditingPatient] = useState(null);
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    dateNaissance: "",
    sexe: "",
    contact: "",
  });

  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    setLoading(true);
    try {
      const data = await api.getData("/patients"); // Route existante dans votre backend
      setPatients(data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError(err.message || "Erreur lors du chargement des patients");
      setLoading(false);
    }
  };

  const handleEditClick = (patient) => {
    setEditingPatient(patient._id);
    setFormData({
      fullName: patient.fullName || "",
      email: patient.email || "",
      dateNaissance: patient.date_of_birth || "",
      sexe: patient.sexe || "",
      contact: patient.patientProfile?.contact || "",
    });
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Voulez-vous vraiment supprimer ce patient ?")) return;
    try {
      await api.deleteData(`/user/${id}`);
      setPatients(patients.filter((p) => p._id !== id));
      alert("Patient supprim√© avec succ√®s !");
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la suppression : " + err.message);
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const updatedData = {
        fullName: formData.fullName,
        email: formData.email,
        date_of_birth: formData.dateNaissance,
        sexe: formData.sexe,
        patient: { patientProfile: { contact: formData.contact } }, // Correspond au sch√©ma
      };
      
      const updated = await api.putData(`/user/${editingPatient}`, updatedData);
      setPatients(patients.map((p) => (p._id === editingPatient ? updated.updatedUser : p)));
      setEditingPatient(null);
      alert("Patient mis √† jour !");
    } catch (err) {
      console.error(err);
      alert("Erreur update patient : " + err.message);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  if (loading) return <p>Chargement des patients...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div className="doctor-dashboard">
      <h2>Dashboard M√©decin - Liste des Patients</h2>
      <table>
        <thead>
          <tr>
            <th>Nom</th>
            <th>Email</th>
            <th>Date Naissance</th>
            <th>Sexe</th>
            <th>Contact</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {patients.map((patient) =>
            editingPatient === patient._id ? (
              <tr key={patient._id}>
                <td>
                  <input
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleChange}
                  />
                </td>
                <td>
                  <input
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                  />
                </td>
                <td>
                  <input
                    name="dateNaissance"
                    type="date"
                    value={formData.dateNaissance}
                    onChange={handleChange}
                  />
                </td>
                <td>
                  <select name="sexe" value={formData.sexe} onChange={handleChange}>
                    <option value="">S√©lectionner</option>
                    <option value="M">M</option>
                    <option value="F">F</option>
                  </select>
                </td>
                <td>
                  <input
                    name="contact"
                    value={formData.contact}
                    onChange={handleChange}
                  />
                </td>
                <td>
                  <button onClick={handleUpdate}>Enregistrer</button>
                  <button onClick={() => setEditingPatient(null)}>Annuler</button>
                </td>
              </tr>
            ) : (
              <tr key={patient._id}>
                <td>{patient.fullName}</td>
                <td>{patient.email}</td>
                <td>{patient.date_of_birth?.toString().split('T')[0]}</td>
                <td>{patient.sexe}</td>
                <td>{patient.patientProfile?.contact}</td>
                <td>
                  <button onClick={() => handleEditClick(patient)}>Modifier</button>
                  <button onClick={() => handleDelete(patient._id)}>Supprimer</button>
                </td>
              </tr>
            )
          )}
        </tbody>
      </table>
    </div>
  );
};

export default DoctorDashboard;
