import React, { useState, useEffect, useCallback } from 'react';
import { postData, getData } from '../api/endpoint';

const CreateDossierModal = ({ onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    patientId: '',
    patientFullName: '',
    patientEmail: '',
    sexe: 'M',
    groupeSanguin: '',
    resumeMedical: '',
    allergies: ''
  });
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingPatients, setLoadingPatients] = useState(true);
  const [showPatientsList, setShowPatientsList] = useState(false);

  // ‚úÖ CHARGEMENT PATIENTS R√âELS
  const loadPatients = useCallback(async () => {
    try {
      setLoadingPatients(true);
      const data = await getData('/patients');
      setPatients(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Patients:', err);
      setPatients([]);
    } finally {
      setLoadingPatients(false);
    }
  }, []);

  useEffect(() => {
    loadPatients();
  }, [loadPatients]);

  const selectPatient = (patient) => {
    setFormData({
      patientId: patient._id,
      patientFullName: patient.fullName || patient.nom || '',
      patientEmail: patient.email || '',
      sexe: patient.sexe || 'M',
      groupeSanguin: '',
      resumeMedical: '',
      allergies: ''
    });
    setShowPatientsList(false);
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ‚úÖ FORMAT EXACT pour votre backend
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.patientId) {
      alert('‚ùå S√©lectionnez un patient');
      return;
    }

    setLoading(true);
    try {
      // ‚úÖ Backend r√©cup√®re medecinId automatiquement de req.user
      const payload = {
        patientId: formData.patientId,
        resumeMedical: formData.resumeMedical || '',
        groupeSanguin: formData.groupeSanguin || '',
        allergies: formData.allergies ? [{ allergie: formData.allergies }] : [],
        antecedents: []
      };

      console.log('üì§ Envoi:', payload);
      
      await postData('/dossier', payload);
      alert('‚úÖ Dossier cr√©√© avec succ√®s !');
      onSuccess();
    } catch (err) {
      console.error('Erreur:', err);
      alert('‚ùå Erreur cr√©ation (sauvegarde locale activ√©e)');
      
      // Fallback localStorage
      const dossierLocal = {
        _id: Date.now().toString(),
        ...formData,
        createdAt: new Date().toISOString()
      };
      localStorage.setItem('dossiersMedecin', JSON.stringify([dossierLocal]));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      position: 'fixed', zIndex: 10000, inset: 0,
      background: 'rgba(0,0,0,0.6)', display: 'flex', alignItems: 'center', justifyContent: 'center'
    }}>
      <div style={{
        background: 'white', padding: '2.5rem', borderRadius: '16px', maxWidth: '700px',
        width: '90%', maxHeight: '90vh', overflowY: 'auto', boxShadow: '0 20px 60px rgba(0,0,0,0.3)'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '2rem' }}>
          <h2 style={{ margin: 0, color: '#1976d2', fontSize: '1.8rem' }}>‚ûï Nouveau Dossier</h2>
          <button onClick={onClose} style={{ background: 'none', border: 'none', fontSize: '2rem', cursor: 'pointer' }}>√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          {/* S√âLECTION PATIENT */}
          <div style={{ marginBottom: '1.5rem' }}>
            <button 
              type="button"
              onClick={() => setShowPatientsList(!showPatientsList)}
              disabled={loading}
              style={{
                width: '100%', padding: '1.2rem', background: formData.patientId ? '#4caf50' : '#2196f3',
                color: 'white', border: 'none', borderRadius: '12px', fontSize: '1.1rem', cursor: 'pointer'
              }}
            >
              {formData.patientId ? `‚úÖ ${formData.patientFullName}` : 'üë§ S√©lectionner patient'}
            </button>
          </div>

          {showPatientsList && (
            <div style={{ 
              maxHeight: '250px', overflowY: 'auto', border: '2px solid #e0e0e0', 
              borderRadius: '12px', marginBottom: '1.5rem', background: '#f8f9fa'
            }}>
              {loadingPatients ? (
                <div style={{ padding: '2rem', textAlign: 'center' }}>‚è≥ Chargement...</div>
              ) : patients.length === 0 ? (
                <div style={{ padding: '2rem', textAlign: 'center', color: '#666' }}>
                  Aucun patient trouv√©
                </div>
              ) : (
                patients.map(patient => (
                  <div 
                    key={patient._id}
                    onClick={() => selectPatient(patient)}
                    style={{
                      padding: '1.2rem', cursor: 'pointer', borderBottom: '1px solid #eee',
                      display: 'flex', alignItems: 'center', gap: '1rem',
                      background: formData.patientId === patient._id ? '#e3f2fd' : 'transparent'
                    }}
                  >
                    <strong>{patient.fullName || patient.nom}</strong>
                    <span style={{ color: '#666', flex: 1 }}>{patient.email}</span>
                  </div>
                ))
              )}
            </div>
          )}

          {formData.patientId && (
            <div style={{ padding: '1.2rem', background: '#e8f5e8', borderRadius: '12px', marginBottom: '1.5rem' }}>
              ‚úÖ <strong>{formData.patientFullName}</strong> ({formData.patientEmail})
            </div>
          )}

          {/* CHAMPS */}
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1.5rem', marginBottom: '1.5rem' }}>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600' }}>ü©∏ Groupe sanguin</label>
              <select name="groupeSanguin" value={formData.groupeSanguin} onChange={handleInputChange} style={inputStyle}>
                <option value="">S√©lectionner...</option>
                <option value="A+">A+</option><option value="A-">A-</option>
                <option value="B+">B+</option><option value="B-">B-</option>
                <option value="AB+">AB+</option><option value="AB-">AB-</option>
                <option value="O+">O+</option><option value="O-">O-</option>
              </select>
            </div>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600' }}>‚öïÔ∏è Sexe</label>
              <input value={formData.sexe === 'M' ? 'Masculin' : 'F√©minin'} readOnly style={readOnlyStyle} />
            </div>
          </div>

          <div style={{ marginBottom: '2rem' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600' }}>üìù R√©sum√© m√©dical</label>
            <textarea name="resumeMedical" value={formData.resumeMedical} onChange={handleInputChange} 
              rows={3} style={textAreaStyle} />
          </div>

          <div style={{ display: 'flex', gap: '1rem' }}>
            <button type="submit" disabled={!formData.patientId || loading} style={{
              flex: 1, padding: '1.2rem', background: formData.patientId ? '#4caf50' : '#ccc',
              color: 'white', border: 'none', borderRadius: '12px', fontWeight: 'bold'
            }}>
              {loading ? '‚è≥ Cr√©ation...' : 'üíæ Cr√©er Dossier'}
            </button>
            <button type="button" onClick={onClose} style={{
              flex: 1, padding: '1.2rem', background: '#f44336', color: 'white',
              border: 'none', borderRadius: '12px', fontWeight: 'bold'
            }}>
              ‚ùå Annuler
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const inputStyle = {
  padding: '1rem', border: '2px solid #e0e0e0', borderRadius: '8px', width: '100%', fontSize: '1rem'
};
const readOnlyStyle = { ...inputStyle, background: '#f5f5f5' };
const textAreaStyle = { ...inputStyle, resize: 'vertical', minHeight: '100px' };

export default CreateDossierModal;
