import React, { useState } from 'react';
import { postData } from '../api/endpoint';

const CreateDossier = () => {
  const [formData, setFormData] = useState({
    motif: '',
    description: ''
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await postData('/dossiers', formData);
      alert('‚úÖ Dossier cr√©√© avec succ√®s');
      setFormData({ motif: '', description: '' });
    } catch (error) {
      alert(error.message || 'Erreur lors de la cr√©ation');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>üìÅ Nouveau dossier m√©dical</h2>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="motif"
          placeholder="Motif de consultation"
          value={formData.motif}
          onChange={handleChange}
          required
          style={{ width: '100%', padding: '10px', marginBottom: '10px' }}
        />

        <textarea
          name="description"
          placeholder="Description des sympt√¥mes"
          value={formData.description}
          onChange={handleChange}
          required
          rows={4}
          style={{ width: '100%', padding: '10px' }}
        />

        <button disabled={loading}>
          {loading ? 'Cr√©ation...' : 'Cr√©er le dossier'}
        </button>
      </form>
    </div>
  );
};

export default CreateDossier;
