            <tr key={user._id}>
              <td>{user.fullName}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>{user.active ? 'âœ… Actif' : 'ðŸš« DÃ©sactivÃ©'}</td>
              <td>
                <button onClick={() => toggleStatus(user._id, user.active)}>
                  {user.active ? 'DÃ©sactiver' : 'Activer'}
