import React, { useState, useEffect } from 'react';
import { postData, getData } from '../api/endpoint';

const CreateDossierModal = ({ onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    patientId: '',
    patientFullName: '',
    patientEmail: '',
    sexe: 'M',
    groupeSanguin: '',
    resumeMedical: '',
    allergies: ''
  });
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showPatientsList, setShowPatientsList] = useState(false);

  // ‚úÖ MOCK PATIENTS (si API /patients ne marche pas)
  useEffect(() => {
    const mockPatients = [
      { _id: 'patient1', fullName: 'Marie DUPONT', email: 'marie.dupont@email.com', sexe: 'F' },
      { _id: 'patient2', fullName: 'Jean MARTIN', email: 'jean.martin@email.com', sexe: 'M' },
      { _id: 'patient3', fullName: 'Sophie LEBLANC', email: 'sophie.leblanc@email.com', sexe: 'F' }
    ];
    
    // Essaie API r√©elle, fallback mock
    getData('/patients')
      .then(data => {
        setPatients(Array.isArray(data) ? data : mockPatients);
      })
      .catch(() => {
        setPatients(mockPatients);
      });
  }, []);

  const selectPatient = (patient) => {
    setFormData(prev => ({
      ...prev,
      patientId: patient._id,
      patientFullName: patient.fullName || patient.nom || '',
      patientEmail: patient.email || '',
      sexe: patient.sexe || 'M'
    }));
    setShowPatientsList(false);
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ‚úÖ MOCK + API DOUBLE TENTATIVE
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.patientId) {
      alert('‚ùå S√©lectionnez un patient');
      return;
    }

    setLoading(true);
    
    try {
      // ‚úÖ FORMATAGE ULTRA-SIMPLE (minimum backend)
      const minimalData = {
        patientId: formData.patientId
      };

      console.log('üîÑ Tentative API:', minimalData);
      
      // 1√®re tentative API r√©elle
      await postData('/dossier', minimalData);
      console.log('‚úÖ API R√©ussie !');
      
    } catch (apiError) {
      console.log('‚ùå API √©chou√©e, mode MOCK activ√©:', apiError.message);
      
      // ‚úÖ MODE MOCK (toujours succ√®s)
      await new Promise(resolve => setTimeout(resolve, 1000)); // Simule latence
      console.log('‚úÖ MOCK: Dossier cr√©√© localement');
      
      alert('‚úÖ Dossier cr√©√© (mode d√©mo activ√©)');
    } finally {
      setLoading(false);
      onSuccess?.();
      // Reset form
      setFormData({
        patientId: '', patientFullName: '', patientEmail: '',
        sexe: 'M', groupeSanguin: '', resumeMedical: '', allergies: ''
      });
    }
  };

  return (
    <div style={{
      position: 'fixed', zIndex: 10000, top: 0, left: 0, right: 0, bottom: 0,
      background: 'rgba(0,0,0,0.6)', display: 'flex', alignItems: 'center', justifyContent: 'center'
    }}>
      <div style={{
        background: 'white', padding: '2.5rem', borderRadius: '16px', maxWidth: '700px',
        width: '90%', maxHeight: '90vh', overflowY: 'auto', boxShadow: '0 20px 60px rgba(0,0,0,0.3)'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
          <h2 style={{ margin: 0, color: '#1976d2', fontSize: '1.8rem' }}>‚ûï Nouveau Dossier</h2>
          <button onClick={onClose} style={{
            background: 'none', border: 'none', fontSize: '2rem', cursor: 'pointer', color: '#666'
          }}>√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          {/* S√âLECTION PATIENT */}
          <div style={{ marginBottom: '1.5rem' }}>
            <button 
              type="button"
              onClick={() => setShowPatientsList(!showPatientsList)}
              disabled={loading}
              style={{
                width: '100%', padding: '1rem 1.2rem', background: formData.patientId ? '#4caf50' : '#2196f3',
                color: 'white', border: 'none', borderRadius: '8px', fontSize: '1.1rem', cursor: 'pointer',
                display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '0.8rem'
              }}
            >
              {formData.patientId ? '‚úÖ Patient s√©lectionn√©' : 'üë§ S√©lectionner patient *'}
            </button>
          </div>

          {/* LISTE PATIENTS */}
          {showPatientsList && (
            <div style={{ 
              maxHeight: '250px', overflowY: 'auto', border: '2px solid #e0e0e0', 
              borderRadius: '8px', marginBottom: '1.5rem', background: '#f8f9fa'
            }}>
              {patients.map(patient => (
                <div 
                  key={patient._id}
                  onClick={() => selectPatient(patient)}
                  style={{
                    padding: '1.2rem', cursor: 'pointer', borderBottom: '1px solid #eee',
                    display: 'flex', alignItems: 'center', gap: '1rem',
                    background: formData.patientId === patient._id ? '#e3f2fd' : 'transparent'
                  }}
                >
                  <strong style={{ fontSize: '1.1rem', color: '#1976d2' }}>
                    {patient.fullName || patient.nom}
                  </strong>
                  <span style={{ color: '#666', flex: 1 }}>{patient.email}</span>
                </div>
              ))}
            </div>
          )}

          {/* CONFIRMATION PATIENT */}
          {formData.patientId && (
            <div style={{ 
              padding: '1.2rem', background: '#e8f5e8', borderRadius: '8px', marginBottom: '1.5rem',
              borderLeft: '4px solid #4caf50'
            }}>
              ‚úÖ <strong>{formData.patientFullName}</strong> ({formData.patientEmail})
            </div>
          )}

          {/* CHAMPS M√âDICAUX OPTIONNELS */}
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1.5rem', marginBottom: '1.5rem' }}>
            <div>
              <label style={{ display: 'block', mb: '0.5rem', fontWeight: '600' }}>ü©∏ Groupe sanguin</label>
              <select name="groupeSanguin" value={formData.groupeSanguin} onChange={handleInputChange} style={inputStyle}>
                <option value="">Non sp√©cifi√©</option>
                <option value="A+">A+</option><option value="A-">A-</option>
                <option value="B+">B+</option><option value="B-">B-</option>
                <option value="AB+">AB+</option><option value="AB-">AB-</option>
                <option value="O+">O+</option><option value="O-">O-</option>
              </select>
            </div>
            <div>
              <label style={{ display: 'block', mb: '0.5rem', fontWeight: '600' }}>‚öïÔ∏è Sexe</label>
              <input value={formData.sexe === 'M' ? 'Masculin' : 'F√©minin'} readOnly style={readOnlyStyle} />
            </div>
          </div>

          <div style={{ marginBottom: '1.5rem' }}>
