            <textarea name="allergies" value={formData.allergies} onChange={handleInputChange} 
              rows={2} placeholder="Aucun connu..." style={{ ...inputStyle, width: '100%' }} />
          </div>

          {/* BOUTONS */}
          <div style={{ display: 'flex', gap: '1rem' }}>
            <button type="submit" disabled={!formData.patientId || loading} style={{
