import React, { useEffect, useState } from "react";
import * as api from "../api/endpoint";
import "./DoctorDashboard.css";

const DoctorDashboard = () => {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const [selectedPatient, setSelectedPatient] = useState(null); // patient sélectionné pour voir le dossier
  const [dossier, setDossier] = useState(null); // dossier complet

  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    setLoading(true);
    try {
      const data = await api.getData("/patients"); // accessible aux médecins
      setPatients(data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError(err.message || "Erreur lors du chargement des patients");
      setLoading(false);
    }
  };

  const viewDossier = async (patient) => {
    try {
      const data = await api.getData(`/dossier/${patient._id}`); // récupère le dossier complet du patient
      setSelectedPatient(patient);
      setDossier(data);
    } catch (err) {
      console.error(err);
      alert("Erreur lors du chargement du dossier : " + err.message);
    }
  };

  const closeDossier = () => {
    setSelectedPatient(null);
    setDossier(null);
  };

  return (
    <div className="doctor-interface">
      <header className="doctor-header">
        <h1>Dashboard Médecin</h1>
      </header>

      {loading && <p>Chargement des patients...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {!loading && !error && (
        <div className="patients-list">
          {patients.length === 0 ? (
            <p>Aucun patient enregistré.</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Nom</th>
                  <th>Email</th>
                  <th>Date de naissance</th>
                  <th>Sexe</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {patients.map((patient) => (
                  <tr key={patient._id}>
                    <td>{patient.fullName}</td>
                    <td>{patient.email}</td>
                    <td>{patient.date_of_birth ? new Date(patient.date_of_birth).toLocaleDateString() : "Non renseignée"}</td>
                    <td>{patient.sexe || "Non renseigné"}</td>
                    <td>
                      <button onClick={() => viewDossier(patient)}>Voir Dossier</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            )}
        </div>
      )}

      {/* Modal Dossier complet */}
      {selectedPatient && dossier && (
        <div className="dossier-modal">
          <div className="dossier-content">
            <button className="close-btn" onClick={closeDossier}>X</button>
            <h2>Dossier Médical de {selectedPatient.fullName}</h2>
            <p><strong>Email:</strong> {selectedPatient.email}</p>
            <p><strong>Date de naissance:</strong> {selectedPatient.date_of_birth ? new Date(selectedPatient.date_of_birth).toLocaleDateString() : "Non renseignée"}</p>
            <p><strong>Sexe:</strong> {selectedPatient.sexe || "Non renseigné"}</p>
            <p><strong>Statut:</strong> {selectedPatient.statut || "Non renseigné"}</p>

            <h3>Consultations</h3>
            {dossier.consultations && dossier.consultations.length > 0 ? (
              <ul>
                {dossier.consultations.map((c) => (
                  <li key={c._id}>{c.date} - {c.motif}</li>
                ))}
              </ul>
            ) : <p>Aucune consultation enregistrée.</p>}

            <h3>Rendez-vous</h3>
            {dossier.rendezvous && dossier.rendezvous.length > 0 ? (
              <ul>
                {dossier.rendezvous.map((r) => (
                  <li key={r._id}>{r.date} - {r.statut}</li>
                ))}
              </ul>
            ) : <p>Aucun rendez-vous enregistré.</p>}

            <h3>Ordonnances</h3>
            {dossier.ordonnances && dossier.ordonnances.length > 0 ? (
              <ul>
                {dossier.ordonnances.map((o) => (
                  <li key={o._id}>{o.medicaments.join(", ")}</li>
                ))}
              </ul>
            ) : <p>Aucune ordonnance enregistrée.</p>}
          </div>
        </div>
      )}
    </div>
  );
};

export default DoctorDashboard;
