import React, { useEffect, useState } from "react";
import * as api from "../api/endpoint";
import "./DoctorDashboard.css";

const DoctorDashboard = () => {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    loadPatients();
  }, []);

  const loadPatients = async () => {
    setLoading(true);
    try {
      const data = await api.getData("/patients"); // endpoint pour récupérer les patients du médecin
      setPatients(data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError(err.message || "Erreur lors du chargement des patients");
      setLoading(false);
    }
  };

  const handleMarkCompleted = async (id) => {
    try {
      await api.putData(`/appointment/${id}/complete`, {}); // endpoint pour marquer RDV terminé
      alert("Rendez-vous marqué comme terminé !");
      loadPatients();
    } catch (err) {
      console.error(err);
      alert("Erreur : " + err.message);
    }
  };

  if (loading) return <p>Chargement des patients...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div className="doctor-dashboard">
      <h2>Dashboard Médecin</h2>
      <table>
        <thead>
          <tr>
            <th>Nom du Patient</th>
            <th>Email</th>
            <th>Date de Naissance</th>
            <th>Rendez-vous</th>
            <th>Ordonnance</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {patients.map((patient) => (
            <tr key={patient._id}>
              <td>{patient.fullName}</td>
              <td>{patient.email}</td>
              <td>{patient.dateOfBirth}</td>
              <td>{patient.appointment || "Aucun"}</td>
              <td>{patient.prescription || "Aucune"}</td>
              <td>
                <button onClick={() => handleMarkCompleted(patient._id)}>
                  Marquer terminé
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DoctorDashboard;
